    ;;;
     ; Parse transactions from the block.
     ;
     ; @param transactionsOffset Offset of the transactions within the block.
     ; Useful for non-Bitcoin chains where the block header may not be a fixed size.
     ;;
    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"void" parseTransactions [#_"int" __transactionsOffset]
        (§ ass (.. this cursor) __transactionsOffset)
        (§ ass (.. this optimalEncodingMessageSize) Block/HEADER_SIZE)
        (when (== (.. this payload (alength)) (.. this cursor))
            ;; This message is just a header, it has no transactions.
            (§ ass (.. this transactionBytesValid) false)
            (§ return nil)
        )

        (let [#_"int" __numTransactions (int (.. this (readVarInt)))]
            (§ ass (.. this optimalEncodingMessageSize) (+ (.. this optimalEncodingMessageSize) (VarInt/sizeOf __numTransactions)))
            (§ ass (.. this transactions) (ArrayList. #_"<>" __numTransactions))
            (loop-when-recur [#_"int" __i 0] (< __i __numTransactions) [(inc __i)]
                (let [#_"Transaction" __tx (Transaction. (.. this params), (.. this payload), (.. this cursor), this, (.. this serializer), Message/UNKNOWN_LENGTH)]
                    ;; Label the transaction as coming from the P2P network, so code that cares where we first saw it knows.
                    (.. __tx (getConfidence) (setSource TransactionConfidence.Source/NETWORK))
                    (.. this transactions (add __tx))
                    (§ ass (.. this cursor) (+ (.. this cursor) (.. __tx (getMessageSize))))
                    (§ ass (.. this optimalEncodingMessageSize) (+ (.. this optimalEncodingMessageSize) (.. __tx (getOptimalEncodingMessageSize))))
                )
            )
            (§ ass (.. this transactionBytesValid) (.. this serializer (isParseRetainMode)))
            nil
        )
    )

    #_override
    #_protected
    #_throws #_[ "ProtocolException" ]
