;;;
 ; <p>Abstract superclass of classes with list based payload, i.e. InventoryMessage and GetDataMessage.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
#_abstract
(§ class ListMessage (§ extends Message)
    #_public
    #_static
    (§ def #_"long" ListMessage/MAX_INVENTORY_ITEMS 50000)

    #_private
    (§ field- #_"long" arrayLen)
    ;; For some reason the compiler complains if this is inside InventoryItem.
    #_protected
    (§ field #_"List<InventoryItem>" items)

    #_public
    #_throws #_[ "ProtocolException" ]
    (§ constructor ListMessage [#_"NetworkParameters" __params, #_"byte[]" __bytes]
        (§ super __params, __bytes, 0)
        this
    )

    #_public
    #_throws #_[ "ProtocolException" ]
    (§ constructor ListMessage [#_"NetworkParameters" __params, #_"byte[]" __payload, #_"MessageSerializer" __serializer, #_"int" __length]
        (§ super __params, __payload, 0, __serializer, __length)
        this
    )

    #_public
    (§ constructor ListMessage [#_"NetworkParameters" __params]
        (§ super __params)

        (§ ass (.. this items) (ArrayList. #_"<>"))
        (§ ass (.. this length) 1) ;; length of 0 varint
        this
    )

    #_public
    (§ method #_"List<InventoryItem>" getItems []
        (Collections/unmodifiableList (.. this items))
    )

    #_public
    (§ method #_"void" addItem [#_"InventoryItem" __item]
        (.. this (unCache))
        (§ ass (.. this length) (- (.. this length) (VarInt/sizeOf (.. this items (size)))))
        (.. this items (add __item))
        (§ ass (.. this length) (+ (.. this length) (VarInt/sizeOf (.. this items (size))) InventoryItem/MESSAGE_LENGTH))
        nil
    )

    #_public
    (§ method #_"void" removeItem [#_"int" __index]
        (.. this (unCache))
        (§ ass (.. this length) (- (.. this length) (VarInt/sizeOf (.. this items (size)))))
        (.. this items (remove __index))
        (§ ass (.. this length) (+ (.. this length) (VarInt/sizeOf (.. this items (size))) (- InventoryItem/MESSAGE_LENGTH)))
        nil
    )

    #_override
    #_protected
    #_throws #_[ "ProtocolException" ]
    (§ method #_"void" parse []
        (§ ass (.. this arrayLen) (.. this (readVarInt)))
        (when (< ListMessage/MAX_INVENTORY_ITEMS (.. this arrayLen))
            (throw (ProtocolException. (str "Too many items in INV message: " (.. this arrayLen))))
        )
        (§ ass (.. this length) (int (+ (- (.. this cursor) (.. this offset)) (* (.. this arrayLen) InventoryItem/MESSAGE_LENGTH))))

        ;; An inv is vector<CInv> where CInv is int+hash.  The int is either 1 or 2 for tx or block.
        (§ ass (.. this items) (ArrayList. #_"<>" (int (.. this arrayLen))))
        (loop-when-recur [#_"int" __i 0] (< __i (.. this arrayLen)) [(inc __i)]
            (when (< (.. this payload (alength)) (+ (.. this cursor) InventoryItem/MESSAGE_LENGTH))
                (throw (ProtocolException. "Ran off the end of the INV"))
            )

            (let [#_"int" __typeCode (int (.. this (readUint32)))
                  ;; see ppszTypeName in net.h
                  #_"InventoryItem.Type" __type
                    (case __typeCode
                        0 InventoryItem.Type/Error
                        1 InventoryItem.Type/Transaction
                        2 InventoryItem.Type/Block
                        3 InventoryItem.Type/FilteredBlock
                        (throw (ProtocolException. (str "Unknown CInv type: " __typeCode)))
                    )]
                (.. this items (add (InventoryItem. __type, (.. this (readHash)))))
            )
        )
        (§ ass (.. this payload) nil)
        nil
    )

    #_override
    #_public
    #_throws #_[ "IOException" ]
    (§ method #_"void" bitcoinSerializeToStream [#_"OutputStream" __stream]
        (.. __stream (write (.. (VarInt. (.. this items (size))) (encode))))
        (doseq [#_"InventoryItem" __i (.. this items)]
            ;; Write out the type code.
            (Utils/uint32ToByteStreamLE (.. __i type (ordinal)), __stream)
            ;; And now the hash.
            (.. __stream (write (.. __i hash (getReversedBytes))))
        )
        nil
    )

    #_override
    #_public
    (§ method #_"boolean" equals [#_"Object" __o]
        (when (== this __o)
            (§ return true)
        )
        (when (or (nil? __o) (!= (getClass) (.. __o (getClass))))
            (§ return false)
        )
        (.. this items (equals (.. (cast ListMessage __o) items)))
    )

    #_override
    #_public
    (§ method #_"int" hashCode []
        (.. this items (hashCode))
    )
)

