    #_protected
    (§ field #_"int" offset)
    ;; The cursor keeps track of where we are in the byte array as we parse it.
    ;; Note that it's relative to the start of the array NOT the start of the message payload.
    #_protected
    (§ field #_"int" cursor)

    #_protected
    (§ field #_"int" length Message/UNKNOWN_LENGTH)

    ;; The raw message payload bytes themselves.
    #_protected
    (§ field #_"byte[]" payload)

    #_protected
    (§ field #_"boolean" recached false)
    #_protected
    (§ field #_"MessageSerializer" serializer)

    #_protected
    (§ field #_"int" protocolVersion)

    #_protected
    (§ field #_"NetworkParameters" params)

    #_protected
    (§ constructor Message []
        (§ ass (.. this serializer) DummySerializer/DEFAULT)
        this
    )

