        #_public
        (§ method #_"void" complete []
            (when (not (.. this future (isDone)))
                (let [#_"Long" __elapsed (- (Utils/currentTimeMillis) (.. this startTimeMsec))]
                    (.. (§ dhis Peer) (addPingTimeData __elapsed))
                    (.. Peer/log (debug "{}: ping time is {} msec", (.. (§ dhis Peer) (toString)), __elapsed))
                    (.. this future (set __elapsed))
