    #_private
    #_volatile
    (§ field- #_"int" vMaxPeersToDiscoverCount 100)
    #_private
    #_static
    (§ def- #_"long" PeerGroup/DEFAULT_PEER_DISCOVERY_TIMEOUT_MILLIS 5000)
    #_private
    #_volatile
    (§ field- #_"long" vPeerDiscoveryTimeoutMillis PeerGroup/DEFAULT_PEER_DISCOVERY_TIMEOUT_MILLIS)

