                (let [#_"InetSocketAddress[]" __addresses (.. __peerDiscovery (getPeers (.. this requiredServices), __peerDiscoveryTimeoutMillis, TimeUnit/MILLISECONDS))]
                    (doseq [#_"InetSocketAddress" __address __addresses]
