        #_private
        (§ field- #_"int" blocksInLastSecond)
        #_private
        (§ field- #_"int" txnsInLastSecond)
        #_private
        (§ field- #_"int" origTxnsInLastSecond)
        #_private
        (§ field- #_"long" bytesInLastSecond)

