        (§ field- #_"int" warmupSeconds -1)

        ;; Used to calculate a moving average.
        #_private
        (§ field- #_"long[]" samples)
        #_private
        (§ field- #_"int" cursor)

        #_private
        (§ field- #_"boolean" syncDone)

        #_override
        #_public
        #_synchronized
        (§ method #_"void" onBlocksDownloaded [#_"Peer" __peer, #_"Block" __block, #_nilable #_"FilteredBlock" __filteredBlock, #_"int" __blocksLeft]
            (§ ass (.. this blocksInLastSecond) (inc (.. this blocksInLastSecond)))
            (§ ass (.. this bytesInLastSecond) (+ (.. this bytesInLastSecond) Block/HEADER_SIZE))
            (let [#_"List<Transaction>" __blockTransactions (.. __block (getTransactions))]
                ;; This whole area of the type hierarchy is a mess.
                (let [#_"int" __txCount (+ (if (some? __blockTransactions) (.. this (countAndMeasureSize __blockTransactions)) 0) (if (some? __filteredBlock) (.. this (countAndMeasureSize (.. __filteredBlock (getAssociatedTransactions) (values)))) 0))]
                    (§ ass (.. this txnsInLastSecond) (+ (.. this txnsInLastSecond) __txCount))
                    (when (some? __filteredBlock)
                        (§ ass (.. this origTxnsInLastSecond) (+ (.. this origTxnsInLastSecond) (.. __filteredBlock (getTransactionCount))))
                    )
                    nil
