        (§ method- #_"int" countAndMeasureSize [#_"Collection<Transaction>" __transactions]
            (doseq [#_"Transaction" __transaction __transactions]
                (§ ass (.. this bytesInLastSecond) (+ (.. this bytesInLastSecond) (.. __transaction (getMessageSize))))
            )
            (.. __transactions (size))
