        #_private
        (§ method- #_"void" calculate []
            (let [#_"int" __minSpeedBytesPerSec
                  #_"int" __period]

                (.. this lock (lock))
                (try
                    (§ ass __minSpeedBytesPerSec (.. this stallMinSpeedBytesSec))
                    (§ ass __period (.. this stallPeriodSeconds))
                    (finally
                        (.. this lock (unlock))
                    )
                )

