                    (let [#_"boolean" __behindPeers (and (some? (.. this chain)) (< (.. this chain (getBestChainHeight)) (PeerGroup/getMostCommonChainHeight)))]
                        (when (not __behindPeers)
                            (§ ass (.. this syncDone) true)
