            (let [#_"long" __numOutputs (.. this (readVarInt))]
                (§ ass (.. this optimalEncodingMessageSize) (+ (.. this optimalEncodingMessageSize) (VarInt/sizeOf __numOutputs)))
                (§ ass (.. this outputs) (ArrayList. #_"<>" (int __numOutputs)))
                (loop-when-recur [#_"long" __i 0] (< __i __numOutputs) [(inc __i)]
                    (let [#_"TransactionOutput" __output (TransactionOutput. (.. this params), this, (.. this payload), (.. this cursor), (.. this serializer))]
                        (.. this outputs (add __output))
                        (let [#_"long" __scriptLen (.. this (readVarInt 8))]
                            (§ ass (.. this optimalEncodingMessageSize) (+ (.. this optimalEncodingMessageSize) 8 (VarInt/sizeOf __scriptLen) __scriptLen))
                            (§ ass (.. this cursor) (+ (.. this cursor) __scriptLen))
