        (when (not (.. this (isCoinBase)))
            (§ return true)
        )

        (when (!= (.. this (getConfidence) (getConfidenceType)) TransactionConfidence.ConfidenceType/BUILDING)
            (§ return false)
        )

        (<= (.. this params (getSpendableCoinbaseDepth)) (.. this (getConfidence) (getDepthInBlocks)))
