    #_public
    (§ method #_"Date" estimateLockTime [#_"AbstractBlockChain" __chain]
        (if (< (.. this lockTime) Transaction/LOCKTIME_THRESHOLD) (.. __chain (estimateBlockTime (int (.. this (getLockTime))))) (Date. (* (.. this (getLockTime)) 1000)))
    )

