    ;;;
     ; Formats a given date+time value to an ISO 8601 string.
     ; @param dateTime value to format, as a Date
     ;;
    #_public
    #_static
    (§ defn #_"String" Utils/dateTimeFormat [#_"Date" __dateTime]
