     ;
     ; <p>This call will reset the current progress towards the timeout.</p>
     ;;
    #_public
    #_synchronized
    (§ method #_"void" setSocketTimeout [#_"int" __timeoutMillis]
        (§ ass (.. this timeoutMillis) __timeoutMillis)
        (.. this (resetTimeout))
        nil
    )

    ;;;
     ; Resets the current progress towards timeout to 0.
     ;;
    #_protected
    #_synchronized
    (§ method #_"void" resetTimeout []
        (when (some? (.. this timeoutTask))
            (.. this timeoutTask (cancel))
        )
        (when (or (== (.. this timeoutMillis) 0) (not (.. this timeoutEnabled)))
            (§ return nil)
        )

        (§ ass (.. this timeoutTask) (TimerTask.
        (§ anon
            #_override
            #_public
            (§ method #_"void" run []
                (.. this (timeoutOccurred))
                nil
            )
        )))
