    #_private
    #_static
    (§ def- #_"int" BlockingClient/BUFFER_SIZE_UPPER_BOUND 65536)

    #_private
    (§ field- #_"Socket" socket)
    #_private
    #_volatile
    (§ field- #_"boolean" vCloseRequested false)
