;;;
 ; Parameters for Bitcoin-like networks.
 ;;
#_public
#_abstract
(§ class AbstractBitcoinNetParams (§ extends NetworkParameters)
    ;;;
     ; Scheme part for Bitcoin URIs.
     ;;
    #_public
    #_static
    (§ def #_"String" AbstractBitcoinNetParams/BITCOIN_SCHEME "bitcoin")
    #_public
    #_static
    (§ def #_"int" AbstractBitcoinNetParams/REWARD_HALVING_INTERVAL 210000)

    #_private
    #_static
    (§ def- #_"Logger" AbstractBitcoinNetParams/log (LoggerFactory/getLogger AbstractBitcoinNetParams))

    #_public
    (§ constructor AbstractBitcoinNetParams []
        (§ super )
        this
    )

    ;;;
     ; Checks if we are at a reward halving point.
     ; @param height The height of the previous stored block.
     ; @return if this is a reward halving point.
     ;;
    #_public
    (§ method #_"boolean" isRewardHalvingPoint [#_"int" __height]
        (== (% (inc __height) AbstractBitcoinNetParams/REWARD_HALVING_INTERVAL) 0)
    )

    ;;;
     ; Checks if we are at a difficulty transition point.
     ; @param height The height of the previous stored block.
     ; @return if this is a difficulty transition point.
     ;;
    #_public
    (§ method #_"boolean" isDifficultyTransitionPoint [#_"int" __height]
        (== (% (inc __height) (.. this (getInterval))) 0)
    )

    #_override
    #_public
    #_throws #_[ "VerificationException", "BlockStoreException" ]
    (§ method #_"void" checkDifficultyTransitions [#_"StoredBlock" __storedPrev, #_"Block" __nextBlock, #_"BlockStore" __blockStore]
        (let [#_"Block" __prev (.. __storedPrev (getHeader))]

            ;; Is this supposed to be a difficulty transition point?
            (when (not (.. this (isDifficultyTransitionPoint (.. __storedPrev (getHeight)))))
                ;; No ... so check the difficulty didn't actually change.
                (when (!= (.. __nextBlock (getDifficultyTarget)) (.. __prev (getDifficultyTarget)))
                    (throw (VerificationException. (str "Unexpected change in difficulty at height " (.. __storedPrev (getHeight)) ": " (Long/toHexString (.. __nextBlock (getDifficultyTarget))) " vs " (Long/toHexString (.. __prev (getDifficultyTarget))))))
                )
                (§ return nil)
            )

            ;; We need to find a block far back in the chain.  It's OK that this is expensive because it only occurs every
            ;; two weeks after the initial block chain download.
            (let [#_"Stopwatch" __watch (Stopwatch/createStarted)]

                (let [#_"Sha256Hash" __hash (.. __prev (getHash))
                      #_"StoredBlock" __cursor nil
                      #_"int" __interval (.. this (getInterval))]
                    (loop-when-recur [#_"int" __i 0] (< __i __interval) [(inc __i)]
                        (§ ass __cursor (.. __blockStore (get __hash)))
                        (when (nil? __cursor)
                            ;; This should never happen.  If it does, it means we are following an incorrect or busted chain.
                            (throw (VerificationException. (str "Difficulty transition point but we did not find a way back to the last transition point. Not found: " __hash)))
                        )
                        (§ ass __hash (.. __cursor (getHeader) (getPrevBlockHash)))
                    )
                    (Preconditions/checkState (and (some? __cursor) (.. this (isDifficultyTransitionPoint (dec (.. __cursor (getHeight)))))), "Didn't arrive at a transition point.")

                    (.. __watch (stop))
                    (when (< 50 (.. __watch (elapsed TimeUnit/MILLISECONDS)))
                        (.. AbstractBitcoinNetParams/log (info "Difficulty transition traversal took {}", __watch))
                    )

                    (let [#_"Block" __blockIntervalAgo (.. __cursor (getHeader))
                          #_"int" __timespan (int (- (.. __prev (getTimeSeconds)) (.. __blockIntervalAgo (getTimeSeconds))))]
                        ;; Limit the adjustment step.
                        (let [#_"int" __targetTimespan (.. this (getTargetTimespan))]
                            (when (< __timespan (/ __targetTimespan 4))
                                (§ ass __timespan (/ __targetTimespan 4))
                            )
                            (when (> __timespan (* __targetTimespan 4))
                                (§ ass __timespan (* __targetTimespan 4))
                            )

                            (let [#_"BigInteger" __newTarget (Utils/decodeCompactBits (.. __prev (getDifficultyTarget)))]
                                (§ ass __newTarget (.. __newTarget (multiply (BigInteger/valueOf __timespan))))
                                (§ ass __newTarget (.. __newTarget (divide (BigInteger/valueOf __targetTimespan))))

                                (when (< 0 (.. __newTarget (compareTo (.. this (getMaxTarget)))))
                                    (.. AbstractBitcoinNetParams/log (info "Difficulty hit proof of work limit: {}", (.. __newTarget (toString 16))))
                                    (§ ass __newTarget (.. this (getMaxTarget)))
                                )

                                (let [#_"int" __accuracyBytes (- (int (>>> (.. __nextBlock (getDifficultyTarget)) 24)) 3)
                                      #_"long" __receivedTargetCompact (.. __nextBlock (getDifficultyTarget))]

                                    ;; The calculated difficulty is to a higher precision than received, so reduce here.
                                    (let [#_"BigInteger" __mask (.. (BigInteger/valueOf 0xffffff) (shiftLeft (* __accuracyBytes 8)))]
                                        (§ ass __newTarget (.. __newTarget (and __mask)))
                                        (let [#_"long" __newTargetCompact (Utils/encodeCompactBits __newTarget)]

                                            (when (!= __newTargetCompact __receivedTargetCompact)
                                                (throw (VerificationException. (str "Network provided difficulty bits do not match what was calculated: " (Long/toHexString __newTargetCompact) " vs " (Long/toHexString __receivedTargetCompact))))
                                            )
                                            nil
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    #_override
    #_public
    (§ method #_"Coin" getMaxMoney []
        NetworkParameters/MAX_MONEY
    )

    #_override
    #_public
    (§ method #_"Coin" getMinNonDustOutput []
        Transaction/MIN_NONDUST_OUTPUT
    )

    #_override
    #_public
    (§ method #_"MonetaryFormat" getMonetaryFormat []
        (MonetaryFormat.)
    )

    #_override
    #_public
    (§ method #_"int" getProtocolVersionNum [#_"NetworkParameters.ProtocolVersion" __version]
        (.. __version (getBitcoinProtocolVersion))
    )

    #_override
    #_public
    (§ method #_"BitcoinSerializer" getSerializer [#_"boolean" __parseRetain]
        (BitcoinSerializer. this, __parseRetain)
    )

    #_override
    #_public
    (§ method #_"String" getUriScheme []
        AbstractBitcoinNetParams/BITCOIN_SCHEME
    )

    #_override
    #_public
    (§ method #_"boolean" hasMaxMoney []
        true
    )
)

;;;
 ; Parameters for the main production network on which people trade goods and services.
 ;;
#_public
(§ class MainNetParams (§ extends AbstractBitcoinNetParams)
    #_public
    #_static
    (§ def #_"int" MainNetParams/MAINNET_MAJORITY_WINDOW 1000)
    #_public
    #_static
    (§ def #_"int" MainNetParams/MAINNET_MAJORITY_REJECT_BLOCK_OUTDATED 950)
    #_public
    #_static
    (§ def #_"int" MainNetParams/MAINNET_MAJORITY_ENFORCE_BLOCK_UPGRADE 750)

    #_public
    (§ constructor MainNetParams []
        (§ super )

        (§ ass (.. this interval) NetworkParameters/INTERVAL)
        (§ ass (.. this targetTimespan) NetworkParameters/TARGET_TIMESPAN)
        (§ ass (.. this maxTarget) (Utils/decodeCompactBits 0x1d00ffff))
        (§ ass (.. this addressHeader) 0)
        (§ ass (.. this p2shHeader) 5)
        (§ ass (.. this acceptableAddressCodes) (int-array [ (.. this addressHeader), (.. this p2shHeader) ]))
        (§ ass (.. this port) 8333)
        (§ ass (.. this packetMagic) 0xf9beb4d9)
        (§ ass (.. this bip32HeaderPub) 0x0488b21e) ;; 4 byte header that serializes in base58 to "xpub"
        (§ ass (.. this bip32HeaderPriv) 0x0488ade4) ;; 4 byte header that serializes in base58 to "xprv"

        (§ ass (.. this majorityEnforceBlockUpgrade) MainNetParams/MAINNET_MAJORITY_ENFORCE_BLOCK_UPGRADE)
        (§ ass (.. this majorityRejectBlockOutdated) MainNetParams/MAINNET_MAJORITY_REJECT_BLOCK_OUTDATED)
        (§ ass (.. this majorityWindow) MainNetParams/MAINNET_MAJORITY_WINDOW)

        (.. this genesisBlock (setDifficultyTarget 0x1d00ffff))
        (.. this genesisBlock (setTime 1231006505))
        (.. this genesisBlock (setNonce 2083236893))
        (§ ass (.. this id) NetworkParameters/ID_MAINNET)
        (§ ass (.. this subsidyDecreaseBlockCount) 210000)
        (§ ass (.. this spendableCoinbaseDepth) 100)
        (let [#_"String" __genesisHash (.. this genesisBlock (getHashAsString))]
            (Preconditions/checkState (.. __genesisHash (equals "000000000019d6689c085ae165831e934ff763ae46a2a6c172b3f1b60a8ce26f")), __genesisHash)

            ;; This contains (at a minimum) the blocks which are not BIP30 compliant.  BIP30 changed how duplicate
            ;; transactions are handled.  Duplicated transactions could occur in the case where a coinbase had the same
