;;;
 ; Utility class that holds all the registered NetworkParameters types used for Address auto discovery.
 ; By default only MainNetParams and TestNet3Params are used.  If you want to use UnitTestParams, use
 ; the register and unregister the TestNet3Params as they don't have their own address version/type code.
 ;;
#_public
(§ class Networks
    ;;; Registered networks. ;;
    #_private
    #_static
    (§ def- #_"Set<? extends NetworkParameters>" Networks/NETWORKS (ImmutableSet/of (TestNet3Params/get), (MainNetParams/get)))

    #_public
    #_static
    (§ defn #_"Set<? extends NetworkParameters>" Networks/get []
        Networks/NETWORKS
    )

    #_public
    #_static
    (§ defn #_"void" Networks/register [#_"NetworkParameters" __network]
        (Networks/register (Lists/newArrayList __network))
        nil
    )

    #_public
    #_static
    (§ defn #_"void" Networks/register [#_"Collection<? extends NetworkParameters>" __networks]
        (let [#_"ImmutableSet.Builder<NetworkParameters>" __builder (ImmutableSet/builder)]
            (.. __builder (addAll Networks/NETWORKS))
            (.. __builder (addAll __networks))
            (§ ass Networks/NETWORKS (.. __builder (build)))
            nil
        )
    )

    #_public
    #_static
    (§ defn #_"void" Networks/unregister [#_"NetworkParameters" __network]
        (when (.. Networks/NETWORKS (contains __network))
            (let [#_"ImmutableSet.Builder<NetworkParameters>" __builder (ImmutableSet/builder)]

                (doseq [#_"NetworkParameters" __parameters Networks/NETWORKS]
                    (when (not (.. __parameters (equals __network)))
                        (.. __builder (add __parameters))
                    )
                )

                (§ ass Networks/NETWORKS (.. __builder (build)))
            )
        )
        nil
    )
)

;;;
 ; Parameters for the testnet, a separate public instance of Bitcoin that has relaxed rules suitable for development
 ; and testing of applications and new Bitcoin versions.
 ;;
#_public
(§ class TestNet3Params (§ extends AbstractBitcoinNetParams)
    #_public
    #_static
    (§ def #_"int" TestNet3Params/TESTNET_MAJORITY_WINDOW 100)
    #_public
    #_static
    (§ def #_"int" TestNet3Params/TESTNET_MAJORITY_REJECT_BLOCK_OUTDATED 75)
    #_public
    #_static
    (§ def #_"int" TestNet3Params/TESTNET_MAJORITY_ENFORCE_BLOCK_UPGRADE 51)

    #_public
    (§ constructor TestNet3Params []
        (§ super )

        (§ ass (.. this id) NetworkParameters/ID_TESTNET)
        (§ ass (.. this packetMagic) 0x0b110907)
        (§ ass (.. this interval) NetworkParameters/INTERVAL)
        (§ ass (.. this targetTimespan) NetworkParameters/TARGET_TIMESPAN)
        (§ ass (.. this maxTarget) (Utils/decodeCompactBits 0x1d00ffff))
        (§ ass (.. this port) 18333)
        (§ ass (.. this addressHeader) 111)
        (§ ass (.. this p2shHeader) 196)
        (§ ass (.. this acceptableAddressCodes) (int-array [ (.. this addressHeader), (.. this p2shHeader) ]))
        (.. this genesisBlock (setTime 1296688602))
        (.. this genesisBlock (setDifficultyTarget 0x1d00ffff))
        (.. this genesisBlock (setNonce 414098458))
        (§ ass (.. this spendableCoinbaseDepth) 100)
        (§ ass (.. this subsidyDecreaseBlockCount) 210000)
        (let [#_"String" __genesisHash (.. this genesisBlock (getHashAsString))]
            (Preconditions/checkState (.. __genesisHash (equals "000000000933ea01ad0ee984209779baaec3ced90fa3f408719526f8d77f4943")))
            (§ ass (.. this alertSigningKey) (.. Utils/HEX (decode "04302390343f91cc401d56d68b123028bf52e5fca1939df127f63c6467cdf9c8e2c14b61104cf817d0b780da337893ecc4aaff1309e536162dabbdb45200ca2b0a")))

            (§ ass (.. this dnsSeeds) (into-array String
            [
                "testnet-seed.bitcoin.jonasschnelli.ch", ;; Jonas Schnelli
                "testnet-seed.bluematt.me",              ;; Matt Corallo
                "testnet-seed.bitcoin.petertodd.org",    ;; Peter Todd
                "testnet-seed.bitcoin.schildbach.de",    ;; Andreas Schildbach
            ]))
            (§ ass (.. this addrSeeds) nil)
            (§ ass (.. this bip32HeaderPub) 0x043587cf)
            (§ ass (.. this bip32HeaderPriv) 0x04358394)

            (§ ass (.. this majorityEnforceBlockUpgrade) TestNet3Params/TESTNET_MAJORITY_ENFORCE_BLOCK_UPGRADE)
            (§ ass (.. this majorityRejectBlockOutdated) TestNet3Params/TESTNET_MAJORITY_REJECT_BLOCK_OUTDATED)
            (§ ass (.. this majorityWindow) TestNet3Params/TESTNET_MAJORITY_WINDOW)
            this
        )
    )

    #_private
    #_static
    (§ def- #_"TestNet3Params" TestNet3Params/INSTANCE)

    #_public
    #_static
    #_synchronized
    (§ defn #_"TestNet3Params" TestNet3Params/get []
        (when (nil? TestNet3Params/INSTANCE)
            (§ ass TestNet3Params/INSTANCE (TestNet3Params.))
        )
        TestNet3Params/INSTANCE
    )

    #_override
    #_public
    (§ method #_"String" getPaymentProtocolId []
        NetworkParameters/PAYMENT_PROTOCOL_ID_TESTNET
    )

    ;; February 16th 2012
    #_private
    #_static
    (§ def- #_"Date" TestNet3Params/TESTNET_DIFF_DATE (Date. 1329264000000))

    #_override
    #_public
    #_throws #_[ "VerificationException", "BlockStoreException" ]
    (§ method #_"void" checkDifficultyTransitions [#_"StoredBlock" __storedPrev, #_"Block" __nextBlock, #_"BlockStore" __blockStore]
        (cond (and (not (.. this (isDifficultyTransitionPoint (.. __storedPrev (getHeight))))) (.. __nextBlock (getTime) (after TestNet3Params/TESTNET_DIFF_DATE)))
            (do
                (let [#_"Block" __prev (.. __storedPrev (getHeader))]

                    ;; After 15th February 2012 the rules on the testnet change to avoid people running up the difficulty
                    ;; and then leaving, making it too hard to mine a block.  On non-difficulty transition points, easy
                    ;; blocks are allowed if there has been a span of 20 minutes without one.
                    (let [#_"long" __timeDelta (- (.. __nextBlock (getTimeSeconds)) (.. __prev (getTimeSeconds)))]
                        ;; There is an integer underflow bug in bitcoin-qt that means mindiff blocks are accepted when time
                        ;; goes backwards.
                        (when (and (<= 0 __timeDelta) (<= __timeDelta (* 2 NetworkParameters/TARGET_SPACING)))
                            ;; Walk backwards until we find a block that doesn't have the easiest proof of work, then check
                            ;; that difficulty is equal to that one.
                            (let [#_"StoredBlock" __cursor __storedPrev]
                                (while (and (not (.. __cursor (getHeader) (equals (.. this (getGenesisBlock))))) (!= (% (.. __cursor (getHeight)) (.. this (getInterval))) 0) (.. __cursor (getHeader) (getDifficultyTargetAsInteger) (equals (.. this (getMaxTarget)))))
                                    (§ ass __cursor (.. __cursor (getPrev __blockStore)))
                                )
                                (let [#_"BigInteger" __cursorTarget (.. __cursor (getHeader) (getDifficultyTargetAsInteger))
                                      #_"BigInteger" __newTarget (.. __nextBlock (getDifficultyTargetAsInteger))]
                                    (when (not (.. __cursorTarget (equals __newTarget)))
                                        (throw (VerificationException. (str "Testnet block transition that is not allowed: " (Long/toHexString (.. __cursor (getHeader) (getDifficultyTarget))) " vs " (Long/toHexString (.. __nextBlock (getDifficultyTarget))))))
                                    )
                                )
                            )
                        )
                    )
                )
            )
            :else
            (do
                (.. super (checkDifficultyTransitions __storedPrev, __nextBlock, __blockStore))
            )
        )
        nil
    )
)

