        (when (.. this (isSentToAddress))
            (§ return Script.ScriptType/P2PKH)
        )
        (when (.. this (isSentToRawPubKey))
            (§ return Script.ScriptType/PUB_KEY)
        )
        (when (.. this (isPayToScriptHash))
