        (.. this (maybeConnect))

        (try
            ;; Create tables if needed.
            (if (not (.. this (tablesExist)))
                (.. this (createTables))
                (.. this (checkCompatibility))
            )
            (.. this (initFromDatabase))
            (catch SQLException __e
                (throw (BlockStoreException. __e))
            )
        )
