            (try
                (when (not (.. __conn (getAutoCommit)))
                    (.. __conn (rollback))
                )
                (.. __conn (close))
                (when (== __conn (.. this conn (get)))
                    (.. this conn (set nil))
                )
                (catch SQLException __e
                    (throw (RuntimeException. __e))
                )
            )
        )
        (.. this allConnections (clear))
        nil
    )

    ;;;
