    ;; create table SQL
    #_private
    #_static
    (§ def- #_"String" H2FullPrunedBlockStore/CREATE_SETTINGS_TABLE (str "CREATE TABLE settings ("
              "name VARCHAR(32) NOT NULL CONSTRAINT settings_pk PRIMARY KEY, "
              "value BLOB"
              ")"))

    #_private
