;;;
 ; Keeps {@link org.bitcoinj.core.StoredBlock}s in memory.  Used primarily for unit testing.
 ;;
#_public
(§ class MemoryBlockStore (§ implements BlockStore)
    #_private
    (§ field- #_"LinkedHashMap<Sha256Hash, StoredBlock>" blockMap (LinkedHashMap. #_"<Sha256Hash, StoredBlock>"
        (§ anon
            #_override
            #_protected
            (§ method #_"boolean" removeEldestEntry [#_"Map.Entry<Sha256Hash, StoredBlock>" __eldest]
                (< 5000 (.. this blockMap (size)))
            )
        )))
    #_private
    (§ field- #_"StoredBlock" chainHead)
    #_private
    (§ field- #_"NetworkParameters" params)

    #_public
    (§ constructor MemoryBlockStore [#_"NetworkParameters" __params]
        ;; Insert the genesis block.
        (try
            (let [#_"Block" __genesisHeader (.. __params (getGenesisBlock) (cloneAsHeader))
                  #_"StoredBlock" __storedGenesis (StoredBlock. __genesisHeader, (.. __genesisHeader (getWork)), 0)]
                (.. this (put __storedGenesis))
                (.. this (setChainHead __storedGenesis))
                (§ ass (.. this params) __params)
            )
            (catch BlockStoreException __e
                (throw (RuntimeException. __e)) ;; Cannot happen.
            )
            (catch VerificationException __e
                (throw (RuntimeException. __e)) ;; Cannot happen.
            )
        )
        this
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" put [#_"StoredBlock" __block]
        (when (nil? (.. this blockMap))
            (throw (BlockStoreException. "MemoryBlockStore is closed"))
        )

        (let [#_"Sha256Hash" __hash (.. __block (getHeader) (getHash))]
            (.. this blockMap (put __hash, __block))
            nil
        )
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredBlock" get [#_"Sha256Hash" __hash]
        (when (nil? (.. this blockMap))
            (throw (BlockStoreException. "MemoryBlockStore is closed"))
        )

        (.. this blockMap (get __hash))
    )

    #_override
    #_public
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredBlock" getChainHead []
        (when (nil? (.. this blockMap))
            (throw (BlockStoreException. "MemoryBlockStore is closed"))
        )

        (.. this chainHead)
    )

    #_override
    #_public
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" setChainHead [#_"StoredBlock" __chainHead]
        (when (nil? (.. this blockMap))
            (throw (BlockStoreException. "MemoryBlockStore is closed"))
        )

        (§ ass (.. this chainHead) __chainHead)
        nil
    )

    #_override
    #_public
    (§ method #_"void" close []
        (§ ass (.. this blockMap) nil)
        nil
    )

    #_override
    #_public
    (§ method #_"NetworkParameters" getParams []
        (.. this params)
    )
)

;;;
 ; Used as a key for memory map (to avoid having to think about NetworkParameters,
 ; which is required for {@link TransactionOutPoint}.
 ;;
(§ class StoredTransactionOutPoint
    ;;; Hash of the transaction to which we refer. ;;
    (§ field #_"Sha256Hash" hash)
    ;;; Which output of that transaction we are talking about. ;;
    (§ field #_"long" index)

    (§ constructor StoredTransactionOutPoint [#_"Sha256Hash" __hash, #_"long" __index]
        (§ ass (.. this hash) __hash)
        (§ ass (.. this index) __index)
        this
    )

    (§ constructor StoredTransactionOutPoint [#_"UTXO" __out]
        (§ ass (.. this hash) (.. __out (getHash)))
        (§ ass (.. this index) (.. __out (getIndex)))
        this
    )

    ;;;
     ; The hash of the transaction to which we refer.
     ;;
    (§ method #_"Sha256Hash" getHash []
        (.. this hash)
    )

    ;;;
     ; The index of the output in transaction to which we refer.
     ;;
    (§ method #_"long" getIndex []
        (.. this index)
    )

    #_override
    #_public
    (§ method #_"int" hashCode []
        (Objects/hashCode (.. this (getIndex)), (.. this (getHash)))
    )

    #_override
    #_public
    (§ method #_"String" toString []
        (str "Stored transaction out point: " (.. this hash) ":" (.. this index))
    )

    #_override
    #_public
    (§ method #_"boolean" equals [#_"Object" __o]
        (when (== this __o)
            (§ return true)
        )
        (when (or (nil? __o) (!= (getClass) (.. __o (getClass))))
            (§ return false)
        )
        (let [#_"StoredTransactionOutPoint" __other (cast StoredTransactionOutPoint __o)]
            (and (== (.. this (getIndex)) (.. __other (getIndex))) (Objects/equal (.. this (getHash)), (.. __other (getHash))))
        )
    )
)

;;;
 ; A HashMap<KeyType, ValueType> that is DB transaction-aware.
 ; This class is not thread-safe.
 ;;
(§ class TransactionalHashMap #_"<KeyType, ValueType>"
    (§ field #_"ThreadLocal<HashMap<KeyType, ValueType>>" tempMap)
    (§ field #_"ThreadLocal<HashSet<KeyType>>" tempSetRemoved)
    #_private
    (§ field- #_"ThreadLocal<Boolean>" inTransaction)

    (§ field #_"HashMap<KeyType, ValueType>" map)

    #_public
    (§ constructor TransactionalHashMap []
        (§ ass (.. this tempMap) (ThreadLocal. #_"<>"))
        (§ ass (.. this tempSetRemoved) (ThreadLocal. #_"<>"))
        (§ ass (.. this inTransaction) (ThreadLocal. #_"<>"))
        (§ ass (.. this map) (HashMap. #_"<>"))
        this
    )

    #_public
    (§ method #_"void" beginDatabaseBatchWrite []
        (.. this inTransaction (set true))
        nil
    )

    #_public
    (§ method #_"void" commitDatabaseBatchWrite []
        (when (some? (.. this tempSetRemoved (get)))
            (doseq [#_"KeyType" __key (.. this tempSetRemoved (get))]
                (.. this map (remove __key))
            )
        )
        (when (some? (.. this tempMap (get)))
            (doseq [#_"Map.Entry<KeyType, ValueType>" __entry (.. this tempMap (get) (entrySet))]
                (.. this map (put (.. __entry (getKey)), (.. __entry (getValue))))
            )
        )
        (.. this (abortDatabaseBatchWrite))
        nil
    )

    #_public
    (§ method #_"void" abortDatabaseBatchWrite []
        (.. this inTransaction (set false))
        (.. this tempSetRemoved (remove))
        (.. this tempMap (remove))
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" get [#_"KeyType" __key]
        (when (.. Boolean/TRUE (equals (.. this inTransaction (get))))
            (when (some? (.. this tempMap (get)))
                (let [#_"ValueType" __value (.. this tempMap (get) (get __key))]
                    (when (some? __value)
                        (§ return __value)
                    )
                )
            )
            (when (and (some? (.. this tempSetRemoved (get))) (.. this tempSetRemoved (get) (contains __key)))
                (§ return nil)
            )
        )
        (.. this map (get __key))
    )

    #_public
    (§ method #_"List<ValueType>" values []
        (let [#_"List<ValueType>" __valueTypes (ArrayList. #_"<>")]
            (doseq [#_"KeyType" __keyType (.. this map (keySet))]
                (.. __valueTypes (add (.. this (get __keyType))))
            )
            __valueTypes
        )
    )

    #_public
    (§ method #_"void" put [#_"KeyType" __key, #_"ValueType" __value]
        (cond (.. Boolean/TRUE (equals (.. this inTransaction (get))))
            (do
                (when (some? (.. this tempSetRemoved (get)))
                    (.. this tempSetRemoved (get) (remove __key))
                )
                (when (nil? (.. this tempMap (get)))
                    (.. this tempMap (set (HashMap. #_"<KeyType, ValueType>")))
                )
                (.. this tempMap (get) (put __key, __value))
            )
            :else
            (do
                (.. this map (put __key, __value))
            )
        )
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" remove [#_"KeyType" __key]
        (cond (.. Boolean/TRUE (equals (.. this inTransaction (get))))
            (do
                (let [#_"ValueType" __retVal (.. this map (get __key))]
                    (when (some? __retVal)
                        (when (nil? (.. this tempSetRemoved (get)))
                            (.. this tempSetRemoved (set (HashSet. #_"<KeyType>")))
                        )
                        (.. this tempSetRemoved (get) (add __key))
                    )
                    (when (some? (.. this tempMap (get)))
                        (let [#_"ValueType" __tempVal (.. this tempMap (get) (remove __key))]
                            (when (some? __tempVal)
                                (§ return __tempVal)
                            )
                        )
                    )
                    (§ return __retVal)
                )
            )
            :else
            (do
                (§ return (.. this map (remove __key)))
            )
        )
    )
)

;;;
 ; A Map with multiple key types that is DB per-thread-transaction-aware.
 ; However, this class is not thread-safe.
 ; @param <UniqueKeyType> Is a key that must be unique per object.
 ; @param <MultiKeyType> Is a key that can have multiple values.
 ;;
(§ class TransactionalMultiKeyHashMap #_"<UniqueKeyType, MultiKeyType, ValueType>"
    (§ field #_"TransactionalHashMap<UniqueKeyType, ValueType>" mapValues)
    (§ field #_"HashMap<MultiKeyType, Set<UniqueKeyType>>" mapKeys)

    #_public
    (§ constructor TransactionalMultiKeyHashMap []
        (§ ass (.. this mapValues) (TransactionalHashMap. #_"<>"))
        (§ ass (.. this mapKeys) (HashMap. #_"<>"))
        this
    )

    #_public
    (§ method #_"void" beginTransaction []
        (.. this mapValues (beginDatabaseBatchWrite))
        nil
    )

    #_public
    (§ method #_"void" commitTransaction []
        (.. this mapValues (commitDatabaseBatchWrite))
        nil
    )

    #_public
    (§ method #_"void" abortTransaction []
        (.. this mapValues (abortDatabaseBatchWrite))
        nil
    )

    #_nilable
    #_public
    (§ method #_"ValueType" get [#_"UniqueKeyType" __key]
        (.. this mapValues (get __key))
    )

    #_public
    (§ method #_"void" put [#_"UniqueKeyType" __uniqueKey, #_"MultiKeyType" __multiKey, #_"ValueType" __value]
        (.. this mapValues (put __uniqueKey, __value))
        (let [#_"Set<UniqueKeyType>" __set (.. this mapKeys (get __multiKey))]
            (cond (nil? __set)
                (do
                    (§ ass __set (HashSet. #_"<>"))
                    (.. __set (add __uniqueKey))
                    (.. this mapKeys (put __multiKey, __set))
                )
                :else
                (do
                    (.. __set (add __uniqueKey))
                )
            )
            nil
        )
    )

    #_nilable
    #_public
    (§ method #_"ValueType" removeByUniqueKey [#_"UniqueKeyType" __key]
        (.. this mapValues (remove __key))
    )

    #_public
    (§ method #_"void" removeByMultiKey [#_"MultiKeyType" __key]
        (let [#_"Set<UniqueKeyType>" __set (.. this mapKeys (remove __key))]
            (when (some? __set)
                (doseq [#_"UniqueKeyType" __uniqueKey __set]
                    (.. this (removeByUniqueKey __uniqueKey))
                )
            )
            nil
        )
    )
)

;;;
 ; Keeps {@link StoredBlock}s, {@link StoredUndoableBlock}s and {@link org.bitcoinj.core.UTXO}s in memory.
 ; Used primarily for unit testing.
 ;;
#_public
(§ class MemoryFullPrunedBlockStore (§ implements FullPrunedBlockStore)
    #_protected
    #_static
    (§ class MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag
        #_public
        (§ field #_"StoredBlock" block)
        #_public
        (§ field #_"boolean" wasUndoable)
        #_public
        (§ constructor MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag [#_"StoredBlock" __block, #_"boolean" __wasUndoable]
            (§ ass (.. this block) __block)
            (§ ass (.. this wasUndoable) __wasUndoable)
            this
        )
    )
    #_private
    (§ field- #_"TransactionalHashMap<Sha256Hash, MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag>" blockMap)
    #_private
    (§ field- #_"TransactionalMultiKeyHashMap<Sha256Hash, Integer, StoredUndoableBlock>" fullBlockMap)
    ;; TODO: Use something more suited to remove-heavy use?
    #_private
    (§ field- #_"TransactionalHashMap<StoredTransactionOutPoint, UTXO>" transactionOutputMap)
    #_private
    (§ field- #_"StoredBlock" chainHead)
    #_private
    (§ field- #_"StoredBlock" verifiedChainHead)
    #_private
    (§ field- #_"int" fullStoreDepth)
    #_private
    (§ field- #_"NetworkParameters" params)

    ;;;
     ; Set up the MemoryFullPrunedBlockStore.
     ; @param params The network parameters of this block store - used to get genesis block.
     ; @param fullStoreDepth The depth of blocks to keep FullStoredBlocks instead of StoredBlocks.
     ;;
    #_public
    (§ constructor MemoryFullPrunedBlockStore [#_"NetworkParameters" __params, #_"int" __fullStoreDepth]
        (§ ass (.. this blockMap) (TransactionalHashMap. #_"<>"))
        (§ ass (.. this fullBlockMap) (TransactionalMultiKeyHashMap. #_"<>"))
        (§ ass (.. this transactionOutputMap) (TransactionalHashMap. #_"<>"))
        (§ ass (.. this fullStoreDepth) (if (< 0 __fullStoreDepth) __fullStoreDepth 1))
        ;; Insert the genesis block.
        (try
            (let [#_"StoredBlock" __storedGenesisHeader (StoredBlock. (.. __params (getGenesisBlock) (cloneAsHeader)), (.. __params (getGenesisBlock) (getWork)), 0)]
                ;; The coinbase in the genesis block is not spendable.
                (let [#_"List<Transaction>" __genesisTransactions (Lists/newLinkedList)
                      #_"StoredUndoableBlock" __storedGenesis (StoredUndoableBlock. (.. __params (getGenesisBlock) (getHash)), __genesisTransactions)]
                    (.. this (put __storedGenesisHeader, __storedGenesis))
                    (.. this (setChainHead __storedGenesisHeader))
                    (.. this (setVerifiedChainHead __storedGenesisHeader))
                    (§ ass (.. this params) __params)
                )
            )
            (catch BlockStoreException __e
                (throw (RuntimeException. __e)) ;; Cannot happen.
            )
            (catch VerificationException __e
                (throw (RuntimeException. __e)) ;; Cannot happen.
            )
        )
        this
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" put [#_"StoredBlock" __block]
        (Preconditions/checkNotNull (.. this blockMap), "MemoryFullPrunedBlockStore is closed")
        (let [#_"Sha256Hash" __hash (.. __block (getHeader) (getHash))]
            (.. this blockMap (put __hash, (MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag. __block, false)))
            nil
        )
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" put [#_"StoredBlock" __storedBlock, #_"StoredUndoableBlock" __undoableBlock]
        (Preconditions/checkNotNull (.. this blockMap), "MemoryFullPrunedBlockStore is closed")
        (let [#_"Sha256Hash" __hash (.. __storedBlock (getHeader) (getHash))]
            (.. this fullBlockMap (put __hash, (.. __storedBlock (getHeight)), __undoableBlock))
            (.. this blockMap (put __hash, (MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag. __storedBlock, true)))
            nil
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredBlock" get [#_"Sha256Hash" __hash]
        (Preconditions/checkNotNull (.. this blockMap), "MemoryFullPrunedBlockStore is closed")
        (let [#_"MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag" __storedBlock (.. this blockMap (get __hash))]
            (when (some? __storedBlock) (.. __storedBlock block))
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredBlock" getOnceUndoableStoredBlock [#_"Sha256Hash" __hash]
        (Preconditions/checkNotNull (.. this blockMap), "MemoryFullPrunedBlockStore is closed")
        (let [#_"MemoryFullPrunedBlockStore.StoredBlockAndWasUndoableFlag" __storedBlock (.. this blockMap (get __hash))]
            (when (and (some? __storedBlock) (.. __storedBlock wasUndoable)) (.. __storedBlock block))
        )
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredUndoableBlock" getUndoBlock [#_"Sha256Hash" __hash]
        (Preconditions/checkNotNull (.. this fullBlockMap), "MemoryFullPrunedBlockStore is closed")
        (.. this fullBlockMap (get __hash))
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredBlock" getChainHead []
        (Preconditions/checkNotNull (.. this blockMap), "MemoryFullPrunedBlockStore is closed")
        (.. this chainHead)
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" setChainHead [#_"StoredBlock" __chainHead]
        (Preconditions/checkNotNull (.. this blockMap), "MemoryFullPrunedBlockStore is closed")
        (§ ass (.. this chainHead) __chainHead)
        nil
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"StoredBlock" getVerifiedChainHead []
        (Preconditions/checkNotNull (.. this blockMap), "MemoryFullPrunedBlockStore is closed")
        (.. this verifiedChainHead)
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" setVerifiedChainHead [#_"StoredBlock" __chainHead]
        (Preconditions/checkNotNull (.. this blockMap), "MemoryFullPrunedBlockStore is closed")
        (§ ass (.. this verifiedChainHead) __chainHead)
        (when (< (.. this chainHead (getHeight)) (.. __chainHead (getHeight)))
            (.. this (setChainHead __chainHead))
        )
        ;; Potential leak here if not all blocks get setChainHead'd.
        ;; Though the FullPrunedBlockStore allows for this, the current AbstractBlockChain will not do it.
        (.. this fullBlockMap (removeByMultiKey (- (.. __chainHead (getHeight)) (.. this fullStoreDepth))))
        nil
    )

    #_override
    #_public
    (§ method #_"void" close []
        (§ ass (.. this blockMap) nil)
        (§ ass (.. this fullBlockMap) nil)
        (§ ass (.. this transactionOutputMap) nil)
        nil
    )

    #_override
    #_nilable
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"UTXO" getTransactionOutput [#_"Sha256Hash" __hash, #_"long" __index]
        (Preconditions/checkNotNull (.. this transactionOutputMap), "MemoryFullPrunedBlockStore is closed")
        (.. this transactionOutputMap (get (StoredTransactionOutPoint. __hash, __index)))
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" addUnspentTransactionOutput [#_"UTXO" __out]
        (Preconditions/checkNotNull (.. this transactionOutputMap), "MemoryFullPrunedBlockStore is closed")
        (.. this transactionOutputMap (put (StoredTransactionOutPoint. __out), __out))
        nil
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" removeUnspentTransactionOutput [#_"UTXO" __out]
        (Preconditions/checkNotNull (.. this transactionOutputMap), "MemoryFullPrunedBlockStore is closed")
        (when (nil? (.. this transactionOutputMap (remove (StoredTransactionOutPoint. __out))))
            (throw (BlockStoreException. "Tried to remove a UTXO from MemoryFullPrunedBlockStore that it didn't have!"))
        )
        nil
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" beginDatabaseBatchWrite []
        (.. this blockMap (beginDatabaseBatchWrite))
        (.. this fullBlockMap (beginTransaction))
        (.. this transactionOutputMap (beginDatabaseBatchWrite))
        nil
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" commitDatabaseBatchWrite []
        (.. this blockMap (commitDatabaseBatchWrite))
        (.. this fullBlockMap (commitTransaction))
        (.. this transactionOutputMap (commitDatabaseBatchWrite))
        nil
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"void" abortDatabaseBatchWrite []
        (.. this blockMap (abortDatabaseBatchWrite))
        (.. this fullBlockMap (abortTransaction))
        (.. this transactionOutputMap (abortDatabaseBatchWrite))
        nil
    )

    #_override
    #_public
    #_synchronized
    #_throws #_[ "BlockStoreException" ]
    (§ method #_"boolean" hasUnspentOutputs [#_"Sha256Hash" __hash, #_"int" __numOutputs]
        (loop-when-recur [#_"int" __i 0] (< __i __numOutputs) [(inc __i)]
            (when (some? (.. this (getTransactionOutput __hash, __i)))
                (§ return true)
            )
        )
        false
    )

    #_override
    #_public
    (§ method #_"NetworkParameters" getParams []
        (.. this params)
    )
)

;;;
 ; <p>A full pruned block store using the Postgres database engine.  As an added bonus an address index is calculated,
 ; so you can use {@link #calculateBalanceForAddress(org.bitcoinj.core.Address)} to quickly look up
 ; the quantity of bitcoins controlled by that address.</p>
 ;;
#_public
(§ class PostgresFullPrunedBlockStore (§ extends DatabaseFullPrunedBlockStore)
    #_private
    #_static
    (§ def- #_"Logger" PostgresFullPrunedBlockStore/log (LoggerFactory/getLogger PostgresFullPrunedBlockStore))

    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/POSTGRES_DUPLICATE_KEY_ERROR_CODE "23505")
    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/DATABASE_DRIVER_CLASS "org.postgresql.Driver")
    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/DATABASE_CONNECTION_URL_PREFIX "jdbc:postgresql://")

    ;; create table SQL
    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/CREATE_SETTINGS_TABLE (str "CREATE TABLE settings ("
            "name character varying(32) NOT NULL, "
            "value bytea, "
            "CONSTRAINT setting_pk PRIMARY KEY (name)"
            ")"))

    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/CREATE_HEADERS_TABLE (str "CREATE TABLE headers ("
            "hash bytea NOT NULL, "
            "chainwork bytea NOT NULL, "
