    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/CREATE_UNDOABLE_TABLE (str "CREATE TABLE undoableblocks ("
            "hash bytea NOT NULL, "
            "height integer NOT NULL, "
            "txoutchanges bytea, "
            "transactions bytea, "
            "CONSTRAINT undoableblocks_pk PRIMARY KEY (hash)"
            ")"))

    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/CREATE_OPEN_OUTPUT_TABLE (str "CREATE TABLE openoutputs ("
            "hash bytea NOT NULL, "
            "index integer NOT NULL, "
            "height integer NOT NULL, "
            "value bigint NOT NULL, "
            "scriptbytes bytea NOT NULL, "
            "toaddress character varying(35), "
            "addresstargetable smallint, "
            "coinbase boolean, "
            "CONSTRAINT openoutputs_pk PRIMARY KEY (hash,index)"
            ")"))

    ;; Some indexes to speed up inserts.
    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/CREATE_OUTPUTS_ADDRESS_MULTI_INDEX "CREATE INDEX openoutputs_hash_index_num_height_toaddress_idx ON openoutputs USING btree (hash, index, height, toaddress)")
    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/CREATE_OUTPUTS_TOADDRESS_INDEX "CREATE INDEX openoutputs_toaddress_idx ON openoutputs USING btree (toaddress)")
    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/CREATE_OUTPUTS_ADDRESSTARGETABLE_INDEX "CREATE INDEX openoutputs_addresstargetable_idx ON openoutputs USING btree (addresstargetable)")
    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/CREATE_OUTPUTS_HASH_INDEX "CREATE INDEX openoutputs_hash_idx ON openoutputs USING btree (hash)")
    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/CREATE_UNDOABLE_TABLE_INDEX "CREATE INDEX undoableblocks_height_idx ON undoableBlocks USING btree (height)")

    #_private
    #_static
    (§ def- #_"String" PostgresFullPrunedBlockStore/SELECT_UNDOABLEBLOCKS_EXISTS_SQL "select 1 from undoableblocks where hash = ?")

    ;;;
     ; Creates a new PostgresFullPrunedBlockStore.
     ;
     ; @param params A copy of the NetworkParameters used.
     ; @param fullStoreDepth The number of blocks of history stored in full (something like 1000 is pretty safe).
     ; @param hostname The hostname of the database to connect to.
     ; @param dbName The database to connect to.
