    #_public
    #_static
    (§ defn #_"<T> boolean" ListenerRegistration/removeFromList #_"<T>" [#_"T" __listener, #_"List<? extends ListenerRegistration<T>>" __list]
        (Preconditions/checkNotNull __listener)

