;;;
 ; A {@link KeyChain} that implements the simplest model possible: it can have keys imported into it, and just
 ; acts as a dumb bag of keys.  It will, left to its own devices, always return the same key for usage by the wallet,
 ; although it will automatically add one to itself if it's empty or if encryption is requested.
 ;;
#_public
(§ class BasicKeyChain (§ implements EncryptableKeyChain)
    #_private
    (§ field- #_"ReentrantLock" lock (Threading/lock "BasicKeyChain"))

    ;; Maps used to let us quickly look up a key given data we find in transcations or the block chain.
    #_private
    (§ field- #_"LinkedHashMap<ByteString, ECKey>" hashToKeys)
    #_private
    (§ field- #_"LinkedHashMap<ByteString, ECKey>" pubkeyToKeys)
    #_nilable
    #_private
    (§ field- #_"KeyCrypter" keyCrypter)
    #_private
    (§ field- #_"boolean" isWatching)

    #_private
    (§ field- #_"CopyOnWriteArrayList<ListenerRegistration<KeyChainEventListener>>" listeners)

    #_public
    (§ constructor BasicKeyChain []
        (§ this nil)
        this
    )

    #_public
    (§ constructor BasicKeyChain [#_nilable #_"KeyCrypter" __crypter]
        (§ ass (.. this keyCrypter) __crypter)
        (§ ass (.. this hashToKeys) (LinkedHashMap. #_"<>"))
        (§ ass (.. this pubkeyToKeys) (LinkedHashMap. #_"<>"))
        (§ ass (.. this listeners) (CopyOnWriteArrayList. #_"<>"))
        this
    )

    ;;; Returns the {@link KeyCrypter} in use or null if the key chain is not encrypted. ;;
    #_override
    #_nilable
    #_public
    (§ method #_"KeyCrypter" getKeyCrypter []
        (.. this lock (lock))
        (try
            (§ return (.. this keyCrypter))
            (finally
                (.. this lock (unlock))
            )
        )
    )

    #_override
    #_public
    (§ method #_"ECKey" getKey [#_nilable #_"KeyChain.KeyPurpose" __ignored]
        (.. this lock (lock))
        (try
            (when (.. this hashToKeys (isEmpty))
                ;; We will refuse to encrypt an empty key chain.
                (Preconditions/checkState (nil? (.. this keyCrypter)))

                (let [#_"ECKey" __key (ECKey.)]
                    (.. this (importKeyLocked __key))
                    (.. this (queueOnKeysAdded (ImmutableList/of __key)))
                )
            )
            (§ return (.. this hashToKeys (values) (iterator) (next)))
            (finally
                (.. this lock (unlock))
            )
        )
    )

    #_override
    #_public
    (§ method #_"List<ECKey>" getKeys [#_nilable #_"KeyChain.KeyPurpose" __purpose, #_"int" __numberOfKeys]
        (Preconditions/checkArgument (< 0 __numberOfKeys))

        (.. this lock (lock))
        (try
            (when (< (.. this hashToKeys (size)) __numberOfKeys)
                (Preconditions/checkState (nil? (.. this keyCrypter)))

                (let [#_"List<ECKey>" __keys (ArrayList. #_"<>")]
