        (let [#_"int" __numChildren (.. this hierarchy (getNumChildren (.. __parent (getPath))))
              #_"int" __needed (- (+ __issued __lookaheadSize __lookaheadThreshold) __numChildren)]

            (when (<= __needed __lookaheadThreshold)
