;;;
 ; Holds the seed bytes for the BIP32 deterministic wallet algorithm, inside a {@link DeterministicKeyChain}.
 ; The purpose of this wrapper is to simplify the encryption code.
 ;;
#_public
(§ class DeterministicSeed (§ implements EncryptableItem)
    ;; It would take more than 10^12 years to brute-force a 128 bit seed using $1B worth of computing equipment.
    #_public
    #_static
    (§ def #_"int" DeterministicSeed/DEFAULT_SEED_ENTROPY_BITS 128)
    #_public
    #_static
    (§ def #_"int" DeterministicSeed/MAX_SEED_ENTROPY_BITS 512)

    #_nilable
    #_private
    (§ field- #_"byte[]" seed)
    #_nilable
    #_private
    (§ field- #_"List<String>" mnemonicCode) ;; only one of mnemonicCode/encryptedMnemonicCode will be set
    #_nilable
    #_private
    (§ field- #_"EncryptedData" encryptedMnemonicCode)
    #_nilable
    #_private
    (§ field- #_"EncryptedData" encryptedSeed)
    #_private
    (§ field- #_"long" creationTimeSeconds)

    #_public
    #_throws #_[ "UnreadableWalletException" ]
    (§ constructor DeterministicSeed [#_"String" __mnemonicCode, #_"byte[]" __seed, #_"String" __passphrase, #_"long" __creationTimeSeconds]
        (§ this (DeterministicSeed/decodeMnemonicCode __mnemonicCode), __seed, __passphrase, __creationTimeSeconds)
        this
    )

    #_public
    (§ constructor DeterministicSeed [#_"byte[]" __seed, #_"List<String>" __mnemonic, #_"long" __creationTimeSeconds]
        (§ ass (.. this seed) (Preconditions/checkNotNull __seed))
        (§ ass (.. this mnemonicCode) (Preconditions/checkNotNull __mnemonic))
        (§ ass (.. this encryptedMnemonicCode) nil)
        (§ ass (.. this creationTimeSeconds) __creationTimeSeconds)
        this
    )

    #_public
    (§ constructor DeterministicSeed [#_"EncryptedData" __encryptedMnemonic, #_nilable #_"EncryptedData" __encryptedSeed, #_"long" __creationTimeSeconds]
        (§ ass (.. this seed) nil)
        (§ ass (.. this mnemonicCode) nil)
        (§ ass (.. this encryptedMnemonicCode) (Preconditions/checkNotNull __encryptedMnemonic))
        (§ ass (.. this encryptedSeed) __encryptedSeed)
        (§ ass (.. this creationTimeSeconds) __creationTimeSeconds)
        this
    )

    ;;;
     ; Constructs a seed from a BIP 39 mnemonic code.
     ; See {@link org.bitcoinj.crypto.MnemonicCode} for more details on this scheme.
     ; @param mnemonicCode A list of words.
     ; @param seed The derived seed, or pass null to derive it from mnemonicCode (slow).
     ; @param passphrase A user supplied passphrase, or an empty string if there is no passphrase.
     ; @param creationTimeSeconds When the seed was originally created, UNIX time.
     ;;
    #_public
    (§ constructor DeterministicSeed [#_"List<String>" __mnemonicCode, #_nilable #_"byte[]" __seed, #_"String" __passphrase, #_"long" __creationTimeSeconds]
        (§ this (or __seed (MnemonicCode/toSeed __mnemonicCode, (Preconditions/checkNotNull __passphrase))), __mnemonicCode, __creationTimeSeconds)
        this
    )

    ;;;
     ; Constructs a seed from a BIP 39 mnemonic code.
     ; See {@link org.bitcoinj.crypto.MnemonicCode} for more details on this scheme.
     ; @param random Entropy source.
     ; @param bits Number of bits, must be divisible by 32.
     ; @param passphrase A user supplied passphrase, or an empty string if there is no passphrase.
     ; @param creationTimeSeconds When the seed was originally created, UNIX time.
     ;;
    #_public
    (§ constructor DeterministicSeed [#_"SecureRandom" __random, #_"int" __bits, #_"String" __passphrase, #_"long" __creationTimeSeconds]
        (§ this (DeterministicSeed/getEntropy __random, __bits), (Preconditions/checkNotNull __passphrase), __creationTimeSeconds)
        this
    )

    ;;;
     ; Constructs a seed from a BIP 39 mnemonic code.
     ; See {@link org.bitcoinj.crypto.MnemonicCode} for more details on this scheme.
     ; @param entropy Entropy bits, length must be divisible by 32.
     ; @param passphrase A user supplied passphrase, or an empty string if there is no passphrase.
     ; @param creationTimeSeconds When the seed was originally created, UNIX time.
     ;;
    #_public
    (§ constructor DeterministicSeed [#_"byte[]" __entropy, #_"String" __passphrase, #_"long" __creationTimeSeconds]
        (Preconditions/checkArgument (== (% (.. __entropy (alength)) 4) 0), "entropy size in bits not divisible by 32")
        (Preconditions/checkArgument (<= DeterministicSeed/DEFAULT_SEED_ENTROPY_BITS (* (.. __entropy (alength)) 8)), "entropy size too small")
        (Preconditions/checkNotNull __passphrase)

        (try
            (§ ass (.. this mnemonicCode) (.. MnemonicCode/INSTANCE (toMnemonic __entropy)))
            (catch MnemonicException.MnemonicLengthException __e
                (throw (RuntimeException. __e)) ;; Cannot happen.
            )
        )
        (§ ass (.. this seed) (MnemonicCode/toSeed (.. this mnemonicCode), __passphrase))
        (§ ass (.. this encryptedMnemonicCode) nil)
        (§ ass (.. this creationTimeSeconds) __creationTimeSeconds)
        this
    )

    #_private
    #_static
    (§ defn- #_"byte[]" DeterministicSeed/getEntropy [#_"SecureRandom" __random, #_"int" __bits]
        (Preconditions/checkArgument (<= __bits DeterministicSeed/MAX_SEED_ENTROPY_BITS), "requested entropy size too large")

        (let [#_"byte[]" __seed (byte-array (/ __bits 8))]
            (.. __random (nextBytes __seed))
            __seed
        )
    )

    #_override
    #_public
    (§ method #_"boolean" isEncrypted []
        (Preconditions/checkState (or (some? (.. this mnemonicCode)) (some? (.. this encryptedMnemonicCode))))
        (some? (.. this encryptedMnemonicCode))
    )

    #_override
    #_public
    (§ method #_"String" toString []
        (if (.. this (isEncrypted)) "DeterministicSeed [encrypted]" (str "DeterministicSeed " (.. this (toHexString)) " " (.. Utils/SPACE_JOINER (join (.. this mnemonicCode)))))
    )

    ;;; Returns the seed as hex or null if encrypted. ;;
    #_nilable
    #_public
    (§ method #_"String" toHexString []
        (when (some? (.. this seed)) (.. Utils/HEX (encode (.. this seed))))
    )

    #_nilable
    #_override
    #_public
    (§ method #_"byte[]" getSecretBytes []
        (.. this (getMnemonicAsBytes))
    )

    #_nilable
    #_public
    (§ method #_"byte[]" getSeedBytes []
        (.. this seed)
    )

    #_nilable
    #_override
    #_public
    (§ method #_"EncryptedData" getEncryptedData []
        (.. this encryptedMnemonicCode)
    )

    #_override
    #_public
    (§ method #_"Protos.Wallet.EncryptionType" getEncryptionType []
        Protos.Wallet.EncryptionType/ENCRYPTED_SCRYPT_AES
    )

    #_nilable
    #_public
    (§ method #_"EncryptedData" getEncryptedSeedData []
        (.. this encryptedSeed)
    )

    #_override
    #_public
    (§ method #_"long" getCreationTimeSeconds []
        (.. this creationTimeSeconds)
    )

    #_public
    (§ method #_"void" setCreationTimeSeconds [#_"long" __creationTimeSeconds]
        (§ ass (.. this creationTimeSeconds) __creationTimeSeconds)
        nil
    )

    #_public
    (§ method #_"DeterministicSeed" encrypt [#_"KeyCrypter" __keyCrypter, #_"KeyParameter" __aesKey]
        (Preconditions/checkState (nil? (.. this encryptedMnemonicCode)), "Trying to encrypt seed twice")
        (Preconditions/checkState (some? (.. this mnemonicCode)), "Mnemonic missing so cannot encrypt")

        (let [#_"EncryptedData" __encryptedMnemonic (.. __keyCrypter (encrypt (.. this (getMnemonicAsBytes)), __aesKey))
              #_"EncryptedData" __encryptedSeed (.. __keyCrypter (encrypt (.. this seed), __aesKey))]
            (DeterministicSeed. __encryptedMnemonic, __encryptedSeed, (.. this creationTimeSeconds))
        )
    )

    #_private
    (§ method- #_"byte[]" getMnemonicAsBytes []
        (.. Utils/SPACE_JOINER (join (.. this mnemonicCode)) (getBytes Charsets/UTF_8))
    )

    #_public
    (§ method #_"DeterministicSeed" decrypt [#_"KeyCrypter" __crypter, #_"String" __passphrase, #_"KeyParameter" __aesKey]
        (Preconditions/checkState (.. this (isEncrypted)))
        (Preconditions/checkNotNull (.. this encryptedMnemonicCode))

        (let [#_"List<String>" __mnemonic (DeterministicSeed/decodeMnemonicCode (.. __crypter (decrypt (.. this encryptedMnemonicCode), __aesKey)))
              #_"byte[]" __seed (when (some? (.. this encryptedSeed)) (.. __crypter (decrypt (.. this encryptedSeed), __aesKey)))]
            (DeterministicSeed. __mnemonic, __seed, __passphrase, (.. this creationTimeSeconds))
        )
    )

    #_override
    #_public
    (§ method #_"boolean" equals [#_"Object" __o]
        (when (== this __o)
            (§ return true)
        )
        (when (or (nil? __o) (!= (getClass) (.. __o (getClass))))
            (§ return false)
        )
        (let [#_"DeterministicSeed" __other (cast DeterministicSeed __o)]
            (and (== (.. this creationTimeSeconds) (.. __other creationTimeSeconds)) (Objects/equal (.. this encryptedMnemonicCode), (.. __other encryptedMnemonicCode)) (Objects/equal (.. this mnemonicCode), (.. __other mnemonicCode)))
        )
    )

    #_override
    #_public
    (§ method #_"int" hashCode []
        (Objects/hashCode (.. this creationTimeSeconds), (.. this encryptedMnemonicCode), (.. this mnemonicCode))
    )

    ;;;
     ; Check if our mnemonic is a valid mnemonic phrase for our word list.
     ; Does nothing if we are encrypted.
     ;
     ; @throws org.bitcoinj.crypto.MnemonicException if check fails.
     ;;
    #_public
    #_throws #_[ "MnemonicException" ]
    (§ method #_"void" check []
        (when (some? (.. this mnemonicCode))
            (.. MnemonicCode/INSTANCE (check (.. this mnemonicCode)))
        )
        nil
    )

    #_throws #_[ "MnemonicException" ]
    (§ method #_"byte[]" getEntropyBytes []
        (.. MnemonicCode/INSTANCE (toEntropy (.. this mnemonicCode)))
    )

    ;;; Get the mnemonic code, or null if unknown. ;;
    #_nilable
    #_public
    (§ method #_"List<String>" getMnemonicCode []
        (.. this mnemonicCode)
    )

    #_private
    #_static
    (§ defn- #_"List<String>" DeterministicSeed/decodeMnemonicCode [#_"byte[]" __mnemonicCode]
