                (when (== __activeState BasicKeyChain.State/EMPTY)
                    (§ return (== __basicState BasicKeyChain.State/WATCHING))
                )

                (when (!= __activeState __basicState)
                    (throw (IllegalStateException. "Mix of watching and non-watching keys in wallet"))
