    #_public
    (§ method #_"List<ECKey>" getIssuedReceiveKeys []
        (.. this keyChainGroupLock (lock))
        (try
            (§ return (.. this keyChainGroup (getActiveKeyChain) (getIssuedReceiveKeys)))
            (finally
                (.. this keyChainGroupLock (unlock))
            )
