            ;; Firstly, put all transactions into an array.
            (let [#_"int" __size (+ (.. this unspent (size)) (.. this spent (size)) (.. this pending (size)))]
                (when (or (< __size __numTransactions) (== __numTransactions 0))
                    (§ ass __numTransactions __size)
