     ; @param merkleRoot The root of the merkle tree formed by the transactions.
     ; @param time UNIX time when the block was mined.
     ; @param difficultyTarget Number which this block hashes lower than.
     ; @param nonce Arbitrary number to make the block hash lower than the target.
     ; @param transactions List of transactions including the coinbase.
     ;;
    #_public
    (§ constructor #_"Block" [#_"NetworkParameters" (§ name ßparams), #_"long" (§ name ßversion), #_"Sha256Hash" (§ name ßprevBlockHash), #_"Sha256Hash" (§ name ßmerkleRoot), #_"long" (§ name ßtime), #_"long" (§ name ßdifficultyTarget), #_"long" (§ name ßnonce), #_"List<Transaction>" (§ name ßtransactions)])
    (§ block
        (§ super ßparams)

        (§ ass (§ name (.. this ßversion)) ßversion)
        (§ ass (§ name (.. this ßprevBlockHash)) ßprevBlockHash)
        (§ ass (§ name (.. this ßmerkleRoot)) ßmerkleRoot)
        (§ ass (§ name (.. this ßtime)) ßtime)
        (§ ass (§ name (.. this ßdifficultyTarget)) ßdifficultyTarget)
