    ;;;
     ; <p>A utility method that calculates how much new Bitcoin would be created by the block at the given height.
     ; The inflation of Bitcoin is predictable and drops roughly every 4 years (210,000 blocks).  At the dawn of
     ; the system it was 50 coins per block, in late 2012 it went to 25 coins per block, and so on.  The size of
     ; a coinbase transaction is inflation plus fees.</p>
     ;
     ; The half-life is controlled by {@link org.bitcoinj.core.NetworkParameters#getSubsidyDecreaseBlockCount()}.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getBlockInflation) [#_"int" (§ name ßheight)])
    (§ block
        (.. FIFTY_COINS (shiftRight (/ ßheight (.. params (getSubsidyDecreaseBlockCount)))))
    )

    ;;;
     ; Parse transactions from the block.
     ;
     ; @param transactionsOffset Offset of the transactions within the block.
     ; Useful for non-Bitcoin chains where the block header may not be a fixed size.
     ;;
    #_protected
    (§ method #_"void" (§ fn parseTransactions) [#_"int" (§ name ßtransactionsOffset)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ ass (§ name cursor) ßtransactionsOffset)
        (§ ass (§ name optimalEncodingMessageSize) HEADER_SIZE)
        (§ if (== (.. payload length) cursor))
        (§ block
            ;; This message is just a header, it has no transactions.
            (§ ass (§ name transactionBytesValid) false)
            (§ return nil)
        )

        (§ let #_"int" (§ name ßnumTransactions) (§ cast #_"int" (readVarInt)))
        (§ block
            (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize (VarInt/sizeOf ßnumTransactions)))
            (§ ass (§ name transactions) (§ new #_"ArrayList<>" ßnumTransactions))
            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumTransactions) :for (§ recur (+ ßi 1)))
            (§ block
                (§ let #_"Transaction" (§ name ßtx) (§ new #_"Transaction" params, payload, cursor, this, serializer, UNKNOWN_LENGTH))
                (§ block
                    ;; Label the transaction as coming from the P2P network, so code that cares where we first saw it knows.
                    (.. ßtx (getConfidence) (setSource TransactionConfidence/Source/NETWORK))
                    (.. transactions (add ßtx))
                    (§ ass (§ name cursor) (+ cursor (.. ßtx (getMessageSize))))
                    (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize (.. ßtx (getOptimalEncodingMessageSize))))
                )
            )
            (§ ass (§ name transactionBytesValid) (.. serializer (isParseRetainMode)))
            nil
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        ;; header
        (§ ass (§ name cursor) offset)
        (§ ass (§ name version) (readUint32))
        (§ ass (§ name prevBlockHash) (readHash))
        (§ ass (§ name merkleRoot) (readHash))
        (§ ass (§ name time) (readUint32))
        (§ ass (§ name difficultyTarget) (readUint32))
        (§ ass (§ name nonce) (readUint32))
        (§ ass (§ name hash) (Sha256Hash/wrapReversed (Sha256Hash/hashTwice payload, offset, (- cursor offset))))
        (§ ass (§ name headerBytesValid) (.. serializer (isParseRetainMode)))

        ;; transactions
        (parseTransactions (+ offset HEADER_SIZE))
        (§ ass (§ name length) (- cursor offset))
        nil
    )

    #_public
    (§ method #_"int" (§ fn getOptimalEncodingMessageSize) [])
    (§ block
        (§ if (!= optimalEncodingMessageSize 0))
        (§ block
            (§ return optimalEncodingMessageSize)
        )
        (§ ass (§ name optimalEncodingMessageSize) (.. (bitcoinSerialize) length))
        optimalEncodingMessageSize
    )

    ;; default for testing
    (§ method #_"void" (§ fn writeHeader) [#_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        ;; try for cached write first
        (§ if (and headerBytesValid (some? payload) (<= (+ offset HEADER_SIZE) (.. payload length))))
        (§ block
            (.. ßstream (write payload, offset, HEADER_SIZE))
            (§ return nil)
        )

        ;; fall back to manual write
        (Utils/uint32ToByteStreamLE version, ßstream)
        (.. ßstream (write (.. prevBlockHash (getReversedBytes))))
        (.. ßstream (write (.. (getMerkleRoot) (getReversedBytes))))
        (Utils/uint32ToByteStreamLE time, ßstream)
        (Utils/uint32ToByteStreamLE difficultyTarget, ßstream)
        (Utils/uint32ToByteStreamLE nonce, ßstream)
        nil
    )

    #_private
    (§ method- #_"void" (§ fn writeTransactions) [#_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        ;; check for no transaction conditions first
        (§ if (nil? transactions))
        (§ block
            (§ return nil)
        )

        ;; confirmed we must have transactions either cached or as objects
        (§ if (and transactionBytesValid (some? payload) (<= (+ offset length) (.. payload length))))
        (§ block
            (.. ßstream (write payload, (+ offset HEADER_SIZE), (- length HEADER_SIZE)))
            (§ return nil)
        )

        (§ if (some? transactions))
        (§ block
            (.. ßstream (write (.. (§ new #_"VarInt" (.. transactions (size))) (encode))))
            (§ for (§ var #_"Transaction" (§ name ßtx)) :for transactions)
            (§ block
                (.. ßtx (bitcoinSerialize ßstream))
            )
        )
        nil
    )

    ;;;
     ; Special handling to check if we have a valid byte array for both header and transactions.
     ;
     ; @throws IOException
     ;;
    #_override
    #_public
    (§ method #_"byte[]" (§ fn bitcoinSerialize) [])
    (§ block
        ;; we have completely cached byte array
        (§ if (and headerBytesValid transactionBytesValid))
        (§ block
            (Preconditions/checkNotNull payload, "Bytes should never be nil if headerBytesValid && transactionBytesValid")
            (§ if (== length (.. payload length)))
            (§ block
                (§ return payload)
            )

            ;; byte array is offset, so copy out the correct range
            (§ let #_"byte[]" (§ name ßbuf) (§ new #_"byte[]" (§ count length)))
            (§ block
                (System/arraycopy payload, offset, ßbuf, 0, length)
                (§ return ßbuf)
            )
        )

        ;; At least one of the two cacheable components is invalid, so fall back to stream write since we can't be sure of the length.
        (§ let #_"ByteArrayOutputStream" (§ name ßstream) (§ new #_"UnsafeByteArrayOutputStream" (if (== length UNKNOWN_LENGTH) (+ HEADER_SIZE (guessTransactionsLength)) length)))
        (§ block
            (§ try )
            (§ block
                (writeHeader ßstream)
                (writeTransactions ßstream)
            )
            (§ catch #_"IOException" (§ name ße))
            (§ block
                ;; Cannot happen, we are serializing to a memory stream.
            )
            (.. ßstream (toByteArray))
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        (writeHeader ßstream)
        ;; We may only have enough data to write the header.
        (writeTransactions ßstream)
        nil
    )

    ;;;
     ; Provides a reasonable guess at the byte length of the transactions part of the block.
     ; The returned value will be accurate in 99% of cases and in those cases where not will probably slightly oversize.
     ;
     ; This is used to preallocate the underlying byte array for a ByteArrayOutputStream.  If the size is under the
     ; real value the only penalty is resizing of the underlying byte array.
     ;;
    #_private
    (§ method- #_"int" (§ fn guessTransactionsLength) [])
    (§ block
        (§ if transactionBytesValid)
        (§ block
            (§ return (- (.. payload length) HEADER_SIZE))
        )
        (§ if (nil? transactions))
        (§ block
            (§ return 0)
        )
        (§ let #_"int" (§ name ßlen) (VarInt/sizeOf (.. transactions (size))))
        (§ block
            (§ for (§ var #_"Transaction" (§ name ßtx)) :for transactions)
            (§ block
                ;; 255 is just a guess at an average tx length
                (§ ass (§ name ßlen) (+ ßlen (if (== (.. ßtx length) UNKNOWN_LENGTH) 255 (.. ßtx length))))
            )
            ßlen
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn unCache) [])
    (§ block
        ;; Since we have alternate uncache methods to use internally, this will only ever be called by a child
        ;; transaction, so we only need to invalidate that part of the cache.
        (unCacheTransactions)
        nil
    )

    #_private
    (§ method- #_"void" (§ fn unCacheHeader) [])
    (§ block
        (§ ass (§ name headerBytesValid) false)
        (§ if (not transactionBytesValid))
        (§ block
            (§ ass (§ name payload) nil)
        )
        (§ ass (§ name hash) nil)
        nil
    )

    #_private
    (§ method- #_"void" (§ fn unCacheTransactions) [])
    (§ block
        (§ ass (§ name transactionBytesValid) false)
        (§ if (not headerBytesValid))
        (§ block
            (§ ass (§ name payload) nil)
        )
        ;; Current implementation has to uncache headers as well as any change to a tx will alter the merkle root.
        ;; In future, we can go more granular and cache merkle root separately so rest of the header does not need to be rewritten.
        (unCacheHeader)
        ;; Clear merkleRoot last as it may end up being parsed during unCacheHeader().
        (§ ass (§ name merkleRoot) nil)
        nil
    )

    ;;;
     ; Calculates the block hash by serializing the block and hashing the resulting bytes.
     ;;
    #_private
    (§ method- #_"Sha256Hash" (§ fn calculateHash) [])
    (§ block
        (§ try )
        (§ block
            (§ let #_"ByteArrayOutputStream" (§ name ßbos) (§ new #_"UnsafeByteArrayOutputStream" HEADER_SIZE))
            (§ block
                (writeHeader ßbos)
                (§ return (Sha256Hash/wrapReversed (Sha256Hash/hashTwice (.. ßbos (toByteArray)))))
            )
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
        )
    )

    ;;;
     ; Returns the hash of the block (which for a valid, solved block should be below the target) in the form seen on
     ; the block explorer.  If you call this on block 1 in the mainnet chain,
     ; you will get "00000000839a8e6886ab5951d76f411475428afc90947ee320161bbf18eb6048".
     ;;
    #_public
    (§ method #_"String" (§ fn getHashAsString) [])
    (§ block
        (.. (getHash) (toString))
    )

    ;;;
     ; Returns the hash of the block (which for a valid, solved block should be
     ; below the target).  Big endian.
     ;;
    #_override
    #_public
    (§ method #_"Sha256Hash" (§ fn getHash) [])
    (§ block
        (§ if (nil? hash))
        (§ block
            (§ ass (§ name hash) (calculateHash))
        )
        hash
    )

    ;;;
     ; The number that is one greater than the largest representable SHA-256 hash.
     ;;
    #_private
    #_static
    (§ def- #_"BigInteger" (§ name LARGEST_HASH) (.. BigInteger/ONE (shiftLeft 256)))

    ;;;
     ; Returns the work represented by this block.
     ;
     ; Work is defined as the number of tries needed to solve a block in the
     ; average case.  Consider a difficulty target that covers 5% of all possible
     ; hash values.  Then the work of the block will be 20.  As the target gets
     ; lower, the amount of work goes up.
     ;;
    #_public
    (§ method #_"BigInteger" (§ fn getWork) [])
        (§ throws #_"VerificationException")
    (§ block
        (§ let #_"BigInteger" (§ name ßtarget) (getDifficultyTargetAsInteger))
        (§ block
            (.. LARGEST_HASH (divide (.. ßtarget (add BigInteger/ONE))))
        )
    )

    ;;; Returns a copy of the block, but without any transactions. ;;
    #_public
    (§ method #_"Block" (§ fn cloneAsHeader) [])
    (§ block
        (§ let #_"Block" (§ name ßblock) (§ new #_"Block" params, BLOCK_VERSION_GENESIS))
        (§ block
            (copyBitcoinHeaderTo ßblock)
            ßblock
        )
    )

    ;;; Copy the block without transactions into the provided empty block. ;;
    #_protected
    (§ method #_"void" (§ fn copyBitcoinHeaderTo) [#_"Block" (§ name ßblock)])
    (§ block
        (§ ass (§ name (.. ßblock nonce)) nonce)
        (§ ass (§ name (.. ßblock prevBlockHash)) prevBlockHash)
        (§ ass (§ name (.. ßblock merkleRoot)) (getMerkleRoot))
        (§ ass (§ name (.. ßblock version)) version)
        (§ ass (§ name (.. ßblock time)) time)
        (§ ass (§ name (.. ßblock difficultyTarget)) difficultyTarget)
        (§ ass (§ name (.. ßblock transactions)) nil)
        (§ ass (§ name (.. ßblock hash)) (getHash))
        nil
    )

    ;;;
     ; Returns a multi-line string containing a description of the contents of the block.
     ; Use for debugging purposes only.
     ;;
    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (§ let #_"StringBuilder" (§ name ßsb) (§ new #_"StringBuilder"))
        (§ block
            (.. ßsb (append " block: \n"))
            (.. ßsb (append "   hash: ") (append (getHashAsString)) (append "\n"))
            (.. ßsb (append "   version: ") (append version))
            (§ let #_"String" (§ name ßbips) (.. (Joiner/on ", ") (skipNulls) (join (when (isBIP34) "BIP34"), (when (isBIP66) "BIP66"), (when (isBIP65) "BIP65"))))
            (§ block
                (§ if (not (.. ßbips (isEmpty))))
                (§ block
                    (.. ßsb (append " (") (append ßbips) (append ")"))
                )
                (.. ßsb (append "\n"))
                (.. ßsb (append "   previous block: ") (append (getPrevBlockHash)) (append "\n"))
                (.. ßsb (append "   merkle root: ") (append (getMerkleRoot)) (append "\n"))
                (.. ßsb (append "   time: ") (append time) (append " (") (append (Utils/dateTimeFormat (* time 1000))) (append ")\n"))
                (.. ßsb (append "   difficulty target (nBits): ") (append difficultyTarget) (append "\n"))
                (.. ßsb (append "   nonce: ") (append nonce) (append "\n"))
                (§ if (and (some? transactions) (< 0 (.. transactions (size)))))
                (§ block
                    (.. ßsb (append "   with ") (append (.. transactions (size))) (append " transaction(s):\n"))
                    (§ for (§ var #_"Transaction" (§ name ßtx)) :for transactions)
                    (§ block
                        (.. ßsb (append ßtx))
                    )
                )
                (.. ßsb (toString))
            )
        )
    )

    ;;;
     ; <p>Finds a value of nonce that makes the blocks hash lower than the difficulty target.  This is called mining,
     ; but solve() is far too slow to do real mining with.  It exists only for unit testing purposes.
     ;
     ; <p>This can loop forever if a solution cannot be found solely by incrementing nonce.  It doesn't change
     ; extraNonce.</p>
     ;;
    #_public
    (§ method #_"void" (§ fn solve) [])
    (§ block
        (§ while true)
        (§ block
            (§ try )
            (§ block
                ;; Is our proof of work valid yet?
                (§ if (checkProofOfWork false))
                (§ block
                    (§ return nil)
                )
                ;; No, so increment the nonce and try again.
                (setNonce (+ (getNonce) 1))
            )
            (§ catch #_"VerificationException" (§ name ße))
            (§ block
                (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
            )
        )
        nil
    )

    ;;;
     ; Returns the difficulty target as a 256 bit value that can be compared to a SHA-256 hash.  Inside a block the
     ; target is represented using a compact form.  If this form decodes to a value that is out of bounds, an exception
     ; is thrown.
     ;;
    #_public
    (§ method #_"BigInteger" (§ fn getDifficultyTargetAsInteger) [])
        (§ throws #_"VerificationException")
    (§ block
        (§ let #_"BigInteger" (§ name ßtarget) (Utils/decodeCompactBits difficultyTarget))
        (§ block
            (§ if (or (<= (.. ßtarget (signum)) 0) (< 0 (.. ßtarget (compareTo (.. params maxTarget))))))
            (§ block
                (§ throw (§ new #_"VerificationException" (str "Difficulty target is bad: " ßtarget)))
            )
            ßtarget
        )
    )

    ;;; Returns true if the hash of the block is OK (lower than difficulty target). ;;
    #_protected
    (§ method #_"boolean" (§ fn checkProofOfWork) [#_"boolean" (§ name ßthrowException)])
        (§ throws #_"VerificationException")
    (§ block
        ;; This part is key - it is what proves the block was as difficult to make as it claims
        ;; to be.  Note however that in the context of this function, the block can claim to be
        ;; as difficult as it wants to be .... if somebody was able to take control of our network
        ;; connection and fork us onto a different chain, they could send us valid blocks with
        ;; ridiculously easy difficulty and this function would accept them.
        ;;
        ;; To prevent this attack from being possible, elsewhere we check that the difficultyTarget
        ;; field is of the right value.  This requires us to have the preceeding blocks.
        (§ let #_"BigInteger" (§ name ßtarget) (getDifficultyTargetAsInteger))
        (§ block

            (§ let #_"BigInteger" (§ name ßh) (.. (getHash) (toBigInteger)))
            (§ block
                (§ if (< 0 (.. ßh (compareTo ßtarget))))
                (§ block
                    ;; Proof of work check failed!
                    (§ if ßthrowException)
                    (§ block
                        (§ throw (§ new #_"VerificationException" (str "Hash is higher than target: " (getHashAsString) " vs " (.. ßtarget (toString 16)))))
                    )
                    (§ else )
                    (§ block
                        (§ return false)
                    )
                )
                true
            )
        )
    )

    #_private
    (§ method- #_"void" (§ fn checkTimestamp) [])
        (§ throws #_"VerificationException")
    (§ block
        (§ let #_"long" (§ name ßallowedTime) (+ (Utils/currentTimeSeconds) ALLOWED_TIME_DRIFT))
        (§ block
            (§ if (< ßallowedTime time))
            (§ block
                (§ throw (§ new #_"VerificationException" (String/format Locale/US, "Block too far in future: %s (%d) vs allowed %s (%d)", (Utils/dateTimeFormat (* time 1000)), time, (Utils/dateTimeFormat (* ßallowedTime 1000)), ßallowedTime)))
            )
            nil
        )
    )

    #_private
    (§ method- #_"void" (§ fn checkSigOps) [])
        (§ throws #_"VerificationException")
    (§ block
        ;; Check there aren't too many signature verifications in the block.  This is an anti-DoS measure, see the
        ;; comments for MAX_BLOCK_SIGOPS.
        (§ let #_"int" (§ name ßsigOps) 0)
        (§ block
            (§ for (§ var #_"Transaction" (§ name ßtx)) :for transactions)
            (§ block
                (§ ass (§ name ßsigOps) (+ ßsigOps (.. ßtx (getSigOpCount))))
            )

            (§ if (< MAX_BLOCK_SIGOPS ßsigOps))
            (§ block
                (§ throw (§ new #_"VerificationException" "Block had too many Signature Operations"))
            )
            nil
        )
    )

    #_private
    (§ method- #_"void" (§ fn checkMerkleRoot) [])
        (§ throws #_"VerificationException")
    (§ block
        (§ let #_"Sha256Hash" (§ name ßcalculatedRoot) (calculateMerkleRoot))
        (§ block
            (§ if (not (.. ßcalculatedRoot (equals merkleRoot))))
            (§ block
                (.. log (error "Merkle tree did not verify"))
                (§ throw (§ new #_"VerificationException" (str "Merkle hashes do not match: " ßcalculatedRoot " vs " merkleRoot)))
            )
            nil
        )
    )

    #_private
    (§ method- #_"Sha256Hash" (§ fn calculateMerkleRoot) [])
    (§ block
        (§ let #_"List<byte[]>" (§ name ßtree) (buildMerkleTree))
        (§ block
            (Sha256Hash/wrap (.. ßtree (get (- (.. ßtree (size)) 1))))
        )
    )

    #_private
    (§ method- #_"List<byte[]>" (§ fn buildMerkleTree) [])
    (§ block
        ;; The Merkle root is based on a tree of hashes calculated from the transactions:
        ;;
        ;;     root
        ;;     /  \
        ;;   A      B
        ;;  / \    / \
        ;; t1 t2  t3 t4
        ;;
        ;; The tree is represented as a list: (t1 t2 t3 t4 A B root), where each entry is a hash.
        ;;
        ;; The hashing algorithm is double SHA-256.  The leaves are a hash of the serialized contents of the transaction.
        ;; The interior nodes are hashes of the concenation of the two child hashes.
        ;;
        ;; This structure allows the creation of proof that a transaction was included into a block without having to
        ;; provide the full block contents.  Instead, you can provide only a Merkle branch.  For example to prove tx2 was
        ;; in a block you can just provide tx2, the hash(tx1) and B.  Now the other party has everything they need to
        ;; derive the root, which can be checked against the block header.  These proofs aren't used right now but
        ;; will be helpful later when we want to download partial block contents.
        ;;
        ;; Note that if the number of transactions is not even, the last tx is repeated to make it so (see tx3 above).
        ;; A tree with 5 transactions would look like this:
        ;;
        ;;          root
        ;;        /      \
        ;;      1          5
        ;;    /   \       / \
        ;;   2     3     4   4
        ;;  / \   / \   / \
        ;; t1 t2 t3 t4 t5 t5
        (§ let #_"ArrayList<byte[]>" (§ name ßtree) (§ new #_"ArrayList<>"))
        (§ block
            ;; Start by adding all the hashes of the transactions as leaves of the tree.
            (§ for (§ var #_"Transaction" (§ name ßt)) :for transactions)
            (§ block
                (.. ßtree (add (.. ßt (getHash) (getBytes))))
            )

            ;; Offset in the list where the currently processed level starts.
            (§ let #_"int" (§ name ßlevelOffset) 0)
            (§ block
                ;; Step through each level, stopping when we reach the root (levelSize == 1).
                (§ for (§ var #_"int" (§ name ßlevelSize) (.. transactions (size))) :for (< 1 ßlevelSize) :for (§ recur (/ (+ ßlevelSize 1) 2)))
                (§ block
                    ;; For each pair of nodes on that level:
                    (§ for (§ var #_"int" (§ name ßleft) 0) :for (< ßleft ßlevelSize) :for (§ recur (+ ßleft 2)))
                    (§ block
                        ;; The right hand node can be the same as the left hand, in the case where we don't have enough transactions.
                        (§ let #_"int" (§ name ßright) (Math/min (+ ßleft 1), (- ßlevelSize 1)))
                        (§ block
                            (§ let #_"byte[]" (§ name ßleftBytes) (Utils/reverseBytes (.. ßtree (get (+ ßlevelOffset ßleft)))))
                            (§ block
                                (§ let #_"byte[]" (§ name ßrightBytes) (Utils/reverseBytes (.. ßtree (get (+ ßlevelOffset ßright)))))
                                (§ block
                                    (.. ßtree (add (Utils/reverseBytes (hashTwice ßleftBytes, 0, 32, ßrightBytes, 0, 32))))
                                )
                            )
                        )
                    )
                    ;; Move to the next level.
                    (§ ass (§ name ßlevelOffset) (+ ßlevelOffset ßlevelSize))
                )
                ßtree
            )
        )
    )

    ;;;
     ; Verify the transactions on a block.
     ;
     ; @param height Block height, if known, or -1 otherwise.
     ;               If provided, used to validate the coinbase input script of v2 and above blocks.
     ; @throws VerificationException if there was an error verifying the block.
     ;;
    #_private
    (§ method- #_"void" (§ fn checkTransactions) [#_"int" (§ name ßheight), #_"EnumSet<VerifyFlag>" (§ name ßflags)])
        (§ throws #_"VerificationException")
    (§ block
        ;; The first transaction in a block must always be a coinbase transaction.
        (§ if (not (.. transactions (get 0) (isCoinBase))))
        (§ block
            (§ throw (§ new #_"VerificationException" "First tx is not coinbase"))
        )

        (§ if (and (.. ßflags (contains Block/VerifyFlag/HEIGHT_IN_COINBASE)) (<= BLOCK_HEIGHT_GENESIS ßheight)))
        (§ block
            (.. transactions (get 0) (checkCoinBaseHeight ßheight))
        )

        ;; The rest must not be.
        (§ for (§ var #_"int" (§ name ßi) 1) :for (< ßi (.. transactions (size))) :for (§ recur (+ ßi 1)))
        (§ block
            (§ if (.. transactions (get ßi) (isCoinBase)))
            (§ block
                (§ throw (§ new #_"VerificationException" (str "TX " ßi " is coinbase when it should not be.")))
            )
        )
        nil
    )

    ;;;
     ; Checks the block data to ensure it follows the rules laid out in the network parameters.  Specifically,
     ; throws an exception if the proof of work is invalid, or if the timestamp is too far from what it should be.
     ; This is <b>not</b> everything that is required for a block to be valid, only what is checkable independent
     ; of the chain and without a transaction index.
     ;
     ; @throws VerificationException
     ;;
    #_public
    (§ method #_"void" (§ fn verifyHeader) [])
        (§ throws #_"VerificationException")
    (§ block
        ;; Prove that this block is OK.  It might seem that we can just ignore most of these checks given that the
        ;; network is also verifying the blocks, but we cannot as it'd open us to a variety of obscure attacks.
        ;;
        ;; Firstly we need to ensure this block does in fact represent real work done.  If the difficulty is high
        ;; enough, it's probably been done by the network.
        (checkProofOfWork true)
        (checkTimestamp)
        nil
    )

    ;;;
     ; Checks the block contents.
     ;
     ; @param height Block height, if known, or -1 otherwise.
     ;               If valid, used to validate the coinbase input script of v2 and above blocks.
     ; @param flags Flags to indicate which tests should be applied (i.e. whether to test for height in the coinbase transaction).
     ; @throws VerificationException if there was an error verifying the block.
     ;;
    #_public
    (§ method #_"void" (§ fn verifyTransactions) [#_"int" (§ name ßheight), #_"EnumSet<VerifyFlag>" (§ name ßflags)])
        (§ throws #_"VerificationException")
    (§ block
        ;; Now we need to check that the body of the block actually matches the headers.  The network won't generate
        ;; an invalid block, but if we didn't validate this then an untrusted man-in-the-middle could obtain the next
        ;; valid block from the network and simply replace the transactions in it with their own fictional
        ;; transactions that reference spent or non-existant inputs.
        (§ if (.. transactions (isEmpty)))
        (§ block
            (§ throw (§ new #_"VerificationException" "Block had no transactions"))
        )
        (§ if (< MAX_BLOCK_SIZE (.. this (getOptimalEncodingMessageSize))))
        (§ block
            (§ throw (§ new #_"VerificationException" "Block larger than MAX_BLOCK_SIZE"))
        )
        (checkTransactions ßheight, ßflags)
        (checkMerkleRoot)
        (checkSigOps)
        (§ for (§ var #_"Transaction" (§ name ßtransaction)) :for transactions)
        (§ block
            (.. ßtransaction (verify))
        )
        nil
    )

    ;;;
     ; Verifies both the header and that the transactions hash to the merkle root.
     ;
     ; @param height Block height, if known, or -1 otherwise.
     ; @param flags Flags to indicate which tests should be applied (i.e. whether to test for height in the coinbase transaction).
     ; @throws VerificationException if there was an error verifying the block.
     ;;
    #_public
    (§ method #_"void" (§ fn verify) [#_"int" (§ name ßheight), #_"EnumSet<VerifyFlag>" (§ name ßflags)])
        (§ throws #_"VerificationException")
    (§ block
        (verifyHeader)
        (verifyTransactions ßheight, ßflags)
        nil
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (.. (getHash) (equals (.. (§ cast #_"Block" ßo) (getHash))))
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (.. (getHash) (hashCode))
    )

    ;;;
     ; Returns the merkle root in big endian form, calculating it from transactions if necessary.
     ;;
    #_public
    (§ method #_"Sha256Hash" (§ fn getMerkleRoot) [])
    (§ block
        (§ if (nil? merkleRoot))
        (§ block
            ;; TODO: Check if this is really necessary.
            (unCacheHeader)
            (§ ass (§ name merkleRoot) (calculateMerkleRoot))
        )
        merkleRoot
    )

    ;;; Exists only for unit testing. ;;
    (§ method #_"void" (§ fn setMerkleRoot) [#_"Sha256Hash" (§ name ßvalue)])
    (§ block
        (unCacheHeader)
        (§ ass (§ name merkleRoot) ßvalue)
        (§ ass (§ name hash) nil)
        nil
    )

    ;;; Adds a transaction to this block.  The nonce and merkle root are invalid after this. ;;
    #_public
    (§ method #_"void" (§ fn addTransaction) [#_"Transaction" (§ name ßt)])
    (§ block
        (addTransaction ßt, true)
        nil
    )

    ;;; Adds a transaction to this block, with or without checking the sanity of doing so. ;;
    (§ method #_"void" (§ fn addTransaction) [#_"Transaction" (§ name ßt), #_"boolean" (§ name ßrunSanityChecks)])
    (§ block
        (unCacheTransactions)
        (§ if (nil? transactions))
        (§ block
            (§ ass (§ name transactions) (§ new #_"ArrayList<>"))
        )
        (.. ßt (setParent this))
        (§ if (and ßrunSanityChecks (== (.. transactions (size)) 0) (not (.. ßt (isCoinBase)))))
        (§ block
            (§ throw (§ new #_"RuntimeException" (str "Attempted to add a non-coinbase transaction as the first transaction: " ßt)))
        )
        (§ if (and ßrunSanityChecks (< 0 (.. transactions (size))) (.. ßt (isCoinBase))))
        (§ block
            (§ throw (§ new #_"RuntimeException" (str "Attempted to add a coinbase transaction when there already is one: " ßt)))
        )
        (.. transactions (add ßt))
        (adjustLength (.. transactions (size)), (.. ßt length))
        ;; Force a recalculation next time the values are needed.
        (§ ass (§ name merkleRoot) nil)
        (§ ass (§ name hash) nil)
        nil
    )

    ;;;
     ; Returns the version of the block data structure as defined by the Bitcoin protocol.
     ;;
    #_public
    (§ method #_"long" (§ fn getVersion) [])
    (§ block
        version
    )

    ;;;
     ; Returns the hash of the previous block in the chain, as defined by the block header.
     ;;
    #_public
    (§ method #_"Sha256Hash" (§ fn getPrevBlockHash) [])
    (§ block
        prevBlockHash
    )

    (§ method #_"void" (§ fn setPrevBlockHash) [#_"Sha256Hash" (§ name ßprevBlockHash)])
    (§ block
        (unCacheHeader)
        (§ ass (§ name (.. this ßprevBlockHash)) ßprevBlockHash)
        (§ ass (§ name (.. this hash)) nil)
        nil
    )

    ;;;
     ; Returns the time at which the block was solved and broadcast, according to the clock of the solving node.
     ; This is measured in seconds since the UNIX epoch (midnight Jan 1st 1970).
     ;;
    #_public
    (§ method #_"long" (§ fn getTimeSeconds) [])
    (§ block
        time
    )

    ;;;
     ; Returns the time at which the block was solved and broadcast, according to the clock of the solving node.
     ;;
    #_public
    (§ method #_"Date" (§ fn getTime) [])
    (§ block
        (§ new #_"Date" (* (getTimeSeconds) 1000))
    )

    #_public
    (§ method #_"void" (§ fn setTime) [#_"long" (§ name ßtime)])
    (§ block
        (unCacheHeader)
        (§ ass (§ name (.. this ßtime)) ßtime)
        (§ ass (§ name (.. this hash)) nil)
        nil
    )

    ;;;
     ; Returns the difficulty of the proof of work that this block should meet encoded <b>in compact form</b>.
     ; The {@link BlockChain} verifies that this is not too easy by looking at the length of the chain when the block is added.
     ; To find the actual value the hash should be compared against, use {@link org.bitcoinj.core.Block#getDifficultyTargetAsInteger()}.
     ; Note that this is <b>not</b> the same as the difficulty value reported by the Bitcoin "getdifficulty" RPC that you may see on various block explorers.
     ; That number is the result of applying a formula to the underlying difficulty to normalize the minimum to 1.
     ; Calculating the difficulty that way is currently unsupported.
     ;;
    #_public
    (§ method #_"long" (§ fn getDifficultyTarget) [])
    (§ block
        difficultyTarget
    )

    ;;; Sets the difficulty target in compact form. ;;
    #_public
    (§ method #_"void" (§ fn setDifficultyTarget) [#_"long" (§ name ßcompactForm)])
    (§ block
        (unCacheHeader)
        (§ ass (§ name (.. this difficultyTarget)) ßcompactForm)
        (§ ass (§ name (.. this hash)) nil)
        nil
    )

    ;;;
     ; Returns the nonce, an arbitrary value that exists only to make the hash of the block header fall below the difficulty target.
     ;;
    #_public
    (§ method #_"long" (§ fn getNonce) [])
    (§ block
        nonce
    )

    ;;;
     ; Sets the nonce and clears any cached data.
     ;;
    #_public
    (§ method #_"void" (§ fn setNonce) [#_"long" (§ name ßnonce)])
    (§ block
        (unCacheHeader)
        (§ ass (§ name (.. this ßnonce)) ßnonce)
        (§ ass (§ name (.. this hash)) nil)
        nil
    )

    ;;; Returns an immutable list of transactions held in this block, or null if this object represents just a header. ;;
    #_nilable
    #_public
    (§ method #_"List<Transaction>" (§ fn getTransactions) [])
    (§ block
        (when (some? transactions) (ImmutableList/copyOf transactions))
    )

    ;; Used to make transactions unique.
    #_private
    #_static
    (§ def- #_"int" (§ name txCounter))

    ;;; Adds a coinbase transaction to the block.  This exists for unit tests.
     ;
     ; @param height Block height, if known, or -1 otherwise.
     ;;
    #_testing
    (§ method #_"void" (§ fn addCoinbaseTransaction) [#_"byte[]" (§ name ßpubKeyTo), #_"Coin" (§ name ßvalue), #_"int" (§ name ßheight)])
    (§ block
        (unCacheTransactions)
        (§ ass (§ name transactions) (§ new #_"ArrayList<>"))
        (§ let #_"Transaction" (§ name ßcoinbase) (§ new #_"Transaction" params))
        (§ block
            (§ let #_"ScriptBuilder" (§ name ßinputBuilder) (§ new #_"ScriptBuilder"))
            (§ block

                (§ if (<= Block/BLOCK_HEIGHT_GENESIS ßheight))
                (§ block
                    (.. ßinputBuilder (number ßheight))
                )
                (.. ßinputBuilder (data (§ new #_"byte[]" (§ coll (§ cast #_"byte" txCounter), (§ cast #_"byte" (>> txCounter 8)) ))))
                (§ ass (§ name txCounter) (+ txCounter 1))

                ;; A real coinbase transaction has some stuff in the scriptSig like the extraNonce and difficulty.
                ;; The transactions are distinguished by every TX output going to a different key.
                ;;
                ;; Here we will do things a bit differently so a new address isn't needed every time.
                ;; We'll put a simple counter in the scriptSig, so every transaction has a different hash.
                (.. ßcoinbase (addInput (§ new #_"TransactionInput" params, ßcoinbase, (.. ßinputBuilder (build) (getProgram)))))
                (.. ßcoinbase (addOutput (§ new #_"TransactionOutput" params, ßcoinbase, ßvalue, (.. (ScriptBuilder/createOutputScript (ECKey/fromPublicOnly ßpubKeyTo)) (getProgram)))))
                (.. transactions (add ßcoinbase))
                (.. ßcoinbase (setParent this))
                (§ ass (§ name (.. ßcoinbase length)) (.. ßcoinbase (unsafeBitcoinSerialize) length))
                (adjustLength (.. transactions (size)), (.. ßcoinbase length))
                nil
            )
        )
    )

    #_static
    (§ def #_"byte[]" (§ name EMPTY_BYTES) (§ new #_"byte[]" (§ count 32)))

    ;; It's pretty weak to have this around at runtime: fix later.
    #_private
    #_static
    (§ def- #_"byte[]" (§ name pubkeyForTesting) (.. (§ new #_"ECKey") (getPubKey)))

    ;;;
     ; Returns a solved block that builds on top of this one.  This exists for unit tests.
     ;;
    #_testing
    #_public
    (§ method #_"Block" (§ fn createNextBlock) [#_"Address" (§ name ßto), #_"long" (§ name ßversion), #_"long" (§ name ßtime), #_"int" (§ name ßblockHeight)])
    (§ block
        (createNextBlock ßto, ßversion, nil, ßtime, pubkeyForTesting, FIFTY_COINS, ßblockHeight)
    )

    ;;;
     ; Returns a solved block that builds on top of this one.  This exists for unit tests.
     ; In this variant you can specify a public key (pubkey) for use in generating coinbase blocks.
     ;
     ; @param height Block height, if known, or -1 otherwise.
     ;;
    (§ method #_"Block" (§ fn createNextBlock) [#_nilable #_"Address" (§ name ßto), #_"long" (§ name ßversion), #_nilable #_"TransactionOutPoint" (§ name ßprevOut), #_"long" (§ name ßtime), #_"byte[]" (§ name ßpubKey), #_"Coin" (§ name ßcoinbaseValue), #_"int" (§ name ßheight)])
    (§ block
        (§ let #_"Block" (§ name ßb) (§ new #_"Block" params, ßversion))
        (§ block
            (.. ßb (setDifficultyTarget difficultyTarget))
            (.. ßb (addCoinbaseTransaction ßpubKey, ßcoinbaseValue, ßheight))

            (§ if (some? ßto))
            (§ block
                ;; Add a transaction paying 50 coins to the "to" address.
                (§ let #_"Transaction" (§ name ßt) (§ new #_"Transaction" params))
                (§ block
                    (.. ßt (addOutput (§ new #_"TransactionOutput" params, ßt, FIFTY_COINS, ßto)))
                    ;; The input does not really need to be a valid signature, as long as it has the right general form.
                    (§ let #_"TransactionInput" (§ name ßinput))
                    (§ block
                        (§ if (nil? ßprevOut))
                        (§ block
                            (§ ass (§ name ßinput) (§ new #_"TransactionInput" params, ßt, (Script/createInputScript EMPTY_BYTES, EMPTY_BYTES)))
                            ;; Importantly, the outpoint hash cannot be zero as that's how we detect a coinbase transaction in isolation,
                            ;; but it must be unique to avoid 'different' transactions looking the same.
                            (§ let #_"byte[]" (§ name ßcounter) (§ new #_"byte[]" (§ count 32)))
                            (§ block
                                (§ ass (§ name (§ ai ßcounter 0)) (§ cast #_"byte" txCounter))
                                (§ ass (§ name (§ ai ßcounter 1)) (§ cast #_"byte" (>> txCounter 8)))
                                (§ ass (§ name txCounter) (+ txCounter 1))
                                (.. ßinput (getOutpoint) (setHash (Sha256Hash/wrap ßcounter)))
                            )
                        )
                        (§ else )
                        (§ block
                            (§ ass (§ name ßinput) (§ new #_"TransactionInput" params, ßt, (Script/createInputScript EMPTY_BYTES, EMPTY_BYTES), ßprevOut))
                        )
                        (.. ßt (addInput ßinput))
                        (.. ßb (addTransaction ßt))
                    )
                )
            )

            (.. ßb (setPrevBlockHash (getHash)))
            ;; Don't let timestamp go backwards.
            (§ if (<= ßtime (getTimeSeconds)))
            (§ block
                (.. ßb (setTime (+ (getTimeSeconds) 1)))
            )
            (§ else )
            (§ block
                (.. ßb (setTime ßtime))
            )
            (.. ßb (solve))
            (§ try )
            (§ block
                (.. ßb (verifyHeader))
            )
            (§ catch #_"VerificationException" (§ name ße))
            (§ block
                (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
            )
            (§ if (!= (.. ßb (getVersion)) ßversion))
            (§ block
                (§ throw (§ new #_"RuntimeException"))
            )
            ßb
        )
    )

    #_testing
    #_public
    (§ method #_"Block" (§ fn createNextBlock) [#_nilable #_"Address" (§ name ßto), #_"TransactionOutPoint" (§ name ßprevOut)])
    (§ block
        (createNextBlock ßto, BLOCK_VERSION_GENESIS, ßprevOut, (+ (getTimeSeconds) 5), pubkeyForTesting, FIFTY_COINS, BLOCK_HEIGHT_UNKNOWN)
    )

    #_testing
    #_public
    (§ method #_"Block" (§ fn createNextBlock) [#_nilable #_"Address" (§ name ßto), #_"Coin" (§ name ßvalue)])
    (§ block
        (createNextBlock ßto, BLOCK_VERSION_GENESIS, nil, (+ (getTimeSeconds) 5), pubkeyForTesting, ßvalue, BLOCK_HEIGHT_UNKNOWN)
    )

    #_testing
    #_public
    (§ method #_"Block" (§ fn createNextBlock) [#_nilable #_"Address" (§ name ßto)])
    (§ block
        (createNextBlock ßto, FIFTY_COINS)
    )

    #_testing
    #_public
    (§ method #_"Block" (§ fn createNextBlockWithCoinbase) [#_"long" (§ name ßversion), #_"byte[]" (§ name ßpubKey), #_"Coin" (§ name ßcoinbaseValue), #_"int" (§ name ßheight)])
    (§ block
        (createNextBlock nil, ßversion, (§ cast #_"TransactionOutPoint" nil), (Utils/currentTimeSeconds), ßpubKey, ßcoinbaseValue, ßheight)
    )

    ;;;
     ; Create a block sending 50BTC as a coinbase transaction to the public key specified.
     ; This method is intended for test use only.
     ;;
    #_testing
    (§ method #_"Block" (§ fn createNextBlockWithCoinbase) [#_"long" (§ name ßversion), #_"byte[]" (§ name ßpubKey), #_"int" (§ name ßheight)])
    (§ block
        (createNextBlock nil, ßversion, (§ cast #_"TransactionOutPoint" nil), (Utils/currentTimeSeconds), ßpubKey, FIFTY_COINS, ßheight)
    )

    #_testing
    (§ method #_"boolean" (§ fn isHeaderBytesValid) [])
    (§ block
        headerBytesValid
    )

    #_testing
    (§ method #_"boolean" (§ fn isTransactionBytesValid) [])
    (§ block
        transactionBytesValid
    )

    ;;;
     ; Return whether this block contains any transactions.
     ;
     ; @return true if the block contains transactions, false otherwise (is purely a header).
     ;;
    #_public
    (§ method #_"boolean" (§ fn hasTransactions) [])
    (§ block
        (not (.. this transactions (isEmpty)))
    )

    ;;;
     ; Returns whether this block conforms to
     ; <a href="https://github.com/bitcoin/bips/blob/master/bip-0034.mediawiki">BIP34: Height in Coinbase</a>.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isBIP34) [])
    (§ block
        (<= BLOCK_VERSION_BIP34 version)
    )

    ;;;
     ; Returns whether this block conforms to
     ; <a href="https://github.com/bitcoin/bips/blob/master/bip-0066.mediawiki">BIP66: Strict DER signatures</a>.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isBIP66) [])
    (§ block
        (<= BLOCK_VERSION_BIP66 version)
    )

    ;;;
     ; Returns whether this block conforms to
     ; <a href="https://github.com/bitcoin/bips/blob/master/bip-0065.mediawiki">BIP65: OP_CHECKLOCKTIMEVERIFY</a>.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isBIP65) [])
    (§ block
        (<= BLOCK_VERSION_BIP65 version)
    )
)

#_(ns org.bitcoinj.core #_"BlockChain"
    (:import [java.util ArrayList List])
    (:import [com.google.common.base Preconditions])
   (:require [org.bitcoinj.store BlockStore BlockStoreException]
             [org.bitcoinj.wallet Wallet]))

;; TODO: Rename this class to SPVBlockChain at some point.

;;;
 ; A BlockChain implements the <i>simplified payment verification</i> mode of the Bitcoin protocol.  It is the right
 ; choice to use for programs that have limited resources as it won't verify transactions signatures or attempt to store
 ; all of the block chain.  Really, this class should be called SPVBlockChain but for backwards compatibility it is not.
 ;;
#_public
(§ class #_"BlockChain" (§ extends #_"AbstractBlockChain")
    ;;; Keeps a map of block hashes to StoredBlocks. ;;
    #_protected
    (§ field #_"BlockStore" (§ name blockStore))

    ;;;
     ; <p>Constructs a BlockChain connected to the given wallet and store.  To obtain a {@link Wallet} you can construct
     ; one from scratch, or you can deserialize a saved wallet from disk using {@link Wallet#loadFromFile(java.io.File)}.</p>
     ;
     ; <p>For the store, you should use {@link org.bitcoinj.store.SPVBlockStore} or you could also try a
     ; {@link org.bitcoinj.store.MemoryBlockStore} if you want to hold all headers in RAM and don't care about
     ; disk serialization (this is rare).</p>
     ;;
    #_public
    (§ constructor #_"BlockChain" [#_"Context" (§ name ßcontext), #_"Wallet" (§ name ßwallet), #_"BlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this ßcontext, (§ new #_"ArrayList<Wallet>"), ßblockStore)
        (addWallet ßwallet)
        this
    )

    ;;; See {@link #BlockChain(Context, Wallet, BlockStore)}}. ;;
    #_public
    (§ constructor #_"BlockChain" [#_"NetworkParameters" (§ name ßparams), #_"Wallet" (§ name ßwallet), #_"BlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this (Context/getOrCreate ßparams), ßwallet, ßblockStore)
        this
    )

    ;;;
     ; Constructs a BlockChain that has no wallet at all.  This is helpful when you don't actually care about sending
     ; and receiving coins but rather, just want to explore the network data structures.
     ;;
    #_public
    (§ constructor #_"BlockChain" [#_"Context" (§ name ßcontext), #_"BlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this ßcontext, (§ new #_"ArrayList<Wallet>"), ßblockStore)
        this
    )

    ;;; See {@link #BlockChain(Context, BlockStore)}. ;;
    #_public
    (§ constructor #_"BlockChain" [#_"NetworkParameters" (§ name ßparams), #_"BlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this ßparams, (§ new #_"ArrayList<Wallet>"), ßblockStore)
        this
    )

    ;;;
     ; Constructs a BlockChain connected to the given list of listeners and a store.
     ;;
    #_public
    (§ constructor #_"BlockChain" [#_"Context" (§ name ßparams), #_"List<? extends Wallet>" (§ name ßwallets), #_"BlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ super ßparams, ßwallets, ßblockStore)
        (§ ass (§ name (.. this ßblockStore)) ßblockStore)
        this
    )

    ;;; See {@link #BlockChain(Context, List, BlockStore)}. ;;
    #_public
    (§ constructor #_"BlockChain" [#_"NetworkParameters" (§ name ßparams), #_"List<? extends Wallet>" (§ name ßwallets), #_"BlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this (Context/getOrCreate ßparams), ßwallets, ßblockStore)
        this
    )

    #_override
    #_protected
    (§ method #_"StoredBlock" (§ fn addToBlockStore) [#_"StoredBlock" (§ name ßstoredPrev), #_"Block" (§ name ßblockHeader), #_"TransactionOutputChanges" (§ name ßtxOutChanges)])
        (§ throws #_"BlockStoreException", #_"VerificationException")
    (§ block
        (§ let #_"StoredBlock" (§ name ßnewBlock) (.. ßstoredPrev (build ßblockHeader)))
        (§ block
            (.. blockStore (put ßnewBlock))
            ßnewBlock
        )
    )

    #_override
    #_protected
    (§ method #_"StoredBlock" (§ fn addToBlockStore) [#_"StoredBlock" (§ name ßstoredPrev), #_"Block" (§ name ßblockHeader)])
        (§ throws #_"BlockStoreException", #_"VerificationException")
    (§ block
        (§ let #_"StoredBlock" (§ name ßnewBlock) (.. ßstoredPrev (build ßblockHeader)))
        (§ block
            (.. blockStore (put ßnewBlock))
            ßnewBlock
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn rollbackBlockStore) [#_"int" (§ name ßheight)])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"int" (§ name ßcurrentHeight) (getBestChainHeight))
            (§ block
                (Preconditions/checkArgument (<= 0 ßheight ßcurrentHeight), "Bad ßheight: %s", ßheight)
                (§ if (== ßheight ßcurrentHeight))
                (§ block
                    (§ return nil) ;; nothing to do
                )

                ;; Look for the block we want to be the new chain head.
                (§ let #_"StoredBlock" (§ name ßnewChainHead) (.. blockStore (getChainHead)))
                (§ block
                    (§ while (< ßheight (.. ßnewChainHead (getHeight))))
                    (§ block
                        (§ ass (§ name ßnewChainHead) (.. ßnewChainHead (getPrev blockStore)))
                        (§ if (nil? ßnewChainHead))
                        (§ block
                            (§ throw (§ new #_"BlockStoreException" "Unreachable height"))
                        )
                    )

                    ;; Modify store directly.
                    (.. blockStore (put ßnewChainHead))
                    (.. this (setChainHead ßnewChainHead))
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    #_override
    #_protected
    (§ method #_"boolean" (§ fn shouldVerifyTransactions) [])
    (§ block
        false
    )

    #_override
    #_protected
    (§ method #_"TransactionOutputChanges" (§ fn connectTransactions) [#_"int" (§ name ßheight), #_"Block" (§ name ßblock)])
    (§ block
        ;; Don't have to do anything as this is only called if shouldVerifyTransactions().
        (§ throw (§ new #_"UnsupportedOperationException"))
    )

    #_override
    #_protected
    (§ method #_"TransactionOutputChanges" (§ fn connectTransactions) [#_"StoredBlock" (§ name ßnewBlock)])
    (§ block
        ;; Don't have to do anything as this is only called if shouldVerifyTransactions().
        (§ throw (§ new #_"UnsupportedOperationException"))
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn disconnectTransactions) [#_"StoredBlock" (§ name ßblock)])
    (§ block
        ;; Don't have to do anything as this is only called if shouldVerifyTransactions().
        (§ throw (§ new #_"UnsupportedOperationException"))
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn doSetChainHead) [#_"StoredBlock" (§ name ßchainHead)])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. blockStore (setChainHead ßchainHead))
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn notSettingChainHead) [])
        (§ throws #_"BlockStoreException")
    (§ block
        ;; We don't use DB transactions here, so we don't need to do anything.
        nil
    )

    #_override
    #_protected
    (§ method #_"StoredBlock" (§ fn getStoredBlockInCurrentScope) [#_"Sha256Hash" (§ name ßhash)])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. blockStore (get ßhash))
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn add) [#_"FilteredBlock" (§ name ßblock)])
        (§ throws #_"VerificationException", #_"PrunedException")
    (§ block
        (§ let #_"boolean" (§ name ßsuccess) (.. super (add ßblock)))
        (§ block
            (§ if ßsuccess)
            (§ block
                (trackFilteredTransactions (.. ßblock (getTransactionCount)))
            )
            ßsuccess
        )
    )
)

#_(ns org.bitcoinj.core #_"BloomFilter"
    (:import [java.io IOException OutputStream]
             #_static [java.lang.Math *]
             [java.util ArrayList Arrays List])
    (:import [com.google.common.base Objects Preconditions]
             [com.google.common.collect Lists])
   (:require [org.bitcoinj.script Script ScriptChunk]))

;;;
 ; <p>A Bloom filter is a probabilistic data structure which can be sent to another client so that it can avoid
 ; sending us transactions that aren't relevant to our set of keys.  This allows for significantly more efficient
 ; use of available network bandwidth and CPU time.</p>
 ;
 ; <p>Because a Bloom filter is probabilistic, it has a configurable false positive rate.  So the filter will sometimes
 ; match transactions that weren't inserted into it, but it will never fail to match transactions that were.  This is
 ; a useful privacy feature - if you have spare bandwidth the false positive rate can be increased so the remote peer
 ; gets a noisy picture of what transactions are relevant to your wallet.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"BloomFilter" (§ extends #_"Message")
    ;;;
     ; The BLOOM_UPDATE_* constants control when the bloom filter is auto-updated by the peer using
     ; it as a filter, either never, for all outputs or only for pay-2-pubkey outputs (default).
     ;;
    #_public
    (§ enum #_"BloomUpdate"
        (§ item UPDATE_NONE) ;; 0
        (§ item UPDATE_ALL) ;; 1
        ;;; Only adds outpoints to the filter if the output is a pay-to-pubkey/pay-to-multisig script. ;;
        (§ item UPDATE_P2PUBKEY_ONLY) ;; 2
    )

    #_private
    (§ field- #_"byte[]" (§ name data))
    #_private
    (§ field- #_"long" (§ name hashFuncs))
    #_private
    (§ field- #_"long" (§ name nTweak))
    #_private
    (§ field- #_"byte" (§ name nFlags))

    ;; Same value as of Bitcoin Core.
    ;; A filter of 20,000 items and a false positive rate of 0.1% or one of 10,000 items and 0.0001% is just under 36,000 bytes.
    #_private
    #_static
    (§ def- #_"long" (§ name MAX_FILTER_SIZE) 36000)
    ;; There is little reason to ever have more hash functions than 50 given a limit of 36,000 bytes.
    #_private
    #_static
    (§ def- #_"int" (§ name MAX_HASH_FUNCS) 50)

    ;;;
     ; Construct a BloomFilter by deserializing payloadBytes.
     ;;
    #_public
    (§ constructor #_"BloomFilter" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayloadBytes)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayloadBytes, 0)
        this
    )

    ;;;
     ; Constructs a filter with the given parameters which is updated on pay2pubkey outputs only.
     ;;
    #_public
    (§ constructor #_"BloomFilter" [#_"int" (§ name ßelements), #_"double" (§ name ßfalsePositiveRate), #_"long" (§ name ßrandomNonce)])
    (§ block
        (§ this ßelements, ßfalsePositiveRate, ßrandomNonce, BloomUpdate/UPDATE_P2PUBKEY_ONLY)
        this
    )

    ;;;
     ; <p>Constructs a new Bloom Filter which will provide approximately the given false positive rate when the given
     ; number of elements have been inserted.  If the filter would otherwise be larger than the maximum allowed size,
     ; it will be automatically downsized to the maximum size.</p>
     ;
     ; <p>To check the theoretical false positive rate of a given filter, use {@link BloomFilter#getFalsePositiveRate(int)}.</p>
     ;
     ; <p>The anonymity of which coins are yours to any peer which you send a BloomFilter to is controlled by the
     ; false positive rate.  For reference, as of block 187,000, the total number of addresses used in the chain was
     ; roughly 4.5 million.  Thus, if you use a false positive rate of 0.001 (0.1%), there will be, on average, 4,500
     ; distinct public keys/addresses which will be thought to be yours by nodes which have your bloom filter, but
     ; which are not actually yours.  Keep in mind that a remote node can do a pretty good job estimating the order of
     ; magnitude of the false positive rate of a given filter you provide it when considering the anonymity of a given
     ; filter.</p>
     ;
     ; <p>In order for filtered block download to function efficiently, the number of matched transactions in any given
     ; block should be less than (with some headroom) the maximum size of the MemoryPool used by the Peer
     ; doing the downloading (default is {@link TxConfidenceTable#MAX_SIZE}).  See the comment in processBlock(FilteredBlock)
     ; for more information on this restriction.</p>
     ;
     ; <p>randomNonce is a tweak for the hash function used to prevent some theoretical DoS attacks.
     ; It should be a random value, however secureness of the random value is of no great consequence.</p>
     ;
     ; <p>updateFlag is used to control filter behaviour on the server (remote node) side when it encounters a hit.
     ; See {@link org.bitcoinj.core.BloomFilter.BloomUpdate} for a brief description of each mode.  The purpose
     ; of this flag is to reduce network round-tripping and avoid over-dirtying the filter for the most common
     ; wallet configurations.</p>
     ;;
    #_public
    (§ constructor #_"BloomFilter" [#_"int" (§ name ßelements), #_"double" (§ name ßfalsePositiveRate), #_"long" (§ name ßrandomNonce), #_"BloomUpdate" (§ name ßupdateFlag)])
    (§ block
        ;; The following formulas were stolen from Wikipedia's page on Bloom Filters (with the addition of min(..., MAX_...)).
        ;; Size required for a given number of elements and false-positive rate.
        (§ let #_"int" (§ name ßsize) (§ cast #_"int" (* (/ -1 (pow (log 2), 2)) ßelements (log ßfalsePositiveRate))))
        (§ block
            (§ ass (§ name ßsize) (max 1, (/ (min ßsize, (* (§ cast #_"int" MAX_FILTER_SIZE) 8)) 8)))
            (§ ass (§ name data) (§ new #_"byte[]" (§ count ßsize)))
            ;; Optimal number of hash functions for a given filter size and element count.
            (§ ass (§ name hashFuncs) (§ cast #_"int" (* (/ (* (.. data length) 8) (§ cast #_"double" ßelements)) (log 2))))
            (§ ass (§ name hashFuncs) (max 1, (min hashFuncs, MAX_HASH_FUNCS)))
            (§ ass (§ name (.. this nTweak)) ßrandomNonce)
            (§ ass (§ name (.. this nFlags)) (§ cast #_"byte" (& 0xff (.. ßupdateFlag (ordinal)))))
            this
        )
    )

    ;;;
     ; Returns the theoretical false positive rate of this filter if were to contain the given number of elements.
     ;;
    #_public
    (§ method #_"double" (§ fn getFalsePositiveRate) [#_"int" (§ name ßelements)])
    (§ block
        (pow (- 1 (pow E, (/ (* -1.0 hashFuncs ßelements) (* (.. data length) 8)))), hashFuncs)
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "Bloom Filter of size " (.. data length) " with " hashFuncs " hash functions.")
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ ass (§ name data) (readByteArray))
        (§ if (< MAX_FILTER_SIZE (.. data length)))
        (§ block
            (§ throw (§ new #_"ProtocolException" "Bloom filter out of size range."))
        )
        (§ ass (§ name hashFuncs) (readUint32))
        (§ if (< MAX_HASH_FUNCS hashFuncs))
        (§ block
            (§ throw (§ new #_"ProtocolException" "Bloom filter hash function count out of range"))
        )
        (§ ass (§ name nTweak) (readUint32))
        (§ ass (§ name nFlags) (§ ai (readBytes 1) 0))
        (§ ass (§ name length) (- cursor offset))
        nil
    )

    ;;;
     ; Serializes this message to the provided stream.  If you just want the raw bytes use bitcoinSerialize().
     ;;
    #_override
    #_protected
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        (.. ßstream (write (.. (§ new #_"VarInt" (.. data length)) (encode))))
        (.. ßstream (write data))
        (Utils/uint32ToByteStreamLE hashFuncs, ßstream)
        (Utils/uint32ToByteStreamLE nTweak, ßstream)
        (.. ßstream (write nFlags))
        nil
    )

    #_private
    #_static
    (§ defn- #_"int" (§ fn rotateLeft32) [#_"int" (§ name ßx), #_"int" (§ name ßr)])
    (§ block
        (| (<< ßx ßr) (>>> ßx (- 32 ßr)))
    )

    ;;;
     ; Applies the MurmurHash3 (x86_32) algorithm to the given data.
     ; See <a href="https://github.com/aappleby/smhasher/blob/master/src/MurmurHash3.cpp">this C++ code</a> for the original.
     ;;
    #_public
    #_static
    (§ defn #_"int" (§ fn murmurHash3) [#_"byte[]" (§ name ßdata), #_"long" (§ name ßnTweak), #_"int" (§ name ßhashNum), #_"byte[]" (§ name ßobject)])
    (§ block
        (§ let #_"int" (§ name ßh1) (§ cast #_"int" (+ (* ßhashNum 0xfba4c795) ßnTweak)))
        (§ block
            (§ let #_"int" (§ name ßc1) 0xcc9e2d51)
            (§ block
                (§ let #_"int" (§ name ßc2) 0x1b873593)
                (§ block

                    (§ let #_"int" (§ name ßnumBlocks) (* (/ (.. ßobject length) 4) 4))
                    (§ block

                        ;; body
                        (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumBlocks) :for (§ recur (+ ßi 4)))
                        (§ block
                            (§ let #_"int" (§ name ßk1) (| (& 0xff (§ ai ßobject ßi)) (<< (& 0xff (§ ai ßobject (+ ßi 1))) 8) (<< (& 0xff (§ ai ßobject (+ ßi 2))) 16) (<< (& 0xff (§ ai ßobject (+ ßi 3))) 24)))
                            (§ block

                                (§ ass (§ name ßk1) (* ßk1 ßc1))
                                (§ ass (§ name ßk1) (rotateLeft32 ßk1, 15))
                                (§ ass (§ name ßk1) (* ßk1 ßc2))

                                (§ ass (§ name ßh1) (bit-xor ßh1 ßk1))
                                (§ ass (§ name ßh1) (rotateLeft32 ßh1, 13))
                                (§ ass (§ name ßh1) (+ (* ßh1 5) 0xe6546b64))
                            )
                        )

                        (§ let #_"int" (§ name ßk1) 0)
                        (§ block
                            (§ switch (& (.. ßobject length) 3))
                            (§ block
                                (§ case 3)
                                (§ block
                                    (§ ass (§ name ßk1) (bit-xor ßk1 (<< (& 0xff (§ ai ßobject (+ ßnumBlocks 2))) 16)))
                                    ;; Fall through.
                                )
                                (§ case 2)
                                (§ block
                                    (§ ass (§ name ßk1) (bit-xor ßk1 (<< (& 0xff (§ ai ßobject (+ ßnumBlocks 1))) 8)))
                                    ;; Fall through.
                                )
                                (§ case 1)
                                (§ block
                                    (§ ass (§ name ßk1) (bit-xor ßk1 (& 0xff (§ ai ßobject ßnumBlocks))))
                                    (§ ass (§ name ßk1) (* ßk1 ßc1))
                                    (§ ass (§ name ßk1) (rotateLeft32 ßk1, 15))
                                    (§ ass (§ name ßk1) (* ßk1 ßc2))
                                    (§ ass (§ name ßh1) (bit-xor ßh1 ßk1))
                                    ;; Fall through.
                                )
                                (§ default )
                                (§ block
                                    ;; Do nothing.
                                    (§ break )
                                )
                            )

                            ;; finalization
                            (§ ass (§ name ßh1) (bit-xor ßh1 (.. ßobject length)))
                            (§ ass (§ name ßh1) (bit-xor ßh1 (>>> ßh1 16)))
                            (§ ass (§ name ßh1) (* ßh1 0x85ebca6b))
                            (§ ass (§ name ßh1) (bit-xor ßh1 (>>> ßh1 13)))
                            (§ ass (§ name ßh1) (* ßh1 0xc2b2ae35))
                            (§ ass (§ name ßh1) (bit-xor ßh1 (>>> ßh1 16)))

                            (§ cast #_"int" (% (& ßh1 0xffffffff) (* (.. ßdata length) 8)))
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Returns true if the given object matches the filter either because it was inserted, or because we have a false-positive.
     ;;
    #_public
    #_synchronized
    (§ method #_"boolean" (§ fn contains) [#_"byte[]" (§ name ßobject)])
    (§ block
        (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi hashFuncs) :for (§ recur (+ ßi 1)))
        (§ block
            (§ if (not (Utils/checkBitLE data, (murmurHash3 data, nTweak, ßi, ßobject))))
            (§ block
                (§ return false)
            )
        )

        true
    )

    ;;; Insert the given arbitrary data into the filter. ;;
    #_public
    #_synchronized
    (§ method #_"void" (§ fn insert) [#_"byte[]" (§ name ßobject)])
    (§ block
        (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi hashFuncs) :for (§ recur (+ ßi 1)))
        (§ block
            (Utils/setBitLE data, (murmurHash3 data, nTweak, ßi, ßobject))
        )
        nil
    )

    ;;; Inserts the given key and equivalent hashed form (for the address). ;;
    #_public
    #_synchronized
    (§ method #_"void" (§ fn insert) [#_"ECKey" (§ name ßkey)])
    (§ block
        (insert (.. ßkey (getPubKey)))
        (insert (.. ßkey (getPubKeyHash)))
        nil
    )

    ;;;
     ; Sets this filter to match all objects.  A Bloom filter which matches everything may seem pointless, however,
     ; it is useful in order to reduce steady state bandwidth usage when you want full blocks.  Instead of receiving
     ; all transaction data twice, you will receive the vast majority of all transactions just once, at broadcast time.
     ; Solved blocks will then be send just as Merkle trees of tx hashes, meaning a constant 32 bytes of data for each
     ; transaction instead of 100-300 bytes as per usual.
     ;;
    #_public
    #_synchronized
    (§ method #_"void" (§ fn setMatchAll) [])
    (§ block
        (§ ass (§ name data) (§ new #_"byte[]" (§ coll (§ cast #_"byte" 0xff) )))
        nil
    )

    ;;;
     ; Copies filter into this.  Filter must have the same size, hash function count and nTweak or an
     ; IllegalArgumentException will be thrown.
     ;;
    #_public
    #_synchronized
    (§ method #_"void" (§ fn merge) [#_"BloomFilter" (§ name ßfilter)])
    (§ block
        (§ if (and (not (.. this (matchesAll))) (not (.. ßfilter (matchesAll)))))
        (§ block
            (Preconditions/checkArgument (and (== (.. ßfilter data length) (.. this data length)) (== (.. ßfilter hashFuncs) (.. this hashFuncs)) (== (.. ßfilter nTweak) (.. this nTweak))))

            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. data length)) :for (§ recur (+ ßi 1)))
            (§ block
                (§ ass (§ name this.data[ßi]) (| (§ ai (.. this data) ßi) (§ ai (.. ßfilter data) ßi)))
            )
        )
        (§ else )
        (§ block
            (§ ass (§ name (.. this data)) (§ new #_"byte[]" (§ coll (§ cast #_"byte" 0xff) )))
        )
        nil
    )

    ;;;
     ; Returns true if this filter will match anything.  See {@link org.bitcoinj.core.BloomFilter#setMatchAll()}
     ; for when this can be a useful thing to do.
     ;;
    #_public
    #_synchronized
    (§ method #_"boolean" (§ fn matchesAll) [])
    (§ block
        (§ for (§ var #_"byte" (§ name ßb)) :for data)
        (§ block
            (§ if (!= ßb (§ cast #_"byte" 0xff)))
            (§ block
                (§ return false)
            )
        )
        true
    )

    ;;;
     ; The update flag controls how application of the filter to a block modifies the filter.  See the enum javadocs
     ; for information on what occurs and when.
     ;;
    #_public
    #_synchronized
    (§ method #_"BloomUpdate" (§ fn getUpdateFlag) [])
    (§ block
        (§ if (== nFlags 0))
        (§ block
            (§ return BloomUpdate/UPDATE_NONE)
        )
        (§ if (== nFlags 1))
        (§ block
            (§ return BloomUpdate/UPDATE_ALL)
        )
        (§ if (== nFlags 2))
        (§ block
            (§ return BloomUpdate/UPDATE_P2PUBKEY_ONLY)
        )

        (§ throw (§ new #_"IllegalStateException" "Unknown flag combination"))
    )

    ;;;
     ; Creates a new FilteredBlock from the given Block, using this filter to select transactions.  Matches can cause the
     ; filter to be updated with the matched element, this ensures that when a filter is applied to a block, spends of
     ; matched transactions are also matched.  However it means this filter can be mutated by the operation.  The returned
     ; filtered block already has the matched transactions associated with it.
     ;;
    #_public
    #_synchronized
    (§ method #_"FilteredBlock" (§ fn applyAndUpdate) [#_"Block" (§ name ßblock)])
    (§ block
        (§ let #_"List<Transaction>" (§ name ßtxns) (.. ßblock (getTransactions)))
        (§ block
            (§ let #_"List<Sha256Hash>" (§ name ßtxHashes) (§ new #_"ArrayList<>" (.. ßtxns (size))))
            (§ block
                (§ let #_"List<Transaction>" (§ name ßmatched) (Lists/newArrayList))
                (§ block
                    (§ let #_"byte[]" (§ name ßbits) (§ new #_"byte[]" (§ count (§ cast #_"int" (Math/ceil (/ (.. ßtxns (size)) 8.0))))))
                    (§ block
                        (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi (.. ßtxns (size))) :for (§ recur (+ ßi 1)))
                        (§ block
                            (§ let #_"Transaction" (§ name ßtx) (.. ßtxns (get ßi)))
                            (§ block
                                (.. ßtxHashes (add (.. ßtx (getHash))))
                                (§ if (applyAndUpdate ßtx))
                                (§ block
                                    (Utils/setBitLE ßbits, ßi)
                                    (.. ßmatched (add ßtx))
                                )
                            )
                        )
                        (§ let #_"PartialMerkleTree" (§ name ßpmt) (PartialMerkleTree/buildFromLeaves (.. ßblock (getParams)), ßbits, ßtxHashes))
                        (§ block
                            (§ let #_"FilteredBlock" (§ name ßfilteredBlock) (§ new #_"FilteredBlock" (.. ßblock (getParams)), (.. ßblock (cloneAsHeader)), ßpmt))
                            (§ block
                                (§ for (§ var #_"Transaction" (§ name ßtransaction)) :for ßmatched)
                                (§ block
                                    (.. ßfilteredBlock (provideTransaction ßtransaction))
                                )
                                ßfilteredBlock
                            )
                        )
                    )
                )
            )
        )
    )

    #_public
    #_synchronized
    (§ method #_"boolean" (§ fn applyAndUpdate) [#_"Transaction" (§ name ßtx)])
    (§ block
        (§ if (contains (.. ßtx (getHash) (getBytes))))
        (§ block
            (§ return true)
        )
        (§ let #_"boolean" (§ name ßfound) false)
        (§ block
            (§ let #_"BloomUpdate" (§ name ßflag) (getUpdateFlag))
            (§ block
                (§ for (§ var #_"TransactionOutput" (§ name ßoutput)) :for (.. ßtx (getOutputs)))
                (§ block
                    (§ let #_"Script" (§ name ßscript) (.. ßoutput (getScriptPubKey)))
                    (§ block
                        (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for (.. ßscript (getChunks)))
                        (§ block
                            (§ if (and (.. ßchunk (isPushData)) (contains (.. ßchunk data))))
                            (§ block
                                (§ let #_"boolean" (§ name ßisSendingToPubKeys) (or (.. ßscript (isSentToRawPubKey)) (.. ßscript (isSentToMultiSig))))
                                (§ block
                                    (§ if (or (== ßflag BloomUpdate/UPDATE_ALL) (and (== ßflag BloomUpdate/UPDATE_P2PUBKEY_ONLY) ßisSendingToPubKeys)))
                                    (§ block
                                        (insert (.. ßoutput (getOutPointFor) (unsafeBitcoinSerialize)))
                                    )
                                    (§ ass (§ name ßfound) true)
                                )
                            )
                        )
                    )
                )
                (§ if ßfound)
                (§ block
                    (§ return true)
                )
                (§ for (§ var #_"TransactionInput" (§ name ßinput)) :for (.. ßtx (getInputs)))
                (§ block
                    (§ if (contains (.. ßinput (getOutpoint) (unsafeBitcoinSerialize))))
                    (§ block
                        (§ return true)
                    )

                    (§ for (§ var #_"ScriptChunk" (§ name ßchunk)) :for (.. ßinput (getScriptSig) (getChunks)))
                    (§ block
                        (§ if (and (.. ßchunk (isPushData)) (contains (.. ßchunk data))))
                        (§ block
                            (§ return true)
                        )
                    )
                )
                false
            )
        )
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"BloomFilter" (§ name ßother) (§ cast #_"BloomFilter" ßo))
        (§ block
            (and (== hashFuncs (.. ßother hashFuncs)) (== nTweak (.. ßother nTweak)) (Arrays/equals data, (.. ßother data)))
        )
    )

    #_override
    #_public
    #_synchronized
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode hashFuncs, nTweak, (Arrays/hashCode data))
    )
)

#_(ns org.bitcoinj.core #_"CheckpointManager"
    (:import [java.io BufferedInputStream BufferedReader DataInputStream IOException InputStream InputStreamReader]
             [java.nio ByteBuffer ByteOrder]
             [java.security DigestInputStream MessageDigest]
             [java.util Arrays Map TreeMap])
    (:import [com.google.common.base Charsets Preconditions]
             [com.google.common.hash HashCode Hasher Hashing]
             [com.google.common.io BaseEncoding]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.store BlockStore BlockStoreException FullPrunedBlockStore]))

;;;
 ; <p>Vends hard-coded {@link StoredBlock}s for blocks throughout the chain.  Checkpoints serve two purposes:</p>
 ; <ol>
 ;    <li>They act as a safety mechanism against huge re-orgs that could rewrite large chunks of history, thus
 ;    constraining the block chain to be a consensus mechanism only for recent parts of the timeline.</li>
 ;    <li>They allow synchronization to the head of the chain for new wallets/users much faster than syncing all
 ;    headers from the genesis block.</li>
 ; </ol>
 ;
 ; <p>Checkpoints are used by the SPV {@link BlockChain} to initialize fresh {@link org.bitcoinj.store.SPVBlockStore}s.
 ; They are not used by fully validating mode, which instead has a different concept of checkpoints that are used
 ; to hard-code the validity of blocks that violate BIP30 (duplicate coinbase transactions).
 ; Those "checkpoints" can be found in NetworkParameters.</p>
 ;
 ; <p>The file format consists of the string "CHECKPOINTS 1", followed by a uint32 containing the number of signatures
 ; to read.  The value may not be larger than 256 (so it could have been a byte but isn't for historical reasons).
 ; If the number of signatures is larger than zero, each 65 byte ECDSA secp256k1 signature then follows.  The signatures
 ; sign the hash of all bytes that follow the last signature.</p>
 ;
 ; <p>After the signatures come an int32 containing the number of checkpoints in the file.  Then each checkpoint follows
 ; one after the other.  A checkpoint is 12 bytes for the total work done field, 4 bytes for the height, 80 bytes
 ; for the block header and then 1 zero byte at the end (i.e. number of transactions in the block: always zero).</p>
 ;;
#_public
(§ class #_"CheckpointManager"
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"CheckpointManager")))

    #_private
    #_static
    (§ def- #_"String" (§ name BINARY_MAGIC) "CHECKPOINTS 1")
    #_private
    #_static
    (§ def- #_"String" (§ name TEXTUAL_MAGIC) "TXT CHECKPOINTS 1")
    #_private
    #_static
    (§ def- #_"int" (§ name MAX_SIGNATURES) 256)

    ;; Map of block header time to data.
    #_protected
    (§ field #_"TreeMap<Long, StoredBlock>" (§ name checkpoints) (§ new #_"TreeMap<>"))

    #_protected
    (§ field #_"NetworkParameters" (§ name params))
    #_protected
    (§ field #_"Sha256Hash" (§ name dataHash))

    #_public
    #_static
    (§ def #_"BaseEncoding" (§ name BASE64) (.. (BaseEncoding/base64) (omitPadding)))

    ;;; Loads the default checkpoints bundled with bitcoinj. ;;
    #_public
    (§ constructor #_"CheckpointManager" [#_"Context" (§ name ßcontext)])
        (§ throws #_"IOException")
    (§ block
        (§ this (.. ßcontext (getParams)), nil)
        this
    )

    ;;; Loads the checkpoints from the given stream. ;;
    #_public
    (§ constructor #_"CheckpointManager" [#_"NetworkParameters" (§ name ßparams), #_nilable #_"InputStream" (§ name ßinputStream)])
        (§ throws #_"IOException")
    (§ block
        (§ ass (§ name (.. this ßparams)) (Preconditions/checkNotNull ßparams))
        (§ if (nil? ßinputStream))
        (§ block
            (§ ass (§ name ßinputStream) (openStream ßparams))
        )
        (Preconditions/checkNotNull ßinputStream)
        (§ ass (§ name ßinputStream) (§ new #_"BufferedInputStream" ßinputStream))
        (.. ßinputStream (mark 1))
        (§ let #_"int" (§ name ßfirst) (.. ßinputStream (read)))
        (§ block
            (.. ßinputStream (reset))
            (§ if (== ßfirst (.. BINARY_MAGIC (charAt 0))))
            (§ block
                (§ ass (§ name dataHash) (readBinary ßinputStream))
            )
            (§ elseif (== ßfirst (.. TEXTUAL_MAGIC (charAt 0))))
            (§ block
                (§ ass (§ name dataHash) (readTextual ßinputStream))
            )
            (§ else )
            (§ block
                (§ throw (§ new #_"IOException" "Unsupported format."))
            )
            this
        )
    )

    ;;; Returns a checkpoints stream pointing to inside the bitcoinj JAR. ;;
    #_public
    #_static
    (§ defn #_"InputStream" (§ fn openStream) [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (.. (§ klass #_"CheckpointManager") (getResourceAsStream (str "/" (.. ßparams (getId)) ".checkpoints.txt")))
    )

    #_private
    (§ method- #_"Sha256Hash" (§ fn readBinary) [#_"InputStream" (§ name ßinputStream)])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"DataInputStream" (§ name ßdis) nil)
        (§ block
            (§ try )
            (§ block
                (§ let #_"MessageDigest" (§ name ßdigest) (Sha256Hash/newDigest))
                (§ block
                    (§ let #_"DigestInputStream" (§ name ßdigestInputStream) (§ new #_"DigestInputStream" ßinputStream, ßdigest))
                    (§ block
                        (§ ass (§ name ßdis) (§ new #_"DataInputStream" ßdigestInputStream))
                        (.. ßdigestInputStream (on false))
                        (§ let #_"byte[]" (§ name ßheader) (§ new #_"byte[]" (§ count (.. BINARY_MAGIC (length)))))
                        (§ block
                            (.. ßdis (readFully ßheader))
                            (§ if (not (Arrays/equals ßheader, (.. BINARY_MAGIC (getBytes "US-ASCII")))))
                            (§ block
                                (§ throw (§ new #_"IOException" "Header bytes did not match expected version"))
                            )
                            (§ let #_"int" (§ name ßnumSignatures) (checkPositionIndex (.. ßdis (readInt)), MAX_SIGNATURES, "Num signatures out of range"))
                            (§ block
                                (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumSignatures) :for (§ recur (+ ßi 1)))
                                (§ block
                                    (§ let #_"byte[]" (§ name ßsig) (§ new #_"byte[]" (§ count 65)))
                                    (§ block
                                        (.. ßdis (readFully ßsig))
                                        ;; TODO: Do something with the signature here.
                                    )
                                )
                                (.. ßdigestInputStream (on true))
                                (§ let #_"int" (§ name ßnumCheckpoints) (.. ßdis (readInt)))
                                (§ block
                                    (Preconditions/checkState (< 0 ßnumCheckpoints))
                                    (§ let #_"int" (§ name ßsize) StoredBlock/COMPACT_SERIALIZED_SIZE)
                                    (§ block
                                        (§ let #_"ByteBuffer" (§ name ßbuffer) (ByteBuffer/allocate ßsize))
                                        (§ block
                                            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumCheckpoints) :for (§ recur (+ ßi 1)))
                                            (§ block
                                                (§ if (< (.. ßdis (read (.. ßbuffer (array)), 0, ßsize)) ßsize))
                                                (§ block
                                                    (§ throw (§ new #_"IOException" "Incomplete read whilst loading checkpoints."))
                                                )
                                                (§ let #_"StoredBlock" (§ name ßblock) (StoredBlock/deserializeCompact params, ßbuffer))
                                                (§ block
                                                    (.. ßbuffer (position 0))
                                                    (.. checkpoints (put (.. ßblock (getHeader) (getTimeSeconds)), ßblock))
                                                )
                                            )
                                            (§ let #_"Sha256Hash" (§ name ßdataHash) (Sha256Hash/wrap (.. ßdigest (digest))))
                                            (§ block
                                                (.. log (info "Read {} checkpoints, hash is {}", (.. checkpoints (size)), ßdataHash))
                                                (§ return ßdataHash)
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
            (§ catch #_"ProtocolException" (§ name ße))
            (§ block
                (§ throw (§ new #_"IOException" ße))
            )
            (§ finally )
            (§ block
                (§ if (some? ßdis))
                (§ block
                    (.. ßdis (close))
                )
                (.. ßinputStream (close))
            )
        )
    )

    #_private
    (§ method- #_"Sha256Hash" (§ fn readTextual) [#_"InputStream" (§ name ßinputStream)])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"Hasher" (§ name ßhasher) (.. (Hashing/sha256) (newHasher)))
        (§ block
            (§ let #_"BufferedReader" (§ name ßreader) nil)
            (§ block
                (§ try )
                (§ block
                    (§ ass (§ name ßreader) (§ new #_"BufferedReader" (§ new #_"InputStreamReader" ßinputStream, Charsets/US_ASCII)))
                    (§ let #_"String" (§ name ßmagic) (.. ßreader (readLine)))
                    (§ block
                        (§ if (not (.. TEXTUAL_MAGIC (equals ßmagic))))
                        (§ block
                            (§ throw (§ new #_"IOException" (str "unexpected magic: " ßmagic)))
                        )
                        (§ let #_"int" (§ name ßnumSigs) (Integer/parseInt (.. ßreader (readLine))))
                        (§ block
                            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumSigs) :for (§ recur (+ ßi 1)))
                            (§ block
                                (.. ßreader (readLine)) ;; Skip sigs for now.
                            )
                            (§ let #_"int" (§ name ßnumCheckpoints) (Integer/parseInt (.. ßreader (readLine))))
                            (§ block
                                (Preconditions/checkState (< 0 ßnumCheckpoints))
                                ;; Hash numCheckpoints in a way compatible to the binary format.
                                (.. ßhasher (putBytes (.. (ByteBuffer/allocate 4) (order ByteOrder/BIG_ENDIAN) (putInt ßnumCheckpoints) (array))))
                                (§ let #_"int" (§ name ßsize) StoredBlock/COMPACT_SERIALIZED_SIZE)
                                (§ block
                                    (§ let #_"ByteBuffer" (§ name ßbuffer) (ByteBuffer/allocate ßsize))
                                    (§ block
                                        (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumCheckpoints) :for (§ recur (+ ßi 1)))
                                        (§ block
                                            (§ let #_"byte[]" (§ name ßbytes) (.. BASE64 (decode (.. ßreader (readLine)))))
                                            (§ block
                                                (.. ßhasher (putBytes ßbytes))
                                                (.. ßbuffer (position 0))
                                                (.. ßbuffer (put ßbytes))
                                                (.. ßbuffer (position 0))
                                                (§ let #_"StoredBlock" (§ name ßblock) (StoredBlock/deserializeCompact params, ßbuffer))
                                                (§ block
                                                    (.. checkpoints (put (.. ßblock (getHeader) (getTimeSeconds)), ßblock))
                                                )
                                            )
                                        )
                                        (§ let #_"HashCode" (§ name ßhash) (.. ßhasher (hash)))
                                        (§ block
                                            (.. log (info "Read {} checkpoints, ßhash is {}", (.. checkpoints (size)), ßhash))
                                            (§ return (Sha256Hash/wrap (.. ßhash (asBytes))))
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
                (§ finally )
                (§ block
                    (§ if (some? ßreader))
                    (§ block
                        (.. ßreader (close))
                    )
                )
            )
        )
    )

    ;;;
     ; Returns a {@link StoredBlock} representing the last checkpoint before the given time, for example, normally
     ; you would want to know the checkpoint before the earliest wallet birthday.
     ;;
    #_public
    (§ method #_"StoredBlock" (§ fn getCheckpointBefore) [#_"long" (§ name ßtime)])
    (§ block
        (§ try )
        (§ block
            (Preconditions/checkArgument (< (.. params (getGenesisBlock) (getTimeSeconds)) ßtime))
            ;; This is thread safe because the map never changes after creation.
            (§ let #_"Map.Entry<Long, StoredBlock>" (§ name ßentry) (.. checkpoints (floorEntry ßtime)))
            (§ block
                (§ if (some? ßentry))
                (§ block
                    (§ return (.. ßentry (getValue)))
                )
                (§ let #_"Block" (§ name ßgenesis) (.. params (getGenesisBlock) (cloneAsHeader)))
                (§ block
                    (§ return (§ new #_"StoredBlock" ßgenesis, (.. ßgenesis (getWork)), 0))
                )
            )
        )
        (§ catch #_"VerificationException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
        )
    )

    ;;; Returns the number of checkpoints that were loaded. ;;
    #_public
    (§ method #_"int" (§ fn numCheckpoints) [])
    (§ block
        (.. checkpoints (size))
    )

    ;;; Returns a hash of the concatenated checkpoint data. ;;
    #_public
    (§ method #_"Sha256Hash" (§ fn getDataHash) [])
    (§ block
        dataHash
    )

    ;;;
     ; <p>Convenience method that creates a CheckpointManager, loads the given data, gets the checkpoint for the given
     ; time, then inserts it into the store and sets that to be the chain head.  Useful when you have just created
     ; a new store from scratch and want to use configure it all in one go.</p>
     ;
     ; <p>Note that time is adjusted backwards by a week to account for possible clock drift in the block headers.</p>
     ;;
    #_public
    #_static
    (§ defn #_"void" (§ fn checkpoint) [#_"NetworkParameters" (§ name ßparams), #_"InputStream" (§ name ßcheckpoints), #_"BlockStore" (§ name ßstore), #_"long" (§ name ßtime)])
        (§ throws #_"IOException", #_"BlockStoreException")
    (§ block
        (Preconditions/checkNotNull ßparams)
        (Preconditions/checkNotNull ßstore)
        (Preconditions/checkArgument (not (§ insta ßstore #_"FullPrunedBlockStore")), "You cannot use checkpointing with a full store.")

        (§ ass (§ name ßtime) (- ßtime (* 86400 7)))

        (Preconditions/checkArgument (< 0 ßtime))
        (.. log (info "Attempting to initialize a new block ßstore with a checkpoint for ßtime {} ({})", ßtime, (Utils/dateTimeFormat (* ßtime 1000))))

        (§ let #_"BufferedInputStream" (§ name ßstream) (§ new #_"BufferedInputStream" ßcheckpoints))
        (§ block
            (§ let #_"CheckpointManager" (§ name ßmanager) (§ new #_"CheckpointManager" ßparams, ßstream))
            (§ block
                (§ let #_"StoredBlock" (§ name ßcheckpoint) (.. ßmanager (getCheckpointBefore ßtime)))
                (§ block
                    (.. ßstore (put ßcheckpoint))
                    (.. ßstore (setChainHead ßcheckpoint))
                    nil
                )
            )
        )
    )
)

#_(ns org.bitcoinj.core #_"ChildMessage")

;;;
 ; <p>Represents a Message type that can be contained within another Message.  ChildMessages that have a cached
 ; backing byte array need to invalidate their parent's caches as well as their own if they are modified.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
#_abstract
(§ class #_"ChildMessage" (§ extends #_"Message")
    #_nilable
    #_protected
    (§ field #_"Message" (§ name parent))

    #_public
    (§ constructor #_"ChildMessage" [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ super ßparams)
        this
    )

    #_public
    (§ constructor #_"ChildMessage" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayload), #_"int" (§ name ßoffset), #_"int" (§ name ßprotocolVersion)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayload, ßoffset, ßprotocolVersion)
        this
    )

    #_public
    (§ constructor #_"ChildMessage" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayload), #_"int" (§ name ßoffset), #_"int" (§ name ßprotocolVersion), #_"Message" (§ name ßparent), #_"MessageSerializer" (§ name ßsetSerializer), #_"int" (§ name ßlength)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayload, ßoffset, ßprotocolVersion, ßsetSerializer, ßlength)
        (§ ass (§ name (.. this ßparent)) ßparent)
        this
    )

    #_public
    (§ constructor #_"ChildMessage" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayload), #_"int" (§ name ßoffset)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayload, ßoffset)
        this
    )

    #_public
    (§ constructor #_"ChildMessage" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayload), #_"int" (§ name ßoffset), #_nilable #_"Message" (§ name ßparent), #_"MessageSerializer" (§ name ßsetSerializer), #_"int" (§ name ßlength)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayload, ßoffset, ßsetSerializer, ßlength)
        (§ ass (§ name (.. this ßparent)) ßparent)
        this
    )

    #_public
    (§ method #_"void" (§ fn setParent) [#_nilable #_"Message" (§ name ßparent)])
    (§ block
        (§ if (and (some? (.. this ßparent)) (!= (.. this ßparent) ßparent) (some? ßparent)))
        (§ block
            ;; After old parent is unlinked it won't be able to receive notice if this ChildMessage changes internally.
            ;; To be safe we invalidate the parent cache to ensure it rebuilds manually on serialization.
            (.. this ßparent (unCache))
        )
        (§ ass (§ name (.. this ßparent)) ßparent)
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn unCache) [])
    (§ block
        (.. super (unCache))
        (§ if (some? parent))
        (§ block
            (.. parent (unCache))
        )
        nil
    )

    #_protected
    (§ method #_"void" (§ fn adjustLength) [#_"int" (§ name ßadjustment)])
    (§ block
        (adjustLength 0, ßadjustment)
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn adjustLength) [#_"int" (§ name ßnewArraySize), #_"int" (§ name ßadjustment)])
    (§ block
        (.. super (adjustLength ßnewArraySize, ßadjustment))
        (§ if (some? parent))
        (§ block
            (.. parent (adjustLength ßnewArraySize, ßadjustment))
        )
        nil
    )
)

#_(ns org.bitcoinj.core #_"Coin"
    (:import [java.io Serializable]
             [java.math BigDecimal])
    (:import [com.google.common.base Preconditions]
             [com.google.common.math LongMath]
             [com.google.common.primitives Longs])
   (:require [org.bitcoinj.utils MonetaryFormat]))

;;;
 ; Represents a monetary Bitcoin value.  This class is immutable.
 ;;
#_public
(§ class #_"Coin" (§ implements #_"Monetary", #_"Comparable<Coin>", #_"Serializable")
    ;;;
     ; Number of decimals for one Bitcoin.  This constant is useful for quick adapting to other coins because a lot of
     ; constants derive from it.
     ;;
    #_public
    #_static
    (§ def #_"int" (§ name SMALLEST_UNIT_EXPONENT) 8)

    ;;;
     ; The number of satoshis equal to one bitcoin.
     ;;
    #_private
    #_static
    (§ def- #_"long" (§ name COIN_VALUE) (LongMath/pow 10, SMALLEST_UNIT_EXPONENT))

    ;;;
     ; Zero Bitcoins.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name ZERO) (Coin/valueOf 0))

    ;;;
     ; One Bitcoin.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name COIN) (Coin/valueOf COIN_VALUE))

    ;;;
     ; 0.01 Bitcoins.  This unit is not really used much.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name CENT) (.. COIN (divide 100)))

    ;;;
     ; 0.001 Bitcoins, also known as 1 mBTC.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name MILLICOIN) (.. COIN (divide 1000)))

    ;;;
     ; 0.000001 Bitcoins, also known as 1 µBTC or 1 uBTC.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name MICROCOIN) (.. MILLICOIN (divide 1000)))

    ;;;
     ; A satoshi is the smallest unit that can be transferred.  100 million of them fit into a Bitcoin.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name SATOSHI) (Coin/valueOf 1))

    #_public
    #_static
    (§ def #_"Coin" (§ name FIFTY_COINS) (.. COIN (multiply 50)))

    ;;;
     ; Represents a monetary value of minus one satoshi.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name NEGATIVE_SATOSHI) (Coin/valueOf -1))

    ;;;
     ; The number of satoshis of this monetary value.
     ;;
    #_public
    (§ field #_"long" (§ name value))

    #_private
    (§ constructor- #_"Coin" [#_"long" (§ name ßsatoshis)])
    (§ block
        (§ ass (§ name (.. this value)) ßsatoshis)
        this
    )

    #_public
    #_static
    (§ defn #_"Coin" (§ fn valueOf) [#_"long" (§ name ßsatoshis)])
    (§ block
        (§ new #_"Coin" ßsatoshis)
    )

    #_override
    #_public
    (§ method #_"int" (§ fn smallestUnitExponent) [])
    (§ block
        SMALLEST_UNIT_EXPONENT
    )

    ;;;
     ; Returns the number of satoshis of this monetary value.
     ;;
    #_override
    #_public
    (§ method #_"long" (§ fn getValue) [])
    (§ block
        value
    )

    ;;;
     ; Convert an amount expressed in the way humans are used to into satoshis.
     ;;
    #_public
    #_static
    (§ defn #_"Coin" (§ fn valueOf) [#_"int" (§ name ßcoins), #_"int" (§ name ßcents)])
    (§ block
        (Preconditions/checkArgument (and (<= 0 ßcoins) (<= 0 ßcents) (< ßcents 100)))

        (.. COIN (multiply ßcoins) (add (.. CENT (multiply ßcents))))
    )

    ;;;
     ; Parses an amount expressed in the way humans are used to.
     ;
     ; This takes string in a format understood by {@link BigDecimal#BigDecimal(String)},
     ; for example "0", "1", "0.10", "1.23E3", "1234.5E-5".
     ;
     ; @throws IllegalArgumentException if you try to specify fractional satoshis, or a value out of range.
     ;;
    #_public
    #_static
    (§ defn #_"Coin" (§ fn parseCoin) [#_"String" (§ name ßstr)])
    (§ block
        (§ try )
        (§ block
            (§ let #_"long" (§ name ßsatoshis) (.. (§ new #_"BigDecimal" ßstr) (movePointRight SMALLEST_UNIT_EXPONENT) (longValueExact)))
            (§ block
                (§ return (Coin/valueOf ßsatoshis))
            )
        )
        (§ catch #_"ArithmeticException" (§ name ße))
        (§ block
            (§ throw (§ new #_"IllegalArgumentException" ße)) ;; Repackage exception to honor method contract.
        )
    )

    ;;;
     ; Parses an amount expressed in the way humans are used to.  The amount is cut to satoshi precision.
     ;
     ; This takes string in a format understood by {@link BigDecimal#BigDecimal(String)},
     ; for example "0", "1", "0.10", "1.23E3", "1234.5E-5".
     ;
     ; @throws IllegalArgumentException if you try to specify a value out of range.
     ;;
    #_public
    #_static
    (§ defn #_"Coin" (§ fn parseCoinInexact) [#_"String" (§ name ßstr)])
    (§ block
        (§ try )
        (§ block
            (§ let #_"long" (§ name ßsatoshis) (.. (§ new #_"BigDecimal" ßstr) (movePointRight SMALLEST_UNIT_EXPONENT) (longValue)))
            (§ block
                (§ return (Coin/valueOf ßsatoshis))
            )
        )
        (§ catch #_"ArithmeticException" (§ name ße))
        (§ block
            (§ throw (§ new #_"IllegalArgumentException" ße)) ;; Repackage exception to honor method contract.
        )
    )

    #_public
    (§ method #_"Coin" (§ fn add) [#_"Coin" (§ name ßvalue)])
    (§ block
        (§ new #_"Coin" (LongMath/checkedAdd (.. this ßvalue), (.. ßvalue ßvalue)))
    )

    ;;; Alias for add. ;;
    #_public
    (§ method #_"Coin" (§ fn plus) [#_"Coin" (§ name ßvalue)])
    (§ block
        (add ßvalue)
    )

    #_public
    (§ method #_"Coin" (§ fn subtract) [#_"Coin" (§ name ßvalue)])
    (§ block
        (§ new #_"Coin" (LongMath/checkedSubtract (.. this ßvalue), (.. ßvalue ßvalue)))
    )

    ;;; Alias for subtract. ;;
    #_public
    (§ method #_"Coin" (§ fn minus) [#_"Coin" (§ name ßvalue)])
    (§ block
        (subtract ßvalue)
    )

    #_public
    (§ method #_"Coin" (§ fn multiply) [#_"long" (§ name ßfactor)])
    (§ block
        (§ new #_"Coin" (LongMath/checkedMultiply (.. this value), ßfactor))
    )

    ;;; Alias for multiply. ;;
    #_public
    (§ method #_"Coin" (§ fn times) [#_"long" (§ name ßfactor)])
    (§ block
        (multiply ßfactor)
    )

    ;;; Alias for multiply. ;;
    #_public
    (§ method #_"Coin" (§ fn times) [#_"int" (§ name ßfactor)])
    (§ block
        (multiply ßfactor)
    )

    #_public
    (§ method #_"Coin" (§ fn divide) [#_"long" (§ name ßdivisor)])
    (§ block
        (§ new #_"Coin" (/ (.. this value) ßdivisor))
    )

    ;;; Alias for divide. ;;
    #_public
    (§ method #_"Coin" (§ fn div) [#_"long" (§ name ßdivisor)])
    (§ block
        (divide ßdivisor)
    )

    ;;; Alias for divide. ;;
    #_public
    (§ method #_"Coin" (§ fn div) [#_"int" (§ name ßdivisor)])
    (§ block
        (divide ßdivisor)
    )

    #_public
    (§ method #_"Coin[]" (§ fn divideAndRemainder) [#_"long" (§ name ßdivisor)])
    (§ block
        (§ new #_"Coin[]" (§ coll (§ new #_"Coin" (/ (.. this value) ßdivisor)), (§ new #_"Coin" (% (.. this value) ßdivisor)) ))
    )

    #_public
    (§ method #_"long" (§ fn divide) [#_"Coin" (§ name ßdivisor)])
    (§ block
        (/ (.. this value) (.. ßdivisor value))
    )

    ;;;
     ; Returns true if and only if this instance represents a monetary value greater than zero,
     ; otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isPositive) [])
    (§ block
        (== (signum) 1)
    )

    ;;;
     ; Returns true if and only if this instance represents a monetary value less than zero,
     ; otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isNegative) [])
    (§ block
        (== (signum) -1)
    )

    ;;;
     ; Returns true if and only if this instance represents zero monetary value,
     ; otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isZero) [])
    (§ block
        (== (signum) 0)
    )

    ;;;
     ; Returns true if the monetary value represented by this instance is greater than that
     ; of the given other Coin, otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isGreaterThan) [#_"Coin" (§ name ßother)])
    (§ block
        (< 0 (compareTo ßother))
    )

    ;;;
     ; Returns true if the monetary value represented by this instance is less than that
     ; of the given other Coin, otherwise false.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isLessThan) [#_"Coin" (§ name ßother)])
    (§ block
        (< (compareTo ßother) 0)
    )

    #_public
    (§ method #_"Coin" (§ fn shiftLeft) [#_"int" (§ name ßn)])
    (§ block
        (§ new #_"Coin" (<< (.. this value) ßn))
    )

    #_public
    (§ method #_"Coin" (§ fn shiftRight) [#_"int" (§ name ßn)])
    (§ block
        (§ new #_"Coin" (>> (.. this value) ßn))
    )

    #_override
    #_public
    (§ method #_"int" (§ fn signum) [])
    (§ block
        (§ if (== (.. this value) 0))
        (§ block
            (§ return 0)
        )
        (if (< (.. this value) 0) -1 1)
    )

    #_public
    (§ method #_"Coin" (§ fn negate) [])
    (§ block
        (§ new #_"Coin" (- (.. this value)))
    )

    ;;;
     ; Returns the number of satoshis of this monetary value.  It's deprecated in favour of accessing {@link #value} directly.
     ;;
    #_public
    (§ method #_"long" (§ fn longValue) [])
    (§ block
        (.. this value)
    )

    #_private
    #_static
    (§ def- #_"MonetaryFormat" (§ name FRIENDLY_FORMAT) (.. MonetaryFormat/BTC (minDecimals 2) (repeatOptionalDecimals 1, 6) (postfixCode)))

    ;;;
     ; Returns the value as a 0.12 type string.  More digits after the decimal place will be used
     ; if necessary, but two will always be present.
     ;;
    #_public
    (§ method #_"String" (§ fn toFriendlyString) [])
    (§ block
        (.. FRIENDLY_FORMAT (format this) (toString))
    )

    #_private
    #_static
    (§ def- #_"MonetaryFormat" (§ name PLAIN_FORMAT) (.. MonetaryFormat/BTC (minDecimals 0) (repeatOptionalDecimals 1, 8) (noCode)))

    ;;;
     ; Returns the value as a plain string denominated in BTC.
     ; The result is unformatted with no trailing zeroes.
     ; For instance, a value of 150000 satoshis gives an output string of "0.0015" BTC.
     ;;
    #_public
    (§ method #_"String" (§ fn toPlainString) [])
    (§ block
        (.. PLAIN_FORMAT (format this) (toString))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (Long/toString value)
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (== (.. this value) (.. (§ cast #_"Coin" ßo) value))
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (§ cast #_"int" (.. this value))
    )

    #_override
    #_public
    (§ method #_"int" (§ fn compareTo) [#_"Coin" (§ name ßother)])
    (§ block
        (Longs/compare (.. this value), (.. ßother value))
    )
)

#_(ns org.bitcoinj.core #_"Context"
    (:import [com.google.common.base Preconditions]
             [org.slf4j *]))

;; TODO: Finish adding Context c'tors to all the different objects so we can start deprecating the versions that take NetworkParameters.
;; TODO: Add a working directory notion to Context and make various subsystems that want to use files default to that directory (e.g. Orchid, block stores, wallet, etc).
;; TODO: Auto-register the block chain object here, and then use it in the (newly deprecated) TransactionConfidence.getDepthInBlocks() method:
;;       the new version should take an AbstractBlockChain specifically.
;;       Also use the block chain object reference from the context in PeerGroup and remove the other constructors, as it's easy to forget to wire things up.
;; TODO: Move Threading.USER_THREAD to here and leave behind just a source code stub.  Allow different instantiations of the library to use different user threads.
;; TODO: Keep a URI to where library internal data files can be found, to abstract over the lack of JAR files on Android.
;; TODO: Stash anything else that resembles global library configuration in here and use it to clean up the rest of the API without breaking people.
;; TODO: Move the TorClient into Context, so different parts of the library can read data over Tor without having to request it directly. (or maybe a general socket factory??)

;;;
 ; <p>The Context object holds various objects and pieces of configuration that are scoped to a specific instantiation of
 ; bitcoinj for a specific network.  You can get an instance of this class through calling {@link #get()}.</p>
 ;
 ; <p>Context is new in 0.13 and the library is currently in a transitional period: you should create a Context that
 ; wraps your chosen network parameters before using the rest of the library.  However if you don't, things will still
 ; work as a Context will be created for you and stashed in thread local storage.  The context is then propagated between
 ; library created threads as needed.  This automagical propagation and creation is a temporary mechanism: one day it
 ; will be removed to avoid confusing edge cases that could occur if the developer does not fully understand it e.g.
 ; in the case where multiple instances of the library are in use simultaneously.</p>
 ;;
#_public
(§ class #_"Context"
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"Context")))

    #_public
    #_static
    (§ def #_"int" (§ name DEFAULT_EVENT_HORIZON) 100)

    #_private
    (§ field- #_"TxConfidenceTable" (§ name confidenceTable))
    #_private
    (§ field- #_"NetworkParameters" (§ name params))
    #_private
    (§ field- #_"int" (§ name eventHorizon))
    #_private
    (§ field- #_"boolean" (§ name ensureMinRequiredFee))
    #_private
    (§ field- #_"Coin" (§ name feePerKb))

    ;;;
     ; Creates a new context object.  For now, this will be done for you by the framework.  Eventually you will be
     ; expected to do this yourself in the same manner as fetching a NetworkParameters object (at the start of your app).
     ;
     ; @param params The network parameters that will be associated with this context.
     ;;
    #_public
    (§ constructor #_"Context" [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ this ßparams, DEFAULT_EVENT_HORIZON, Transaction/DEFAULT_TX_FEE, true)
        this
    )

    ;;;
     ; Creates a new custom context object.  This is mainly meant for unit tests for now.
     ;
     ; @param params The network parameters that will be associated with this context.
     ; @param eventHorizon Number of blocks after which the library will delete data and be unable to always process reorgs (see {@link #getEventHorizon()}).
     ; @param feePerKb The default fee per 1000 bytes of transaction data to pay when completing transactions.  For details, see {@link SendRequest#feePerKb}.
     ; @param ensureMinRequiredFee Whether to ensure the minimum required fee by default when completing transactions.  For details, see {@link SendRequest#ensureMinRequiredFee}.
     ;;
    #_public
    (§ constructor #_"Context" [#_"NetworkParameters" (§ name ßparams), #_"int" (§ name ßeventHorizon), #_"Coin" (§ name ßfeePerKb), #_"boolean" (§ name ßensureMinRequiredFee)])
    (§ block
        (.. log (info "Creating bitcoinj {} context.", VersionMessage/BITCOINJ_VERSION))
        (§ ass (§ name (.. this confidenceTable)) (§ new #_"TxConfidenceTable"))
        (§ ass (§ name (.. this ßparams)) ßparams)
        (§ ass (§ name (.. this ßeventHorizon)) ßeventHorizon)
        (§ ass (§ name (.. this ßensureMinRequiredFee)) ßensureMinRequiredFee)
        (§ ass (§ name (.. this ßfeePerKb)) ßfeePerKb)
        (§ ass (§ name lastConstructed) this)
        (.. slot (set this))
        this
    )

    #_private
    #_static
    #_volatile
    (§ def- #_"Context" (§ name lastConstructed))
    #_private
    #_static
    (§ def- #_"boolean" (§ name isStrictMode))
    #_private
    #_static
    (§ def- #_"ThreadLocal<Context>" (§ name slot) (§ new #_"ThreadLocal<>"))

    ;;;
     ; Returns the current context that is associated with the <b>calling thread</b>.  BitcoinJ is an API that has thread
     ; affinity: much like OpenGL it expects each thread that accesses it to have been configured with a global Context
     ; object.  This method returns that.  Note that to help you develop, this method will <i>also</i> propagate whichever
     ; context was created last onto the current thread, if it's missing.  However it will print an error when doing so
     ; because propagation of contexts is meant to be done manually: this is so two libraries or subsystems that
     ; independently use bitcoinj (or possibly alt coin forks of it) can operate correctly.
     ;
     ; @throws java.lang.IllegalStateException if no context exists at all or if we are in strict mode and there is no context.
     ;;
    #_public
    #_static
    (§ defn #_"Context" (§ fn get) [])
    (§ block
        (§ let #_"Context" (§ name ßtls) (.. slot (get)))
        (§ block
            (§ if (nil? ßtls))
            (§ block
                (§ if isStrictMode)
                (§ block
                    (.. log (error "Thread is missing a bitcoinj context."))
                    (.. log (error "You should use Context/propagate() or a ContextPropagatingThreadFactory."))
                    (§ throw (§ new #_"IllegalStateException" "missing context"))
                )
                (§ if (nil? lastConstructed))
                (§ block
                    (§ throw (§ new #_"IllegalStateException" "You must construct a Context object before using bitcoinj!"))
                )
                (.. slot (set lastConstructed))
                (.. log (error "Performing thread fixup: you are accessing bitcoinj via a thread that has not had any context set on it."))
                (.. log (error "This error has been corrected for, but doing this makes your app less robust."))
                (.. log (error "You should use Context/propagate() or a ContextPropagatingThreadFactory."))
                (.. log (error "Please refer to the user guide for more information about this."))
                (.. log (error "Thread name is {}.", (.. (Thread/currentThread) (getName))))
                ;; TODO: Actually write the user guide section about this.
                (§ return lastConstructed)
            )

            ßtls
        )
    )

    ;;;
     ; Require that new threads use {@link #propagate(Context)} or {@link org.bitcoinj.utils.ContextPropagatingThreadFactory},
     ; rather than using a heuristic for the desired context.
     ;;
    #_public
    #_static
    (§ defn #_"void" (§ fn enableStrictMode) [])
    (§ block
        (§ ass (§ name isStrictMode) true)
        nil
    )

    ;; A temporary internal shim designed to help us migrate internally in a way that doesn't wreck source compatibility.
    #_public
    #_static
    (§ defn #_"Context" (§ fn getOrCreate) [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ let #_"Context" (§ name ßcontext))
        (§ block
            (§ try )
            (§ block
                (§ ass (§ name ßcontext) (get))
            )
            (§ catch #_"IllegalStateException" (§ name ße))
            (§ block
                (.. log (warn "Implicitly creating ßcontext. This is a migration step and this message will eventually go away."))
                (§ ass (§ name ßcontext) (§ new #_"Context" ßparams))
                (§ return ßcontext)
            )
            (§ if (!= (.. ßcontext (getParams)) ßparams))
            (§ block
                (§ throw (§ new #_"IllegalStateException" (str "Context does not match implicit network params: " (.. ßcontext (getParams)) " vs " ßparams)))
            )
            ßcontext
        )
    )

    ;;;
     ; Sets the given context as the current thread context.  You should use this if you create your own threads that
     ; want to create core BitcoinJ objects.  Generally, if a class can accept a Context in its constructor and might
     ; be used (even indirectly) by a thread, you will want to call this first.  Your task may be simplified by using
     ; a {@link org.bitcoinj.utils.ContextPropagatingThreadFactory}.
     ;;
    #_public
    #_static
    (§ defn #_"void" (§ fn propagate) [#_"Context" (§ name ßcontext)])
    (§ block
        (.. slot (set (Preconditions/checkNotNull ßcontext)))
        nil
    )

    ;;;
     ; Returns the {@link TxConfidenceTable} created by this context.  The pool tracks advertised
     ; and downloaded transactions so their confidence can be measured as a proportion of how many peers announced it.
     ; With an un-tampered with internet connection, the more peers announce a transaction the more confidence you can
     ; have that it's really valid.
     ;;
    #_public
    (§ method #_"TxConfidenceTable" (§ fn getConfidenceTable) [])
    (§ block
        confidenceTable
    )

    ;;;
     ; Returns the {@link org.bitcoinj.core.NetworkParameters} specified when this context was (auto) created.  The
     ; network parameters defines various hard coded constants for a specific instance of a Bitcoin network, such as
     ; main net, testnet, etc.
     ;;
    #_public
    (§ method #_"NetworkParameters" (§ fn getParams) [])
    (§ block
        params
    )

    ;;;
     ; The event horizon is the number of blocks after which various bits of the library consider a transaction to be
     ; so confirmed that it's safe to delete data.  Re-orgs larger than the event horizon will not be correctly
     ; processed, so the default value is high (100).
     ;;
    #_public
    (§ method #_"int" (§ fn getEventHorizon) [])
    (§ block
        eventHorizon
    )

    ;;;
     ; The default fee per 1000 bytes of transaction data to pay when completing transactions.  For details, see {@link SendRequest#feePerKb}.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getFeePerKb) [])
    (§ block
        feePerKb
    )

    ;;;
     ; Whether to ensure the minimum required fee by default when completing transactions.  For details, see {@link SendRequest#ensureMinRequiredFee}.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isEnsureMinRequiredFee) [])
    (§ block
        ensureMinRequiredFee
    )
)

