             [org.spongycastle.crypto.params *]
             [org.spongycastle.crypto.signers ECDSASigner HMacDSAKCalculator]
             [org.spongycastle.math.ec ECAlgorithms ECPoint FixedPointCombMultiplier FixedPointUtil]
             [org.spongycastle.math.ec.custom.sec SecP256K1Curve]
             [org.spongycastle.util.encoders Base64])
   (:require [org.bitcoinj.crypto *]
             [org.bitcoinj.wallet Protos Wallet]))

;; TODO: Move this class to tracking compression state itself.
;; The Bouncy Castle guys are deprecating their own tracking of the compression state.

;;;
 ; <p>Represents an elliptic curve public and (optionally) private key, usable for digital signatures but not encryption.
 ; Creating a new ECKey with the empty constructor will generate a new random keypair.  Other static methods can be used
 ; when you already have the public or private parts.  If you create a key with only the public part, you can check
 ; signatures but not create them.</p>
 ;
 ; <p>ECKey also provides access to Bitcoin Core compatible text message signing, as accessible via the UI or JSON-RPC.
 ; This is slightly different to signing raw bytes - if you want to sign your own data and it won't be exposed as
 ; text to people, you don't want to use this.  If in doubt, ask on the mailing list.</p>
 ;
 ; <p>The ECDSA algorithm supports <i>key recovery</i> in which a signature plus a couple of discriminator bits can
 ; be reversed to find the public key used to calculate it.  This can be convenient when you have a message and a
 ; signature and want to find out who signed it, rather than requiring the user to provide the expected identity.</p>
 ;
 ; <p>This class supports a variety of serialization forms.  The methods that accept/return byte arrays serialize
 ; private keys as raw byte arrays and public keys using the SEC standard byte encoding for public keys.  Signatures
 ; are encoded using ASN.1/DER inside the Bitcoin protocol.</p>
 ;
 ; <p>A key can be <i>compressed</i> or <i>uncompressed</i>.  This refers to whether the public key is represented
 ; when encoded into bytes as an (x, y) coordinate on the elliptic curve, or whether it's represented as just an X
 ; co-ordinate and an extra byte that carries a sign bit.  With the latter form the Y coordinate can be calculated
 ; dynamically, however, <b>because the binary serialization is different the address of a key changes if its
 ; compression status is changed</b>.  If you deviate from the defaults it's important to understand this: money sent
 ; to a compressed version of the key will have a different address to the same key in uncompressed form.  Whether
 ; a public key is compressed or not is recorded in the SEC binary serialisation format, and preserved in a flag in
 ; this class so round-tripping preserves state.  Unless you're working with old software or doing unusual things, you
 ; can usually ignore the compressed/uncompressed distinction.</p>
 ;;
#_public
(§ class #_"ECKey" (§ implements #_"EncryptableItem")
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"ECKey")))

    ;;; Sorts oldest keys first, newest last. ;;
    #_public
    #_static
    (§ def #_"Comparator<ECKey>" (§ name AGE_COMPARATOR) (§ new #_"Comparator<ECKey>"
        (§ anon
            #_override
            #_public
            (§ method #_"int" (§ fn compare) [#_"ECKey" (§ name ßk1), #_"ECKey" (§ name ßk2)])
            (§ block
                (§ if (== (.. ßk1 creationTimeSeconds) (.. ßk2 creationTimeSeconds)))
                (§ block
                    (§ return 0)
                )

                (if (< (.. ßk1 creationTimeSeconds) (.. ßk2 creationTimeSeconds)) -1 1)
            )
        )))

    ;;; Compares pub key bytes using {@link com.google.common.primitives.UnsignedBytes#lexicographicalComparator()}. ;;
    #_public
    #_static
    (§ def #_"Comparator<ECKey>" (§ name PUBKEY_COMPARATOR) (§ new #_"Comparator<ECKey>"
        (§ anon
            #_private
            (§ field- #_"Comparator<byte[]>" (§ name comparator) (UnsignedBytes/lexicographicalComparator))

            #_override
            #_public
            (§ method #_"int" (§ fn compare) [#_"ECKey" (§ name ßk1), #_"ECKey" (§ name ßk2)])
            (§ block
                (.. comparator (compare (.. ßk1 (getPubKey)), (.. ßk2 (getPubKey))))
            )
        )))

    ;; The parameters of the secp256k1 curve that Bitcoin uses.
    #_private
    #_static
    (§ def- #_"X9ECParameters" (§ name CURVE_PARAMS) (CustomNamedCurves/getByName "secp256k1"))

    ;;; The parameters of the secp256k1 curve that Bitcoin uses. ;;
    #_public
    #_static
    (§ def #_"ECDomainParameters" (§ name CURVE))

    ;;;
     ; Equal to CURVE.getN().shiftRight(1), used for canonicalising the S value of a signature.
     ; If you aren't sure what this is about, you can ignore it.
     ;;
    #_public
    #_static
    (§ def #_"BigInteger" (§ name HALF_CURVE_ORDER))

    #_private
    #_static
    (§ def- #_"SecureRandom" (§ name secureRandom))

    #_static
    (§ block
        ;; Init proper random number generator, as some old Android installations have bugs that make it unsecure.
        (§ if (Utils/isAndroidRuntime))
        (§ block
            (§ new #_"LinuxSecureRandom")
        )

        ;; Tell Bouncy Castle to precompute data that's needed during secp256k1 calculations.  Increasing the width
        ;; number makes calculations faster, but at a cost of extra memory usage and with decreasing returns.  12 was
        ;; picked after consulting with the BC team.
        (FixedPointUtil/precompute (.. CURVE_PARAMS (getG)), 12)
        (§ ass (§ name CURVE) (§ new #_"ECDomainParameters" (.. CURVE_PARAMS (getCurve)), (.. CURVE_PARAMS (getG)), (.. CURVE_PARAMS (getN)), (.. CURVE_PARAMS (getH))))
        (§ ass (§ name HALF_CURVE_ORDER) (.. CURVE_PARAMS (getN) (shiftRight 1)))
        (§ ass (§ name secureRandom) (§ new #_"SecureRandom"))
    )

    ;; The two parts of the key.  If "priv" is set, "pub" can always be calculated.  If "pub" is set but not "priv", we
    ;; can only verify signatures not make them.
    #_protected
    (§ field #_"BigInteger" (§ name priv)) ;; A field element.
    #_protected
    (§ field #_"LazyECPoint" (§ name pub))

    ;; Creation time of the key in seconds since the epoch, or zero if the key was deserialized from a version that did
    ;; not have this field.
    #_protected
    (§ field #_"long" (§ name creationTimeSeconds))

    #_protected
    (§ field #_"KeyCrypter" (§ name keyCrypter))
    #_protected
    (§ field #_"EncryptedData" (§ name encryptedPrivateKey))

    #_private
    (§ field- #_"byte[]" (§ name pubKeyHash))

    ;;;
     ; Generates an entirely new keypair.  Point compression is used so the resulting public key will be 33 bytes
     ; (32 for the co-ordinate and 1 byte to represent the y bit).
     ;;
    #_public
    (§ constructor #_"ECKey" [])
    (§ block
        (§ this secureRandom)
        this
    )

    ;;;
     ; Generates an entirely new keypair with the given {@link SecureRandom} object.  Point compression is used so the
     ; resulting public key will be 33 bytes (32 for the co-ordinate and 1 byte to represent the y bit).
     ;;
    #_public
    (§ constructor #_"ECKey" [#_"SecureRandom" (§ name ßsecureRandom)])
    (§ block
        (§ let #_"ECKeyPairGenerator" (§ name ßgenerator) (§ new #_"ECKeyPairGenerator"))
        (§ block
            (§ let #_"ECKeyGenerationParameters" (§ name ßkeygenParams) (§ new #_"ECKeyGenerationParameters" CURVE, ßsecureRandom))
            (§ block
                (.. ßgenerator (init ßkeygenParams))
                (§ let #_"AsymmetricCipherKeyPair" (§ name ßkeypair) (.. ßgenerator (generateKeyPair)))
                (§ block
                    (§ let #_"ECPrivateKeyParameters" (§ name ßprivParams) (§ cast #_"ECPrivateKeyParameters" (.. ßkeypair (getPrivate))))
                    (§ block
                        (§ let #_"ECPublicKeyParameters" (§ name ßpubParams) (§ cast #_"ECPublicKeyParameters" (.. ßkeypair (getPublic))))
                        (§ block
                            (§ ass (§ name priv) (.. ßprivParams (getD)))
                            (§ ass (§ name pub) (§ new #_"LazyECPoint" (.. CURVE (getCurve)), (.. ßpubParams (getQ) (getEncoded true))))
                            (§ ass (§ name creationTimeSeconds) (Utils/currentTimeSeconds))
                            this
                        )
                    )
                )
            )
        )
    )

    #_protected
    (§ constructor #_"ECKey" [#_nilable #_"BigInteger" (§ name ßpriv), #_"ECPoint" (§ name ßpub)])
    (§ block
        (§ this ßpriv, (§ new #_"LazyECPoint" (Preconditions/checkNotNull ßpub)))
        this
    )

    #_protected
    (§ constructor #_"ECKey" [#_nilable #_"BigInteger" (§ name ßpriv), #_"LazyECPoint" (§ name ßpub)])
    (§ block
        (§ if (some? ßpriv))
        (§ block
            (Preconditions/checkArgument (<= (.. ßpriv (bitLength)) (<< 32 3)), "private key exceeds 32 bytes: {} bits", (.. ßpriv (bitLength)))
            ;; Try and catch buggy callers or bad key imports, etc.  Zero and one are special because these are often
            ;; used as sentinel values and because scripting languages have a habit of auto-casting true and false to
            ;; 1 and 0 or vice-versa.  Type confusion bugs could therefore result in private keys with these values.
            (Preconditions/checkArgument (not (.. ßpriv (equals BigInteger/ZERO))))
            (Preconditions/checkArgument (not (.. ßpriv (equals BigInteger/ONE))))
        )
        (§ ass (§ name (.. this ßpriv)) ßpriv)
        (§ ass (§ name (.. this ßpub)) (Preconditions/checkNotNull ßpub))
        this
    )

    ;;;
     ; Utility for compressing an elliptic curve point.  Returns the same point if it's already compressed.
     ; See the ECKey class docs for a discussion of point compression.
     ;;
    #_public
    #_static
    (§ defn #_"ECPoint" (§ fn compressPoint) [#_"ECPoint" (§ name ßpoint)])
    (§ block
        (getPointWithCompression ßpoint, true)
    )

    #_public
    #_static
    (§ defn #_"LazyECPoint" (§ fn compressPoint) [#_"LazyECPoint" (§ name ßpoint)])
    (§ block
        (if (.. ßpoint (isCompressed)) ßpoint (§ new #_"LazyECPoint" (compressPoint (.. ßpoint (get)))))
    )

    ;;;
     ; Utility for decompressing an elliptic curve point.  Returns the same point if it's already compressed.
     ; See the ECKey class docs for a discussion of point compression.
     ;;
    #_public
    #_static
    (§ defn #_"ECPoint" (§ fn decompressPoint) [#_"ECPoint" (§ name ßpoint)])
    (§ block
        (getPointWithCompression ßpoint, false)
    )

    #_public
    #_static
    (§ defn #_"LazyECPoint" (§ fn decompressPoint) [#_"LazyECPoint" (§ name ßpoint)])
    (§ block
        (if (not (.. ßpoint (isCompressed))) ßpoint (§ new #_"LazyECPoint" (decompressPoint (.. ßpoint (get)))))
    )

    #_private
    #_static
    (§ defn- #_"ECPoint" (§ fn getPointWithCompression) [#_"ECPoint" (§ name ßpoint), #_"boolean" (§ name ßcompressed)])
    (§ block
        (§ if (== (.. ßpoint (isCompressed)) ßcompressed))
        (§ block
            (§ return ßpoint)
        )

        (§ ass (§ name ßpoint) (.. ßpoint (normalize)))
        (§ let #_"BigInteger" (§ name ßx) (.. ßpoint (getAffineXCoord) (toBigInteger)))
        (§ block
            (§ let #_"BigInteger" (§ name ßy) (.. ßpoint (getAffineYCoord) (toBigInteger)))
            (§ block
                (.. CURVE (getCurve) (createPoint ßx, ßy, ßcompressed))
            )
        )
    )

    ;;;
     ; Construct an ECKey from an ASN.1 encoded private key.  These are produced by OpenSSL and stored by Bitcoin
     ; Core in its wallet.  Note that this is slow because it requires an EC point multiply.
     ;;
    #_public
    #_static
    (§ defn #_"ECKey" (§ fn fromASN1) [#_"byte[]" (§ name ßasn1privkey)])
    (§ block
        (extractKeyFromASN1 ßasn1privkey)
    )

    ;;;
     ; Creates an ECKey given the private key only.  The public key is calculated from it (this is slow).  The resulting
     ; public key is compressed.
     ;;
    #_public
    #_static
    (§ defn #_"ECKey" (§ fn fromPrivate) [#_"BigInteger" (§ name ßprivKey)])
    (§ block
        (fromPrivate ßprivKey, true)
    )

    ;;;
     ; Creates an ECKey given the private key only.  The public key is calculated from it (this is slow), either
     ; compressed or not.
     ;;
    #_public
    #_static
    (§ defn #_"ECKey" (§ fn fromPrivate) [#_"BigInteger" (§ name ßprivKey), #_"boolean" (§ name ßcompressed)])
    (§ block
        (§ let #_"ECPoint" (§ name ßpoint) (publicPointFromPrivate ßprivKey))
        (§ block
            (§ new #_"ECKey" ßprivKey, (getPointWithCompression ßpoint, ßcompressed))
        )
    )

    ;;;
     ; Creates an ECKey given the private key only.  The public key is calculated from it (this is slow).  The resulting
     ; public key is compressed.
     ;;
    #_public
    #_static
    (§ defn #_"ECKey" (§ fn fromPrivate) [#_"byte[]" (§ name ßprivKeyBytes)])
    (§ block
        (fromPrivate (§ new #_"BigInteger" 1, ßprivKeyBytes))
    )

    ;;;
     ; Creates an ECKey given the private key only.  The public key is calculated from it (this is slow), either
     ; compressed or not.
     ;;
    #_public
    #_static
    (§ defn #_"ECKey" (§ fn fromPrivate) [#_"byte[]" (§ name ßprivKeyBytes), #_"boolean" (§ name ßcompressed)])
    (§ block
        (fromPrivate (§ new #_"BigInteger" 1, ßprivKeyBytes), ßcompressed)
    )

    ;;;
     ; Creates an ECKey that simply trusts the caller to ensure that point is really the result of multiplying the
     ; generator point by the private key.  This is used to speed things up when you know you have the right values
     ; already.  The compression state of pub will be preserved.
     ;;
    #_public
    #_static
    (§ defn #_"ECKey" (§ fn fromPrivateAndPrecalculatedPublic) [#_"BigInteger" (§ name ßpriv), #_"ECPoint" (§ name ßpub)])
    (§ block
        (§ new #_"ECKey" ßpriv, ßpub)
    )

    ;;;
     ; Creates an ECKey that simply trusts the caller to ensure that point is really the result of multiplying the
     ; generator point by the private key.  This is used to speed things up when you know you have the right values
     ; already.  The compression state of the point will be preserved.
     ;;
    #_public
    #_static
    (§ defn #_"ECKey" (§ fn fromPrivateAndPrecalculatedPublic) [#_"byte[]" (§ name ßpriv), #_"byte[]" (§ name ßpub)])
    (§ block
        (Preconditions/checkNotNull ßpriv)
        (Preconditions/checkNotNull ßpub)
        (§ new #_"ECKey" (§ new #_"BigInteger" 1, ßpriv), (.. CURVE (getCurve) (decodePoint ßpub)))
    )

    ;;;
     ; Creates an ECKey that cannot be used for signing, only verifying signatures, from the given point.
     ; The compression state of pub will be preserved.
     ;;
    #_public
    #_static
    (§ defn #_"ECKey" (§ fn fromPublicOnly) [#_"ECPoint" (§ name ßpub)])
    (§ block
        (§ new #_"ECKey" nil, ßpub)
    )

    ;;;
     ; Creates an ECKey that cannot be used for signing, only verifying signatures, from the given encoded point.
     ; The compression state of pub will be preserved.
     ;;
    #_public
    #_static
    (§ defn #_"ECKey" (§ fn fromPublicOnly) [#_"byte[]" (§ name ßpub)])
    (§ block
        (§ new #_"ECKey" nil, (.. CURVE (getCurve) (decodePoint ßpub)))
    )

    ;;;
     ; Returns a copy of this key, but with the public point represented in uncompressed form.  Normally you would
     ; never need this: it's for specialised scenarios or when backwards compatibility in encoded form is necessary.
     ;;
    #_public
    (§ method #_"ECKey" (§ fn decompress) [])
    (§ block
        (if (.. pub (isCompressed)) (§ new #_"ECKey" priv, (decompressPoint (.. pub (get)))) this)
    )

    ;;;
     ; Creates an ECKey given only the private key bytes.  This is the same as using the BigInteger constructor, but
     ; is more convenient if you are importing a key from elsewhere.  The public key will be automatically derived
     ; from the private key.
     ;;
    #_deprecated
    #_public
    (§ constructor #_"ECKey" [#_nilable #_"byte[]" (§ name ßprivKeyBytes), #_nilable #_"byte[]" (§ name ßpubKey)])
    (§ block
        (§ this (when (some? ßprivKeyBytes) (§ new #_"BigInteger" 1, ßprivKeyBytes)), ßpubKey)
        this
    )

    ;;;
     ; Create a new ECKey with an encrypted private key, a public key and a KeyCrypter.
     ;
     ; @param encryptedPrivateKey The encrypted private key.
     ; @param pubKey The public key.
     ; @param keyCrypter The KeyCrypter that will be used, with an AES key, to encrypt and decrypt the private key.
     ;;
    #_deprecated
    #_public
    (§ constructor #_"ECKey" [#_"EncryptedData" (§ name ßencryptedPrivateKey), #_"byte[]" (§ name ßpubKey), #_"KeyCrypter" (§ name ßkeyCrypter)])
    (§ block
        (§ this (§ cast #_"byte[]" nil), ßpubKey)

        (§ ass (§ name (.. this ßkeyCrypter)) (Preconditions/checkNotNull ßkeyCrypter))
        (§ ass (§ name (.. this ßencryptedPrivateKey)) ßencryptedPrivateKey)
        this
    )

    ;;;
     ; Constructs a key that has an encrypted private component.  The given object wraps encrypted bytes and an
     ; initialization vector.  Note that the key will not be decrypted during this call: the returned ECKey is
     ; unusable for signing unless a decryption key is supplied.
     ;;
    #_public
    #_static
    (§ defn #_"ECKey" (§ fn fromEncrypted) [#_"EncryptedData" (§ name ßencryptedPrivateKey), #_"KeyCrypter" (§ name ßcrypter), #_"byte[]" (§ name ßpubKey)])
    (§ block
        (§ let #_"ECKey" (§ name ßkey) (fromPublicOnly ßpubKey))
        (§ block
            (§ ass (§ name (.. ßkey ßencryptedPrivateKey)) (Preconditions/checkNotNull ßencryptedPrivateKey))
            (§ ass (§ name (.. ßkey keyCrypter)) (Preconditions/checkNotNull ßcrypter))
            ßkey
        )
    )

    ;;;
     ; Creates an ECKey given either the private key only, the public key only, or both.  If only the private key
     ; is supplied, the public key will be calculated from it (this is slow).  If both are supplied, it's assumed
     ; the public key already correctly matches the private key.  If only the public key is supplied, this ECKey
     ; cannot be used for signing.
     ; @param compressed If set to true and pubKey is null, the derived public key will be in compressed form.
     ;;
    #_deprecated
    #_public
    (§ constructor #_"ECKey" [#_nilable #_"BigInteger" (§ name ßprivKey), #_nilable #_"byte[]" (§ name ßpubKey), #_"boolean" (§ name ßcompressed)])
    (§ block
        (§ if (and (nil? ßprivKey) (nil? ßpubKey)))
        (§ block
            (§ throw (§ new #_"IllegalArgumentException" "ECKey requires at least private or public key"))
        )
        (§ ass (§ name (.. this priv)) ßprivKey)
        (§ if (nil? ßpubKey))
        (§ block
            ;; Derive public from private.
            (§ let #_"ECPoint" (§ name ßpoint) (publicPointFromPrivate ßprivKey))
            (§ block
                (§ ass (§ name ßpoint) (getPointWithCompression ßpoint, ßcompressed))
                (§ ass (§ name (.. this pub)) (§ new #_"LazyECPoint" ßpoint))
            )
        )
        (§ else )
        (§ block
            ;; We expect the pubkey to be in regular encoded form, just as a BigInteger.
            ;; Therefore the first byte is a special marker byte.
            ;; TODO: This is probably not a useful API and may be confusing.
            (§ ass (§ name (.. this pub)) (§ new #_"LazyECPoint" (.. CURVE (getCurve)), ßpubKey))
        )
        this
    )

    ;;;
     ; Creates an ECKey given either the private key only, the public key only, or both.  If only the private key
     ; is supplied, the public key will be calculated from it (this is slow).  If both are supplied, it's assumed
     ; the public key already correctly matches the public key.  If only the public key is supplied, this ECKey cannot
     ; be used for signing.
     ;;
    #_deprecated
    #_private
    (§ constructor- #_"ECKey" [#_nilable #_"BigInteger" (§ name ßprivKey), #_nilable #_"byte[]" (§ name ßpubKey)])
    (§ block
        (§ this ßprivKey, ßpubKey, false)
        this
    )

    ;;;
     ; Returns true if this key doesn't have unencrypted access to private key bytes.  This may be because
     ; it was never given any private key bytes to begin with (a watching key), or because the key is encrypted.
     ; You can use {@link #isEncrypted()} to tell the cases apart.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isPubKeyOnly) [])
    (§ block
        (nil? priv)
    )

    ;;;
     ; Returns true if this key has unencrypted access to private key bytes.
     ; Does the opposite of {@link #isPubKeyOnly()}.
     ;;
    #_public
    (§ method #_"boolean" (§ fn hasPrivKey) [])
    (§ block
        (some? priv)
    )

    ;;; Returns true if this key is watch only, meaning it has a public key but no private key. ;;
    #_public
    (§ method #_"boolean" (§ fn isWatching) [])
    (§ block
        (and (isPubKeyOnly) (not (isEncrypted)))
    )

    ;;;
     ; Output this ECKey as an ASN.1 encoded private key, as understood by OpenSSL or used by Bitcoin Core
     ; in its wallet storage format.
     ; @throws org.bitcoinj.core.ECKey.MissingPrivateKeyException if the private key is missing or encrypted.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn toASN1) [])
    (§ block
        (§ try )
        (§ block
            (§ let #_"byte[]" (§ name ßprivKeyBytes) (getPrivKeyBytes))
            (§ block
                (§ let #_"ByteArrayOutputStream" (§ name ßbaos) (§ new #_"ByteArrayOutputStream" 400))
                (§ block

                    ;; ASN1_SEQUENCE(EC_PRIVATEKEY) = {
                    ;;   ASN1_SIMPLE(EC_PRIVATEKEY, version, LONG),
                    ;;   ASN1_SIMPLE(EC_PRIVATEKEY, privateKey, ASN1_OCTET_STRING),
                    ;;   ASN1_EXP_OPT(EC_PRIVATEKEY, parameters, ECPKPARAMETERS, 0),
                    ;;   ASN1_EXP_OPT(EC_PRIVATEKEY, publicKey, ASN1_BIT_STRING, 1)
                    ;; } ASN1_SEQUENCE_END(EC_PRIVATEKEY)
                    (§ let #_"DERSequenceGenerator" (§ name ßseq) (§ new #_"DERSequenceGenerator" ßbaos))
                    (§ block
                        (.. ßseq (addObject (§ new #_"ASN1Integer" 1))) ;; version
                        (.. ßseq (addObject (§ new #_"DEROctetString" ßprivKeyBytes)))
                        (.. ßseq (addObject (§ new #_"DERTaggedObject" 0, (.. CURVE_PARAMS (toASN1Primitive)))))
                        (.. ßseq (addObject (§ new #_"DERTaggedObject" 1, (§ new #_"DERBitString" (getPubKey)))))
                        (.. ßseq (close))
                        (§ return (.. ßbaos (toByteArray)))
                    )
                )
            )
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen, writing to memory stream.
        )
    )

    ;;;
     ; Returns public key bytes from the given private key.  To convert a byte array into a BigInteger, use <tt>new BigInteger(1, bytes)</tt>.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn publicKeyFromPrivate) [#_"BigInteger" (§ name ßprivKey), #_"boolean" (§ name ßcompressed)])
    (§ block
        (.. (publicPointFromPrivate ßprivKey) (getEncoded ßcompressed))
    )

    ;;;
     ; Returns public key point from the given private key.  To convert a byte array into a BigInteger, use <tt>new BigInteger(1, bytes)</tt>.
     ;;
    #_public
    #_static
    (§ defn #_"ECPoint" (§ fn publicPointFromPrivate) [#_"BigInteger" (§ name ßprivKey)])
    (§ block
        ;;
         ; TODO: FixedPointCombMultiplier currently doesn't support scalars longer than the group order,
         ; but that could change in future versions.
         ;;
        (§ if (< (.. CURVE (getN) (bitLength)) (.. ßprivKey (bitLength))))
        (§ block
            (§ ass (§ name ßprivKey) (.. ßprivKey (mod (.. CURVE (getN)))))
        )

        (.. (§ new #_"FixedPointCombMultiplier") (multiply (.. CURVE (getG)), ßprivKey))
    )

    ;;; Gets the hash160 form of the public key (as seen in addresses). ;;
    #_public
    (§ method #_"byte[]" (§ fn getPubKeyHash) [])
    (§ block
        (§ if (nil? pubKeyHash))
        (§ block
            (§ ass (§ name pubKeyHash) (Utils/sha256hash160 (.. this pub (getEncoded))))
        )

        pubKeyHash
    )

    ;;;
     ; Gets the raw public key value.  This appears in transaction scriptSigs.  Note that this is <b>not</b> the same
     ; as the pubKeyHash/address.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getPubKey) [])
    (§ block
        (.. pub (getEncoded))
    )

    ;;; Gets the public key in the form of an elliptic curve point object from Bouncy Castle. ;;
    #_public
    (§ method #_"ECPoint" (§ fn getPubKeyPoint) [])
    (§ block
        (.. pub (get))
    )

    ;;;
