        (§ block
            (§ ass (§ name (.. this ßr)) ßr)
            (§ ass (§ name (.. this ßs)) ßs)
            this
        )

        ;;;
         ; Returns true if the S component is "low", that means it is below {@link ECKey#HALF_CURVE_ORDER}.  See
         ; <a href="https://github.com/bitcoin/bips/blob/master/bip-0062.mediawiki#Low_S_values_in_signatures">BIP62</a>.
         ;;
        #_public
        (§ method #_"boolean" (§ fn isCanonical) [])
        (§ block
            (<= (.. s (compareTo HALF_CURVE_ORDER)) 0)
        )

        ;;;
         ; Will automatically adjust the S component to be less than or equal to half the curve order, if necessary.
         ; This is required because for every signature (r,s) the signature (r, -s (mod N)) is a valid signature of
         ; the same message.  However, we dislike the ability to modify the bits of a Bitcoin transaction after it's
         ; been signed, as that violates various assumed invariants.  Thus in future only one of those forms will be
         ; considered legal and the other will be banned.
         ;;
        #_public
        (§ method #_"ECDSASignature" (§ fn toCanonicalised) [])
        (§ block
            (§ if (not (isCanonical)))
            (§ block
                ;; The order of the curve is the number of valid points that exist on that curve.  If S is in the upper
                ;; half of the number of valid points, then bring it back to the lower half.  Otherwise, imagine that
                ;;    N = 10
                ;;    s = 8, so (-8 % 10 == 2) thus both (r, 8) and (r, 2) are valid solutions.
                ;;    10 - 8 == 2, giving us always the latter solution, which is canonical.
                (§ return (§ new #_"ECDSASignature" r, (.. CURVE (getN) (subtract s))))
            )

            this
        )

        ;;;
         ; DER is an international standard for serializing data structures which is widely used in cryptography.
         ; It's somewhat like protocol buffers but less convenient.  This method returns a standard DER encoding
         ; of the signature, as recognized by OpenSSL and other libraries.
         ;;
        #_public
        (§ method #_"byte[]" (§ fn encodeToDER) [])
        (§ block
            (§ try )
            (§ block
                (§ return (.. (derByteStream) (toByteArray)))
            )
            (§ catch #_"IOException" (§ name ße))
            (§ block
                (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen.
            )
        )

        #_public
        #_static
        (§ defn #_"ECDSASignature" (§ fn decodeFromDER) [#_"byte[]" (§ name ßbytes)])
            (§ throws #_"IllegalArgumentException")
        (§ block
            (§ let #_"ASN1InputStream" (§ name ßdecoder) nil)
            (§ block
                (§ try )
                (§ block
                    (§ ass (§ name ßdecoder) (§ new #_"ASN1InputStream" ßbytes))
                    (§ let #_"DLSequence" (§ name ßseq) (§ cast #_"DLSequence" (.. ßdecoder (readObject))))
                    (§ block
                        (§ if (nil? ßseq))
                        (§ block
                            (§ throw (§ new #_"IllegalArgumentException" "Reached past end of ASN.1 stream."))
                        )

                        (§ let #_"ASN1Integer" (§ name ßr))
                        (§ block
                            (§ let #_"ASN1Integer" (§ name ßs))
                            (§ block
                                (§ try )
                                (§ block
                                    (§ ass (§ name ßr) (§ cast #_"ASN1Integer" (.. ßseq (getObjectAt 0))))
                                    (§ ass (§ name ßs) (§ cast #_"ASN1Integer" (.. ßseq (getObjectAt 1))))
                                )
                                (§ catch #_"ClassCastException" (§ name ße))
                                (§ block
                                    (§ throw (§ new #_"IllegalArgumentException" ße))
                                )
                                ;; OpenSSL deviates from the DER spec by interpreting these values as unsigned, though they should not be.
                                ;; Thus, we always use the positive versions.  See http://r6.ca/blog/20111119T211504Z.html
                                (§ return (§ new #_"ECDSASignature" (.. ßr (getPositiveValue)), (.. ßs (getPositiveValue))))
                            )
                        )
                    )
                )
                (§ catch #_"IOException" (§ name ße))
                (§ block
                    (§ throw (§ new #_"IllegalArgumentException" ße))
                )
                (§ finally )
                (§ block
                    (§ if (some? ßdecoder))
                    (§ block
                        (§ try )
                        (§ block
                            (.. ßdecoder (close))
                        )
                        (§ catch #_"IOException" (§ name _))
                        (§ block
                        )
                    )
                )
            )
        )

        #_protected
        (§ method #_"ByteArrayOutputStream" (§ fn derByteStream) [])
            (§ throws #_"IOException")
        (§ block
            ;; Usually 70-72 bytes.
            (§ let #_"ByteArrayOutputStream" (§ name ßbos) (§ new #_"ByteArrayOutputStream" 72))
            (§ block
                (§ let #_"DERSequenceGenerator" (§ name ßseq) (§ new #_"DERSequenceGenerator" ßbos))
                (§ block
                    (.. ßseq (addObject (§ new #_"ASN1Integer" r)))
                    (.. ßseq (addObject (§ new #_"ASN1Integer" s)))
                    (.. ßseq (close))
                    ßbos
                )
            )
        )

        #_override
        #_public
        (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
        (§ block
            (§ if (== this ßo))
            (§ block
                (§ return true)
            )
            (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
            (§ block
                (§ return false)
            )
            (§ let #_"ECDSASignature" (§ name ßother) (§ cast #_"ECDSASignature" ßo))
            (§ block
                (and (.. r (equals (.. ßother r))) (.. s (equals (.. ßother s))))
            )
        )

        #_override
        #_public
        (§ method #_"int" (§ fn hashCode) [])
        (§ block
            (Objects/hashCode r, s)
        )
    )

    ;;;
