     ;;
    #_public
    (§ method #_"ECDSASignature" (§ fn sign) [#_"Sha256Hash" (§ name ßinput), #_nilable #_"KeyParameter" (§ name ßaesKey)])
        (§ throws #_"KeyCrypterException")
    (§ block
        (§ let #_"KeyCrypter" (§ name ßcrypter) (getKeyCrypter))
        (§ block
            (§ if (some? ßcrypter))
            (§ block
                (§ if (nil? ßaesKey))
                (§ block
                    (§ throw (§ new #_"KeyIsEncryptedException"))
                )
                (§ return (.. (decrypt ßaesKey) (sign ßinput)))
            )
            (§ else )
            (§ block
                ;; No decryption of private key required.
                (§ if (nil? priv))
                (§ block
                    (§ throw (§ new #_"MissingPrivateKeyException"))
                )
            )
            (doSign ßinput, priv)
        )
    )

    #_protected
    (§ method #_"ECDSASignature" (§ fn doSign) [#_"Sha256Hash" (§ name ßinput), #_"BigInteger" (§ name ßprivateKeyForSigning)])
    (§ block
        (§ if FAKE_SIGNATURES)
        (§ block
            (§ return (TransactionSignature/dummy))
        )

        (Preconditions/checkNotNull ßprivateKeyForSigning)

        (§ let #_"ECDSASigner" (§ name ßsigner) (§ new #_"ECDSASigner" (§ new #_"HMacDSAKCalculator" (§ new #_"SHA256Digest"))))
        (§ block
            (§ let #_"ECPrivateKeyParameters" (§ name ßprivKey) (§ new #_"ECPrivateKeyParameters" ßprivateKeyForSigning, CURVE))
            (§ block
                (.. ßsigner (init true, ßprivKey))
                (§ let #_"BigInteger[]" (§ name ßcomponents) (.. ßsigner (generateSignature (.. ßinput (getBytes)))))
                (§ block
                    (.. (§ new #_"ECDSASignature" (§ ai ßcomponents 0), (§ ai ßcomponents 1)) (toCanonicalised))
                )
            )
        )
    )

    ;;;
     ; <p>Verifies the given ECDSA signature against the message bytes using the public key bytes.</p>
     ;
