     ; @param pub       The public key bytes to use.
     ;;
    #_public
    #_static
    (§ defn #_"boolean" (§ fn verify) [#_"byte[]" (§ name ßdata), #_"ECDSASignature" (§ name ßsignature), #_"byte[]" (§ name ßpub)])
    (§ block
        (§ if FAKE_SIGNATURES)
        (§ block
            (§ return true)
        )

        (§ let #_"ECDSASigner" (§ name ßsigner) (§ new #_"ECDSASigner"))
        (§ block
            (§ let #_"ECPublicKeyParameters" (§ name ßparams) (§ new #_"ECPublicKeyParameters" (.. CURVE (getCurve) (decodePoint ßpub)), CURVE))
            (§ block
                (.. ßsigner (init false, ßparams))
                (§ try )
                (§ block
                    (§ return (.. ßsigner (verifySignature ßdata, (.. ßsignature r), (.. ßsignature s))))
                )
                (§ catch #_"NullPointerException" (§ name ße))
