    (§ defn #_"boolean" (§ fn verify) [#_"byte[]" (§ name ßdata), #_"byte[]" (§ name ßsignature), #_"byte[]" (§ name ßpub)])
    (§ block
        (verify ßdata, (ECDSASignature/decodeFromDER ßsignature), ßpub)
