    #_public
    (§ method #_"boolean" (§ fn verify) [#_"byte[]" (§ name ßhash), #_"byte[]" (§ name ßsignature)])
    (§ block
        (ECKey/verify ßhash, ßsignature, (getPubKey))
    )

