    #_public
    (§ method #_"boolean" (§ fn verify) [#_"Sha256Hash" (§ name ßsigHash), #_"ECDSASignature" (§ name ßsignature)])
    (§ block
        (ECKey/verify (.. ßsigHash (getBytes)), ßsignature, (getPubKey))
    )

    ;;;
     ; Verifies the given ASN.1 encoded ECDSA signature against a hash using the public key, and throws an exception
     ; if the signature doesn't match.
     ; @throws java.security.SignatureException if the signature does not match.
     ;;
    #_public
    (§ method #_"void" (§ fn verifyOrThrow) [#_"byte[]" (§ name ßhash), #_"byte[]" (§ name ßsignature)])
        (§ throws #_"SignatureException")
    (§ block
        (§ if (not (verify ßhash, ßsignature)))
        (§ block
            (§ throw (§ new #_"SignatureException"))
        )
        nil
    )

