     ;;
    #_public
    (§ method #_"void" (§ fn verifyOrThrow) [#_"Sha256Hash" (§ name ßsigHash), #_"ECDSASignature" (§ name ßsignature)])
        (§ throws #_"SignatureException")
    (§ block
        (§ if (not (ECKey/verify (.. ßsigHash (getBytes)), ßsignature, (getPubKey))))
        (§ block
            (§ throw (§ new #_"SignatureException"))
        )
        nil
    )

    ;;;
     ; Returns true if the given pubkey is canonical, i.e. the correct length taking into account compression.
     ;;
    #_public
    #_static
    (§ defn #_"boolean" (§ fn isPubKeyCanonical) [#_"byte[]" (§ name ßpubkey)])
    (§ block
        (§ if (< (.. ßpubkey length) 33))
        (§ block
            (§ return false)
        )
        ;; Uncompressed pubkey.
        (§ if (== (§ ai ßpubkey 0) 0x04))
        (§ block
            (§ return (== (.. ßpubkey length) 65))
        )
        ;; Compressed pubkey.
        (§ if (or (== (§ ai ßpubkey 0) 0x02) (== (§ ai ßpubkey 0) 0x03)))
        (§ block
            (§ return (== (.. ßpubkey length) 33))
        )
        false
    )

    #_private
    #_static
    (§ defn- #_"ECKey" (§ fn extractKeyFromASN1) [#_"byte[]" (§ name ßasn1privkey)])
    (§ block
        ;; To understand this code, see the definition of the ASN.1 format for EC private keys in the OpenSSL source
        ;; code in ec_asn1.c:
        ;;
        ;; ASN1_SEQUENCE(EC_PRIVATEKEY) = {
        ;;   ASN1_SIMPLE(EC_PRIVATEKEY, version, LONG),
        ;;   ASN1_SIMPLE(EC_PRIVATEKEY, privateKey, ASN1_OCTET_STRING),
        ;;   ASN1_EXP_OPT(EC_PRIVATEKEY, parameters, ECPKPARAMETERS, 0),
        ;;   ASN1_EXP_OPT(EC_PRIVATEKEY, publicKey, ASN1_BIT_STRING, 1)
        ;; } ASN1_SEQUENCE_END(EC_PRIVATEKEY)
        (§ try )
        (§ block
            (§ let #_"ASN1InputStream" (§ name ßdecoder) (§ new #_"ASN1InputStream" ßasn1privkey))
            (§ block
                (§ let #_"DLSequence" (§ name ßseq) (§ cast #_"DLSequence" (.. ßdecoder (readObject))))
                (§ block
                    (Preconditions/checkArgument (nil? (.. ßdecoder (readObject))), "Input contains extra bytes")
                    (.. ßdecoder (close))

                    (Preconditions/checkArgument (== (.. ßseq (size)) 4), "Input does not appear to be an ASN.1 OpenSSL EC private key")

                    (Preconditions/checkArgument (.. (§ cast #_"ASN1Integer" (.. ßseq (getObjectAt 0))) (getValue) (equals BigInteger/ONE)), "Input is of wrong version")

                    (§ let #_"byte[]" (§ name ßprivbits) (.. (§ cast #_"ASN1OctetString" (.. ßseq (getObjectAt 1))) (getOctets)))
                    (§ block
                        (§ let #_"BigInteger" (§ name ßprivkey) (§ new #_"BigInteger" 1, ßprivbits))
                        (§ block

                            (§ let #_"ASN1TaggedObject" (§ name ßpubkey) (§ cast #_"ASN1TaggedObject" (.. ßseq (getObjectAt 3))))
                            (§ block
                                (Preconditions/checkArgument (== (.. ßpubkey (getTagNo)) 1), "Input has 'publicKey' with bad tag number")
                                (§ let #_"byte[]" (§ name ßpubbits) (.. (§ cast #_"DERBitString" (.. ßpubkey (getObject))) (getBytes)))
                                (§ block
                                    (Preconditions/checkArgument (or (== (.. ßpubbits length) 33) (== (.. ßpubbits length) 65)), "Input has 'publicKey' with invalid length")
                                    (§ let #_"int" (§ name ßencoding) (& 0xff (§ ai ßpubbits 0)))
                                    (§ block
                                        ;; Only allow compressed(2,3) and uncompressed(4), not infinity(0) or hybrid(6,7).
                                        (Preconditions/checkArgument (<= 2 ßencoding 4), "Input has 'publicKey' with invalid ßencoding")

                                        ;; Now sanity check to ensure the pubkey bytes match the privkey.
                                        (§ let #_"boolean" (§ name ßcompressed) (== (.. ßpubbits length) 33))
                                        (§ block
                                            (§ let #_"ECKey" (§ name ßkey) (§ new #_"ECKey" ßprivkey, nil, ßcompressed))
                                            (§ block
                                                (§ if (not (Arrays/equals (.. ßkey (getPubKey)), ßpubbits)))
                                                (§ block
                                                    (§ throw (§ new #_"IllegalArgumentException" "Public key in ASN.1 structure does not match private key."))
                                                )
                                                (§ return ßkey)
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ catch #_"IOException" (§ name ße))
        (§ block
            (§ throw (§ new #_"RuntimeException" ße)) ;; Cannot happen, reading from memory stream.
        )
    )

    ;;;
     ; Signs a text message using the standard Bitcoin messaging signing format and returns the signature as a base64
     ; encoded string.
