     ; @throws KeyCrypterException if this ECKey is encrypted and no AESKey is provided or it does not decrypt the ECKey.
     ;;
    #_public
    (§ method #_"String" (§ fn signMessage) [#_"String" (§ name ßmessage)])
        (§ throws #_"KeyCrypterException")
    (§ block
        (signMessage ßmessage, nil)
    )

    ;;;
     ; Signs a text message using the standard Bitcoin messaging signing format and returns the signature as a base64
     ; encoded string.
     ;
     ; @throws IllegalStateException if this ECKey does not have the private part.
     ; @throws KeyCrypterException if this ECKey is encrypted and no AESKey is provided or it does not decrypt the ECKey.
     ;;
    #_public
    (§ method #_"String" (§ fn signMessage) [#_"String" (§ name ßmessage), #_nilable #_"KeyParameter" (§ name ßaesKey)])
        (§ throws #_"KeyCrypterException")
    (§ block
        (§ let #_"byte[]" (§ name ßdata) (Utils/formatMessageForSigning ßmessage))
        (§ block
            (§ let #_"Sha256Hash" (§ name ßhash) (Sha256Hash/twiceOf ßdata))
            (§ block
                (§ let #_"ECDSASignature" (§ name ßsig) (sign ßhash, ßaesKey))
                (§ block
                    ;; Now we have to work backwards to figure out the recId needed to recover the signature.
                    (§ let #_"int" (§ name ßrecId) -1)
                    (§ block
                        (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi 4) :for (§ recur (+ ßi 1)))
                        (§ block
                            (§ let #_"ECKey" (§ name ßk) (ECKey/recoverFromSignature ßi, ßsig, ßhash, (isCompressed)))
                            (§ block
                                (§ if (and (some? ßk) (.. ßk pub (equals pub))))
                                (§ block
                                    (§ ass (§ name ßrecId) ßi)
                                    (§ break )
                                )
                            )
                        )
                        (§ if (== ßrecId -1))
                        (§ block
                            (§ throw (§ new #_"RuntimeException" "Could not construct a recoverable key. This should never happen."))
                        )
                        (§ let #_"int" (§ name ßheaderByte) (+ ßrecId 27 (if (isCompressed) 4 0)))
                        (§ block
                            (§ let #_"byte[]" (§ name ßsigData) (§ new #_"byte[]" (§ count 65))) ;; 1 header + 32 bytes for R + 32 bytes for S
                            (§ block
                                (§ ass (§ name (§ ai ßsigData 0)) (§ cast #_"byte" ßheaderByte))
                                (System/arraycopy (Utils/bigIntegerToBytes (.. ßsig r), 32), 0, ßsigData, 1, 32)
                                (System/arraycopy (Utils/bigIntegerToBytes (.. ßsig s), 32), 0, ßsigData, 33, 32)
                                (§ new #_"String" (Base64/encode ßsigData), (Charset/forName "UTF-8"))
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Given an arbitrary piece of text and a Bitcoin-format message signature encoded in base64, returns an ECKey
     ; containing the public key that was used to sign it.  This can then be compared to the expected public key to
     ; determine if the signature was correct.  These sorts of signatures are compatible with the Bitcoin-Qt/bitcoind
     ; format generated by signmessage/verifymessage RPCs and GUI menu options.  They are intended for humans to verify
     ; their communications with each other, hence the base64 format and the fact that the input is text.
     ;
