        (§ let #_"byte[]" (§ name ßsignatureEncoded))
        (§ block
            (§ try )
            (§ block
                (§ ass (§ name ßsignatureEncoded) (Base64/decode ßsignatureBase64))
            )
            (§ catch #_"RuntimeException" (§ name ße))
            (§ block
                ;; This is what you get back from Bouncy Castle if base64 doesn't decode :( ;; )
                (§ throw (§ new #_"SignatureException" "Could not decode base64", ße))
