                (§ let #_"BigInteger" (§ name ßr) (§ new #_"BigInteger" 1, (Arrays/copyOfRange ßsignatureEncoded, 1, 33)))
                (§ block
                    (§ let #_"BigInteger" (§ name ßs) (§ new #_"BigInteger" 1, (Arrays/copyOfRange ßsignatureEncoded, 33, 65)))
                    (§ block
                        (§ let #_"ECDSASignature" (§ name ßsig) (§ new #_"ECDSASignature" ßr, ßs))
                        (§ block
                            (§ let #_"byte[]" (§ name ßmessageBytes) (Utils/formatMessageForSigning ßmessage))
                            (§ block
                                ;; Note that the C++ code doesn't actually seem to specify any character encoding.
                                ;; Presumably it's whatever JSON-SPIRIT hands back.  Assume UTF-8 for now.
                                (§ let #_"Sha256Hash" (§ name ßmessageHash) (Sha256Hash/twiceOf ßmessageBytes))
                                (§ block
                                    (§ let #_"boolean" (§ name ßcompressed) false)
                                    (§ block
                                        (§ if (<= 31 ßheader))
                                        (§ block
                                            (§ ass (§ name ßcompressed) true)
                                            (§ ass (§ name ßheader) (- ßheader 4))
                                        )
                                        (§ let #_"int" (§ name ßrecId) (- ßheader 27))
                                        (§ block
                                            (§ let #_"ECKey" (§ name ßkey) (ECKey/recoverFromSignature ßrecId, ßsig, ßmessageHash, ßcompressed))
                                            (§ block
                                                (§ if (nil? ßkey))
                                                (§ block
                                                    (§ throw (§ new #_"SignatureException" "Could not recover public key from signature"))
                                                )
                                                ßkey
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; Convenience wrapper around {@link ECKey#signedMessageToKey(String, String)}.
     ; If the key derived from the signature is not the same as this one, throws a SignatureException.
     ;;
    #_public
    (§ method #_"void" (§ fn verifyMessage) [#_"String" (§ name ßmessage), #_"String" (§ name ßsignatureBase64)])
        (§ throws #_"SignatureException")
    (§ block
        (§ let #_"ECKey" (§ name ßkey) (ECKey/signedMessageToKey ßmessage, ßsignatureBase64))
        (§ block
            (§ if (not (.. ßkey pub (equals pub))))
            (§ block
                (§ throw (§ new #_"SignatureException" "Signature did not match for message"))
            )
            nil
        )
    )

    ;;;
     ; <p>Given the components of a signature and a selector value, recover and return the public key
     ; that generated the signature according to the algorithm in SEC1v2 section 4.1.6.</p>
     ;
     ; <p>The recId is an index from 0 to 3 which indicates which of the 4 possible keys is the correct one.  Because
     ; the key recovery operation yields multiple potential keys, the correct key must either be stored alongside the
     ; signature, or you must be willing to try each recId in turn until you find one that outputs the key you are
     ; expecting.</p>
     ;
     ; <p>If this method returns null it means recovery was not possible and recId should be iterated.</p>
     ;
     ; <p>Given the above two points, a correct usage of this method is inside a for loop from 0 to 3, and if the
     ; output is null OR a key that is not the one you expect, you try again with the next recId.</p>
     ;
     ; @param recId Which possible key to recover.
     ; @param sig The R and S components of the signature, wrapped.
     ; @param message Hash of the data that was signed.
     ; @param compressed Whether or not the original pubkey was compressed.
     ; @return An ECKey containing only the public part, or null if recovery wasn't possible.
     ;;
    #_nilable
    #_public
    #_static
    (§ defn #_"ECKey" (§ fn recoverFromSignature) [#_"int" (§ name ßrecId), #_"ECDSASignature" (§ name ßsig), #_"Sha256Hash" (§ name ßmessage), #_"boolean" (§ name ßcompressed)])
    (§ block
        (Preconditions/checkArgument (<= 0 ßrecId), "recId must be positive")
        (Preconditions/checkArgument (<= 0 (.. ßsig r (signum))), "r must be positive")
        (Preconditions/checkArgument (<= 0 (.. ßsig s (signum))), "s must be positive")
        (Preconditions/checkNotNull ßmessage)

        ;; 1.0 For j from 0 to h   (h == recId here and the loop is outside this function)
        ;;   1.1 Let x = r + jn
        (§ let #_"BigInteger" (§ name ßn) (.. CURVE (getN))) ;; Curve order.
        (§ block
            (§ let #_"BigInteger" (§ name ßi) (BigInteger/valueOf (/ (§ cast #_"long" ßrecId) 2)))
            (§ block
                (§ let #_"BigInteger" (§ name ßx) (.. ßsig r (add (.. ßi (multiply ßn)))))
                (§ block
                    ;;   1.2. Convert the integer x to an octet string X of length mlen using the conversion routine
                    ;;        specified in Section 2.3.7, where mlen = ⌈(log2 p)/8⌉ or mlen = ⌈m/8⌉.
                    ;;   1.3. Convert the octet string (16 set binary digits)||X to an elliptic curve point R using the
                    ;;        conversion routine specified in Section 2.3.4. If this conversion routine outputs “invalid”,
                    ;;        then do another iteration of Step 1.
                    ;;
                    ;; More concisely, what these points mean is to use X as a compressed public key.
                    (§ let #_"BigInteger" (§ name ßprime) SecP256K1Curve/q)
                    (§ block
                        ;; Cannot have point co-ordinates larger than this as everything takes place modulo Q.
                        (§ if (<= 0 (.. ßx (compareTo ßprime))))
                        (§ block
                            (§ return nil)
                        )

                        ;; Compressed keys require you to know an extra bit of data about the y-coord as there are two possibilities.
                        ;; So it's encoded in the recId.
                        (§ let #_"ECPoint" (§ name ßR) (decompressKey ßx, (== (& ßrecId 1) 1)))
                        (§ block
                            ;;   1.4. If nR != point at infinity, then do another iteration of Step 1 (callers responsibility).
                            (§ if (not (.. ßR (multiply ßn) (isInfinity))))
                            (§ block
                                (§ return nil)
                            )

                            ;;   1.5. Compute e from M using Steps 2 and 3 of ECDSA signature verification.
                            (§ let #_"BigInteger" (§ name ße) (.. ßmessage (toBigInteger)))
                            (§ block
                                ;;   1.6. For k from 1 to 2 do the following.   (loop is outside this function via iterating recId)
                                ;;   1.6.1. Compute a candidate public key as:
                                ;;               Q = mi(r) * (sR - eG)
                                ;;
                                ;; Where mi(x) is the modular multiplicative inverse. We transform this into the following:
                                ;;               Q = (mi(r) * s ** R) + (mi(r) * -e ** G)
                                ;; Where -e is the modular additive inverse of e, that is z such that z + e = 0 (mod n). In the above equation
                                ;; ** is point multiplication and + is point addition (the EC group operator).
                                ;;
                                ;; We can find the additive inverse by subtracting e from zero then taking the mod. For example the additive
                                ;; inverse of 3 modulo 11 is 8 because 3 + 8 mod 11 = 0, and -3 mod 11 = 8.
                                (§ let #_"BigInteger" (§ name ßeInv) (.. BigInteger/ZERO (subtract ße) (mod ßn)))
                                (§ block
                                    (§ let #_"BigInteger" (§ name ßrInv) (.. ßsig r (modInverse ßn)))
                                    (§ block
                                        (§ let #_"BigInteger" (§ name ßsrInv) (.. ßrInv (multiply (.. ßsig s)) (mod ßn)))
                                        (§ block
                                            (§ let #_"BigInteger" (§ name ßeInvrInv) (.. ßrInv (multiply ßeInv) (mod ßn)))
                                            (§ block
                                                (§ let #_"ECPoint" (§ name ßq) (ECAlgorithms/sumOfTwoMultiplies (.. CURVE (getG)), ßeInvrInv, ßR, ßsrInv))
                                                (§ block
                                                    (ECKey/fromPublicOnly (.. ßq (getEncoded ßcompressed)))
                                                )
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
    )

    ;;; Decompress a compressed public key (x co-ord and low-bit of y-coord). ;;
    #_private
    #_static
    (§ defn- #_"ECPoint" (§ fn decompressKey) [#_"BigInteger" (§ name ßxBN), #_"boolean" (§ name ßyBit)])
    (§ block
        (§ let #_"X9IntegerConverter" (§ name ßx9) (§ new #_"X9IntegerConverter"))
        (§ block
            (§ let #_"byte[]" (§ name ßcompEnc) (.. ßx9 (integerToBytes ßxBN, (+ 1 (.. ßx9 (getByteLength (.. CURVE (getCurve))))))))
            (§ block
                (§ ass (§ name (§ ai ßcompEnc 0)) (§ cast #_"byte" (if ßyBit 0x03 0x02)))
                (.. CURVE (getCurve) (decodePoint ßcompEnc))
            )
        )
    )

    ;;;
     ; Returns a 32 byte array containing the private key.
     ; @throws org.bitcoinj.core.ECKey.MissingPrivateKeyException if the private key bytes are missing/encrypted.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getPrivKeyBytes) [])
    (§ block
        (Utils/bigIntegerToBytes (getPrivKey), 32)
    )

    ;;;
     ; Returns the creation time of this key or zero if the key was deserialized from a version that did not store
     ; that data.
     ;;
    #_override
    #_public
    (§ method #_"long" (§ fn getCreationTimeSeconds) [])
    (§ block
        creationTimeSeconds
    )

    ;;;
     ; Sets the creation time of this key.  Zero is a convention to mean "unavailable".  This method can be useful when
     ; you have a raw key you are importing from somewhere else.
     ;;
    #_public
    (§ method #_"void" (§ fn setCreationTimeSeconds) [#_"long" (§ name ßnewCreationTimeSeconds)])
    (§ block
        (§ if (< ßnewCreationTimeSeconds 0))
        (§ block
            (§ throw (§ new #_"IllegalArgumentException" (str "Cannot set creation time to negative value: " ßnewCreationTimeSeconds)))
        )
        (§ ass (§ name creationTimeSeconds) ßnewCreationTimeSeconds)
        nil
    )

    ;;;
     ; Create an encrypted private key with the keyCrypter and the AES key supplied.
     ; This method returns a new encrypted key and leaves the original unchanged.
     ;
     ; @param keyCrypter The keyCrypter that specifies exactly how the encrypted bytes are created.
     ; @param aesKey The KeyParameter with the AES encryption key (usually constructed with keyCrypter#deriveKey and cached as it is slow to create).
     ; @return encryptedKey
     ;;
    #_public
    (§ method #_"ECKey" (§ fn encrypt) [#_"KeyCrypter" (§ name ßkeyCrypter), #_"KeyParameter" (§ name ßaesKey)])
        (§ throws #_"KeyCrypterException")
    (§ block
        (Preconditions/checkNotNull ßkeyCrypter)

        (§ let #_"byte[]" (§ name ßprivKeyBytes) (getPrivKeyBytes))
        (§ block
            (§ let #_"EncryptedData" (§ name ßencryptedPrivateKey) (.. ßkeyCrypter (encrypt ßprivKeyBytes, ßaesKey)))
            (§ block
                (§ let #_"ECKey" (§ name ßresult) (ECKey/fromEncrypted ßencryptedPrivateKey, ßkeyCrypter, (getPubKey)))
                (§ block
                    (.. ßresult (setCreationTimeSeconds creationTimeSeconds))
                    ßresult
                )
            )
        )
    )

    ;;;
     ; Create a decrypted private key with the keyCrypter and AES key supplied.  Note that if the aesKey is wrong, this
     ; has some chance of throwing KeyCrypterException due to the corrupted padding that will result, but it can also
     ; just yield a garbage key.
     ;
     ; @param keyCrypter The keyCrypter that specifies exactly how the decrypted bytes are created.
     ; @param aesKey The KeyParameter with the AES encryption key (usually constructed with keyCrypter#deriveKey and cached).
     ;;
    #_public
    (§ method #_"ECKey" (§ fn decrypt) [#_"KeyCrypter" (§ name ßkeyCrypter), #_"KeyParameter" (§ name ßaesKey)])
        (§ throws #_"KeyCrypterException")
    (§ block
        (Preconditions/checkNotNull ßkeyCrypter)

        ;; Check that the keyCrypter matches the one used to encrypt the keys, if set.
        (§ if (and (some? (.. this ßkeyCrypter)) (not (.. this ßkeyCrypter (equals ßkeyCrypter)))))
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "The keyCrypter being used to decrypt the key is different to the one that was used to encrypt it"))
        )

        (Preconditions/checkState (some? encryptedPrivateKey), "This key is not encrypted")

        (§ let #_"byte[]" (§ name ßunencryptedPrivateKey) (.. ßkeyCrypter (decrypt encryptedPrivateKey, ßaesKey)))
        (§ block
            (§ let #_"ECKey" (§ name ßkey) (ECKey/fromPrivate ßunencryptedPrivateKey))
            (§ block
                (§ if (not (isCompressed)))
                (§ block
                    (§ ass (§ name ßkey) (.. ßkey (decompress)))
                )
                (§ if (not (Arrays/equals (.. ßkey (getPubKey)), (getPubKey))))
                (§ block
                    (§ throw (§ new #_"KeyCrypterException" "Provided AES key is wrong"))
                )

                (.. ßkey (setCreationTimeSeconds creationTimeSeconds))
                ßkey
            )
        )
    )

    ;;;
     ; Create a decrypted private key with AES key.  Note that if the AES key is wrong, this
     ; has some chance of throwing KeyCrypterException due to the corrupted padding that will result, but it can also
     ; just yield a garbage key.
     ;
     ; @param aesKey The KeyParameter with the AES encryption key (usually constructed with keyCrypter#deriveKey and cached).
     ;;
    #_public
    (§ method #_"ECKey" (§ fn decrypt) [#_"KeyParameter" (§ name ßaesKey)])
        (§ throws #_"KeyCrypterException")
    (§ block
        (§ let #_"KeyCrypter" (§ name ßcrypter) (getKeyCrypter))
        (§ block
            (§ if (nil? ßcrypter))
            (§ block
                (§ throw (§ new #_"KeyCrypterException" "No key crypter available"))
            )

            (decrypt ßcrypter, ßaesKey)
        )
    )

    ;;;
     ; Creates decrypted private key if needed.
     ;;
    #_public
    (§ method #_"ECKey" (§ fn maybeDecrypt) [#_nilable #_"KeyParameter" (§ name ßaesKey)])
        (§ throws #_"KeyCrypterException")
    (§ block
        (if (and (isEncrypted) (some? ßaesKey)) (decrypt ßaesKey) this)
    )

    ;;;
     ; <p>Check that it is possible to decrypt the key with the keyCrypter and that the original key is returned.</p>
     ;
     ; <p>Because it is a critical failure if the private keys cannot be decrypted successfully (resulting of loss of
     ; all bitcoins controlled by the private key) you can use this method to check when you *encrypt* a wallet that
     ; it can definitely be decrypted successfully.</p>
     ;
     ; <p>See {@link Wallet#encrypt(KeyCrypter keyCrypter, KeyParameter aesKey)} for example usage.</p>
     ;
     ; @return true if the encrypted key can be decrypted back to the original key successfully.
     ;;
    #_public
    #_static
    (§ defn #_"boolean" (§ fn encryptionIsReversible) [#_"ECKey" (§ name ßoriginalKey), #_"ECKey" (§ name ßencryptedKey), #_"KeyCrypter" (§ name ßkeyCrypter), #_"KeyParameter" (§ name ßaesKey)])
    (§ block
        (§ try )
        (§ block
            (§ let #_"ECKey" (§ name ßrebornUnencryptedKey) (.. ßencryptedKey (decrypt ßkeyCrypter, ßaesKey)))
            (§ block
                (§ let #_"byte[]" (§ name ßoriginalPrivateKeyBytes) (.. ßoriginalKey (getPrivKeyBytes)))
                (§ block
                    (§ let #_"byte[]" (§ name ßrebornKeyBytes) (.. ßrebornUnencryptedKey (getPrivKeyBytes)))
                    (§ block
                        (§ if (not (Arrays/equals ßoriginalPrivateKeyBytes, ßrebornKeyBytes)))
                        (§ block
                            (.. log (error "The check that encryption could be reversed failed for {}", ßoriginalKey))
                            (§ return false)
                        )
                        (§ return true)
                    )
                )
            )
        )
        (§ catch #_"KeyCrypterException" (§ name ßkce))
        (§ block
            (.. log (error (.. ßkce (getMessage))))
            (§ return false)
        )
    )

    ;;;
     ; Indicates whether the private key is encrypted (true) or not (false).
     ; A private key is deemed to be encrypted when there is both a KeyCrypter and the encryptedPrivateKey is non-zero.
     ;;
    #_override
    #_public
    (§ method #_"boolean" (§ fn isEncrypted) [])
    (§ block
        (and (some? keyCrypter) (some? encryptedPrivateKey) (< 0 (.. encryptedPrivateKey encryptedBytes length)))
    )

    #_nilable
    #_override
    #_public
    (§ method #_"Protos.Wallet.EncryptionType" (§ fn getEncryptionType) [])
    (§ block
        (if (some? keyCrypter) (.. keyCrypter (getUnderstoodEncryptionType)) Protos/Wallet/EncryptionType/UNENCRYPTED)
    )

    ;;;
     ; A wrapper for {@link #getPrivKeyBytes()} that returns null if the private key bytes are missing or would have
     ; to be derived (for the HD key case).
     ;;
    #_override
    #_nilable
    #_public
    (§ method #_"byte[]" (§ fn getSecretBytes) [])
    (§ block
        (§ if (hasPrivKey))
        (§ block
            (§ return (getPrivKeyBytes))
        )

        nil
    )

    ;;; An alias for {@link #getEncryptedPrivateKey()}. ;;
    #_nilable
    #_override
    #_public
    (§ method #_"EncryptedData" (§ fn getEncryptedData) [])
    (§ block
        (getEncryptedPrivateKey)
    )

    ;;;
     ; Returns the the encrypted private key bytes and initialisation vector for this ECKey, or null if the ECKey
     ; is not encrypted.
     ;;
    #_nilable
    #_public
    (§ method #_"EncryptedData" (§ fn getEncryptedPrivateKey) [])
    (§ block
        encryptedPrivateKey
    )

    ;;;
     ; Returns the KeyCrypter that was used to encrypt to encrypt this ECKey.  You need this to decrypt the ECKey.
     ;;
    #_nilable
    #_public
    (§ method #_"KeyCrypter" (§ fn getKeyCrypter) [])
    (§ block
        keyCrypter
    )

    #_public
    #_static
    (§ class #_"MissingPrivateKeyException" (§ extends #_"RuntimeException")
    )

    #_public
    #_static
    (§ class #_"KeyIsEncryptedException" (§ extends #_"MissingPrivateKeyException")
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (not (§ insta ßo #_"ECKey"))))
        (§ block
            (§ return false)
        )
        (§ let #_"ECKey" (§ name ßother) (§ cast #_"ECKey" ßo))
        (§ block
            (and (Objects/equal (.. this priv), (.. ßother priv)) (Objects/equal (.. this pub), (.. ßother pub)) (Objects/equal (.. this creationTimeSeconds), (.. ßother creationTimeSeconds)) (Objects/equal (.. this keyCrypter), (.. ßother keyCrypter)) (Objects/equal (.. this encryptedPrivateKey), (.. ßother encryptedPrivateKey)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (.. pub (hashCode))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (toString false, nil)
    )

    ;;;
     ; Produce a string rendering of the ECKey INCLUDING the private key.
     ; Unless you absolutely need the private key it is better for security reasons to just use {@link #toString()}.
     ;;
    #_public
    (§ method #_"String" (§ fn toStringWithPrivate) [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (toString true, ßparams)
    )

    #_public
    (§ method #_"String" (§ fn getPrivateKeyAsHex) [])
    (§ block
        (.. Utils/HEX (encode (getPrivKeyBytes)))
    )

    #_public
    (§ method #_"String" (§ fn getPublicKeyAsHex) [])
    (§ block
        (.. Utils/HEX (encode (.. pub (getEncoded))))
    )

    #_private
    (§ method- #_"String" (§ fn toString) [#_"boolean" (§ name ßincludePrivate), #_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ let #_"MoreObjects.ToStringHelper" (§ name ßhelper) (.. (MoreObjects/toStringHelper this) (omitNullValues)))
        (§ block
            (.. ßhelper (add "pub HEX", (getPublicKeyAsHex)))
            (§ if ßincludePrivate)
            (§ block
                (§ try )
                (§ block
                    (.. ßhelper (add "priv HEX", (getPrivateKeyAsHex)))
                )
                (§ catch #_"IllegalStateException" (§ name _))
                (§ block
                    ;; TODO: Make hasPrivKey() work for deterministic keys and fix this.
                )
                (§ catch #_"Exception" (§ name ße))
                (§ block
                    (§ let #_"String" (§ name ßmessage) (.. ße (getMessage)))
                    (§ block
                        (.. ßhelper (add "priv EXCEPTION", (str (.. ße (getClass) (getName)) (if (some? ßmessage) (str ": " ßmessage) ""))))
                    )
                )
            )
            (§ if (< 0 creationTimeSeconds))
            (§ block
                (.. ßhelper (add "creationTimeSeconds", creationTimeSeconds))
            )
            (.. ßhelper (add "keyCrypter", keyCrypter))
            (§ if ßincludePrivate)
            (§ block
                (.. ßhelper (add "encryptedPrivateKey", encryptedPrivateKey))
            )
            (.. ßhelper (add "isEncrypted", (isEncrypted)))
            (.. ßhelper (add "isPubKeyOnly", (isPubKeyOnly)))
            (.. ßhelper (toString))
        )
    )

    #_public
    (§ method #_"void" (§ fn formatKeyWithAddress) [#_"boolean" (§ name ßincludePrivateKeys), #_"StringBuilder" (§ name ßsb), #_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ let #_"Address" (§ name ßaddress) (toAddress ßparams))
        (§ block
            (.. ßsb (append "  addr:"))
            (.. ßsb (append (.. ßaddress (toString))))
            (.. ßsb (append "  hash160:"))
            (.. ßsb (append (.. Utils/HEX (encode (getPubKeyHash)))))
            (§ if (< 0 creationTimeSeconds))
            (§ block
                (.. ßsb (append "  creationTimeSeconds:") (append creationTimeSeconds))
            )
            (.. ßsb (append "\n"))
            (§ if ßincludePrivateKeys)
            (§ block
                (.. ßsb (append "  "))
                (.. ßsb (append (toStringWithPrivate ßparams)))
                (.. ßsb (append "\n"))
            )
            nil
        )
    )
)

#_(ns org.bitcoinj.core #_"EmptyMessage"
    (:import [java.io IOException OutputStream]))

;;;
 ; <p>Parent class for header only messages that don't have a payload.
 ; Currently this includes getaddr, verack and special bitcoinj class UnknownMessage.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
#_abstract
(§ class #_"EmptyMessage" (§ extends #_"Message")
    #_public
    (§ constructor #_"EmptyMessage" [])
    (§ block
        (§ ass (§ name length) 0)
        this
    )

    #_public
    (§ constructor #_"EmptyMessage" [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ super ßparams)
        (§ ass (§ name length) 0)
        this
    )

    #_public
    (§ constructor #_"EmptyMessage" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayload), #_"int" (§ name ßoffset)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayload, ßoffset)
        (§ ass (§ name length) 0)
        this
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        nil
    )

    #_override
    #_public
    (§ method #_"byte[]" (§ fn bitcoinSerialize) [])
    (§ block
        (§ new #_"byte[]" (§ count 0))
    )
)

#_(ns org.bitcoinj.core #_"FilteredBlock"
    (:import [java.io IOException OutputStream]
             [java.util *])
    (:import [com.google.common.base Objects]))

;;;
 ; <p>A FilteredBlock is used to relay a block with its transactions filtered using a {@link BloomFilter}.  It consists
 ; of the block header and a {@link PartialMerkleTree} which contains the transactions which matched the filter.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"FilteredBlock" (§ extends #_"Message")
    #_private
    (§ field- #_"Block" (§ name header))

    #_private
    (§ field- #_"PartialMerkleTree" (§ name merkleTree))
    #_private
    (§ field- #_"List<Sha256Hash>" (§ name cachedTransactionHashes))

    ;; A set of transactions whose hashes are a subset of getTransactionHashes().
    ;; These were relayed as a part of the filteredblock getdata, i.e. likely weren't previously received as loose transactions.
    #_private
    (§ field- #_"Map<Sha256Hash, Transaction>" (§ name associatedTransactions) (§ new #_"HashMap<>"))

    #_public
    (§ constructor #_"FilteredBlock" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayloadBytes)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayloadBytes, 0)
        this
    )

    #_public
    (§ constructor #_"FilteredBlock" [#_"NetworkParameters" (§ name ßparams), #_"Block" (§ name ßheader), #_"PartialMerkleTree" (§ name ßpmt)])
    (§ block
        (§ super ßparams)
        (§ ass (§ name (.. this ßheader)) ßheader)
        (§ ass (§ name (.. this merkleTree)) ßpmt)
        this
    )

    #_override
    #_public
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        (§ if (== (.. header transactions) nil))
        (§ block
            (.. header (bitcoinSerializeToStream ßstream))
        )
        (§ else )
        (§ block
            (.. header (cloneAsHeader) (bitcoinSerializeToStream ßstream))
        )
        (.. merkleTree (bitcoinSerializeToStream ßstream))
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ let #_"byte[]" (§ name ßheaderBytes) (§ new #_"byte[]" (§ count Block/HEADER_SIZE)))
        (§ block
            (System/arraycopy payload, 0, ßheaderBytes, 0, Block/HEADER_SIZE)
            (§ ass (§ name header) (.. params (getDefaultSerializer) (makeBlock ßheaderBytes)))

            (§ ass (§ name merkleTree) (§ new #_"PartialMerkleTree" params, payload, Block/HEADER_SIZE))

            (§ ass (§ name length) (+ Block/HEADER_SIZE (.. merkleTree (getMessageSize))))
            nil
        )
    )

    ;;;
     ; Gets a list of leaf hashes which are contained in the partial merkle tree in this filtered block.
     ;
     ; @throws ProtocolException if the partial merkle block is invalid or the merkle root of the partial merkle block doesnt match the block header.
     ;;
    #_public
    (§ method #_"List<Sha256Hash>" (§ fn getTransactionHashes) [])
        (§ throws #_"VerificationException")
    (§ block
        (§ if (some? cachedTransactionHashes))
        (§ block
            (§ return (Collections/unmodifiableList cachedTransactionHashes))
        )

        (§ let #_"List<Sha256Hash>" (§ name ßhashesMatched) (§ new #_"LinkedList<>"))
        (§ block
            (§ if (.. header (getMerkleRoot) (equals (.. merkleTree (getTxnHashAndMerkleRoot ßhashesMatched)))))
            (§ block
                (§ ass (§ name cachedTransactionHashes) ßhashesMatched)
                (§ return (Collections/unmodifiableList cachedTransactionHashes))
            )

            (§ throw (§ new #_"VerificationException" "Merkle root of block header does not match merkle root of partial merkle tree."))
        )
    )

    ;;;
     ; Gets a copy of the block header.
     ;;
    #_public
    (§ method #_"Block" (§ fn getBlockHeader) [])
    (§ block
        (.. header (cloneAsHeader))
    )

    ;;; Gets the hash of the block represented in this FilteredBlock. ;;
    #_override
    #_public
    (§ method #_"Sha256Hash" (§ fn getHash) [])
    (§ block
        (.. header (getHash))
    )

    ;;;
     ; Provide this FilteredBlock with a transaction which is in its Merkle tree.
     ; @return false if the tx is not relevant to this FilteredBlock.
     ;;
    #_public
    (§ method #_"boolean" (§ fn provideTransaction) [#_"Transaction" (§ name ßtx)])
        (§ throws #_"VerificationException")
    (§ block
        (§ let #_"Sha256Hash" (§ name ßhash) (.. ßtx (getHash)))
        (§ block
            (§ if (.. (getTransactionHashes) (contains ßhash)))
            (§ block
                (.. associatedTransactions (put ßhash, ßtx))
                (§ return true)
            )
            false
        )
    )

    ;;; Returns the {@link PartialMerkleTree} object that provides the mathematical proof of transaction inclusion in the block. ;;
    #_public
    (§ method #_"PartialMerkleTree" (§ fn getPartialMerkleTree) [])
    (§ block
        merkleTree
    )

    ;;; Gets the set of transactions which were provided using provideTransaction() which match in getTransactionHashes(). ;;
    #_public
    (§ method #_"Map<Sha256Hash, Transaction>" (§ fn getAssociatedTransactions) [])
    (§ block
        (Collections/unmodifiableMap associatedTransactions)
    )

    ;;; Number of transactions in this block, before it was filtered. ;;
    #_public
    (§ method #_"int" (§ fn getTransactionCount) [])
    (§ block
        (.. merkleTree (getTransactionCount))
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"FilteredBlock" (§ name ßother) (§ cast #_"FilteredBlock" ßo))
        (§ block
            (and (.. associatedTransactions (equals (.. ßother associatedTransactions))) (.. header (equals (.. ßother header))) (.. merkleTree (equals (.. ßother merkleTree))))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode associatedTransactions, header, merkleTree)
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "FilteredBlock{merkleTree=" merkleTree ", header=" header "}")
    )
)

#_(ns org.bitcoinj.core #_"FullPrunedBlockChain"
    (:import [java.util ArrayList LinkedList List ListIterator Set]
             [java.util.concurrent *])
    (:import [com.google.common.base Preconditions]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.script Script]
             [org.bitcoinj.script.Script VerifyFlag]
             [org.bitcoinj.store BlockStoreException FullPrunedBlockStore]
             [org.bitcoinj.utils *]
             [org.bitcoinj.wallet Wallet]))

;;;
 ; <p>A FullPrunedBlockChain works in conjunction with a {@link FullPrunedBlockStore} to verify all the rules of the
 ; Bitcoin system, with the downside being a large cost in system resources.  Fully verifying means all unspent
 ; transaction outputs are stored.  Once a transaction output is spent and that spend is buried deep enough, the data
 ; related to it is deleted to ensure disk space usage doesn't grow forever.  For this reason a pruning node cannot
 ; serve the full block chain to other clients, but it nevertheless provides the same security guarantees as Bitcoin
 ; Core does.</p>
 ;;
#_public
(§ class #_"FullPrunedBlockChain" (§ extends #_"AbstractBlockChain")
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"FullPrunedBlockChain")))

    ;;;
     ; Keeps a map of block hashes to StoredBlocks.
     ;;
    #_protected
    (§ field #_"FullPrunedBlockStore" (§ name blockStore))

    ;; Whether or not to execute scriptPubKeys before accepting a transaction (i.e. check signatures).
    #_private
    (§ field- #_"boolean" (§ name runScripts) true)

    ;;;
     ; Constructs a block chain connected to the given wallet and store.  To obtain a {@link Wallet} you can construct
     ; one from scratch, or you can deserialize a saved wallet from disk using {@link Wallet#loadFromFile(java.io.File)}.
     ;;
    #_public
    (§ constructor #_"FullPrunedBlockChain" [#_"Context" (§ name ßcontext), #_"Wallet" (§ name ßwallet), #_"FullPrunedBlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this ßcontext, (§ new #_"ArrayList<Wallet>"), ßblockStore)
        (addWallet ßwallet)
        this
    )

    ;;;
     ; Constructs a block chain connected to the given wallet and store.  To obtain a {@link Wallet} you can construct
     ; one from scratch, or you can deserialize a saved wallet from disk using {@link Wallet#loadFromFile(java.io.File)}.
     ;;
    #_public
    (§ constructor #_"FullPrunedBlockChain" [#_"NetworkParameters" (§ name ßparams), #_"Wallet" (§ name ßwallet), #_"FullPrunedBlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this (Context/getOrCreate ßparams), ßwallet, ßblockStore)
        this
    )

    ;;;
     ; Constructs a block chain connected to the given store.
     ;;
    #_public
    (§ constructor #_"FullPrunedBlockChain" [#_"Context" (§ name ßcontext), #_"FullPrunedBlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this ßcontext, (§ new #_"ArrayList<Wallet>"), ßblockStore)
        this
    )

    ;;;
     ; See {@link #FullPrunedBlockChain(Context, Wallet, FullPrunedBlockStore)}.
     ;;
    #_public
    (§ constructor #_"FullPrunedBlockChain" [#_"NetworkParameters" (§ name ßparams), #_"FullPrunedBlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this (Context/getOrCreate ßparams), ßblockStore)
        this
    )

    ;;;
     ; Constructs a block chain connected to the given list of wallets and a store.
     ;;
    #_public
    (§ constructor #_"FullPrunedBlockChain" [#_"Context" (§ name ßcontext), #_"List<Wallet>" (§ name ßlisteners), #_"FullPrunedBlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ super ßcontext, ßlisteners, ßblockStore)
        (§ ass (§ name (.. this ßblockStore)) ßblockStore)
        ;; Ignore upgrading for now.
        (§ ass (§ name (.. this chainHead)) (.. ßblockStore (getVerifiedChainHead)))
        this
    )

    ;;;
     ; See {@link #FullPrunedBlockChain(Context, List, FullPrunedBlockStore)}.
     ;;
    #_public
    (§ constructor #_"FullPrunedBlockChain" [#_"NetworkParameters" (§ name ßparams), #_"List<Wallet>" (§ name ßlisteners), #_"FullPrunedBlockStore" (§ name ßblockStore)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ this (Context/getOrCreate ßparams), ßlisteners, ßblockStore)
        this
    )

    #_override
    #_protected
    (§ method #_"StoredBlock" (§ fn addToBlockStore) [#_"StoredBlock" (§ name ßstoredPrev), #_"Block" (§ name ßheader), #_"TransactionOutputChanges" (§ name ßtxOutChanges)])
        (§ throws #_"BlockStoreException", #_"VerificationException")
    (§ block
        (§ let #_"StoredBlock" (§ name ßnewBlock) (.. ßstoredPrev (build ßheader)))
        (§ block
            (.. blockStore (put ßnewBlock, (§ new #_"StoredUndoableBlock" (.. ßnewBlock (getHeader) (getHash)), ßtxOutChanges)))
            ßnewBlock
        )
    )

    #_override
    #_protected
    (§ method #_"StoredBlock" (§ fn addToBlockStore) [#_"StoredBlock" (§ name ßstoredPrev), #_"Block" (§ name ßblock)])
        (§ throws #_"BlockStoreException", #_"VerificationException")
    (§ block
        (§ let #_"StoredBlock" (§ name ßnewBlock) (.. ßstoredPrev (build ßblock)))
        (§ block
            (.. blockStore (put ßnewBlock, (§ new #_"StoredUndoableBlock" (.. ßnewBlock (getHeader) (getHash)), (.. ßblock transactions))))
            ßnewBlock
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn rollbackBlockStore) [#_"int" (§ name ßheight)])
        (§ throws #_"BlockStoreException")
    (§ block
        (§ throw (§ new #_"BlockStoreException" "Unsupported"))
    )

    #_override
    #_protected
    (§ method #_"boolean" (§ fn shouldVerifyTransactions) [])
    (§ block
        true
    )

    ;;;
     ; Whether or not to run scripts whilst accepting blocks (i.e. checking signatures, for most transactions).
     ; If you're accepting data from an untrusted node, such as one found via the P2P network, this should be set
     ; to true (which is the default).  If you're downloading a chain from a node you control, script execution
     ; is redundant because you know the connected node won't relay bad data to you.  In that case it's safe to set
     ; this to false and obtain a significant speedup.
     ;;
    #_public
    (§ method #_"void" (§ fn setRunScripts) [#_"boolean" (§ name ßvalue)])
    (§ block
        (§ ass (§ name (.. this runScripts)) ßvalue)
        nil
    )

    ;; TODO: Remove lots of duplicated code in the two connectTransactions.

    ;; TODO: Execute in order of largest transaction (by input count) first.
    (§ field #_"ExecutorService" (§ name scriptVerificationExecutor) (Executors/newFixedThreadPool (.. (Runtime/getRuntime) (availableProcessors)), (§ new #_"ContextPropagatingThreadFactory" "Script verification")))

    ;;;
     ; A job submitted to the executor which verifies signatures.
     ;;
    #_private
    #_static
    (§ class #_"Verifier" (§ implements #_"Callable<VerificationException>")
        (§ field #_"Transaction" (§ name tx))
        (§ field #_"List<Script>" (§ name prevOutScripts))
        (§ field #_"Set<VerifyFlag>" (§ name verifyFlags))

        #_public
        (§ constructor #_"Verifier" [#_"Transaction" (§ name ßtx), #_"List<Script>" (§ name ßprevOutScripts), #_"Set<VerifyFlag>" (§ name ßverifyFlags)])
        (§ block
            (§ ass (§ name (.. this ßtx)) ßtx)
            (§ ass (§ name (.. this ßprevOutScripts)) ßprevOutScripts)
            (§ ass (§ name (.. this ßverifyFlags)) ßverifyFlags)
            this
        )

        #_nilable
        #_override
        #_public
        (§ method #_"VerificationException" (§ fn call) [])
            (§ throws #_"Exception")
        (§ block
            (§ try )
            (§ block
                (§ let #_"ListIterator<Script>" (§ name ßprevOutIt) (.. prevOutScripts (listIterator)))
                (§ block
                    (§ for (§ var #_"int" (§ name ßindex) 0) :for (< ßindex (.. tx (getInputs) (size))) :for (§ recur (+ ßindex 1)))
                    (§ block
                        (.. tx (getInputs) (get ßindex) (getScriptSig) (correctlySpends tx, ßindex, (.. ßprevOutIt (next)), verifyFlags))
                    )
                    (§ return nil)
                )
            )
            (§ catch #_"VerificationException" (§ name ße))
            (§ block
                (§ return ße)
            )
        )
    )

    ;;;
     ; Get the {@link Script} from the script bytes or return Script of empty byte array.
     ;;
    #_private
    (§ method- #_"Script" (§ fn getScript) [#_"byte[]" (§ name ßscriptBytes)])
    (§ block
        (§ try )
        (§ block
            (§ return (§ new #_"Script" ßscriptBytes))
        )
        (§ catch #_"Exception" (§ name _))
        (§ block
            (§ return (§ new #_"Script" (§ new #_"byte[]" (§ count 0))))
        )
    )

    ;;;
     ; Get the address from the {@link Script} if it exists, otherwise return empty string "".
     ;
     ; @param script The script.
     ; @return The address.
     ;;
    #_private
    (§ method- #_"String" (§ fn getScriptAddress) [#_nilable #_"Script" (§ name ßscript)])
    (§ block
        (§ let #_"String" (§ name ßaddress) "")
        (§ block
            (§ try )
            (§ block
                (§ if (some? ßscript))
                (§ block
                    (§ ass (§ name ßaddress) (.. ßscript (getToAddress params, true) (toString)))
                )
            )
            (§ catch #_"Exception" (§ name _))
            (§ block
            )
            ßaddress
        )
    )

    #_override
    #_protected
    (§ method #_"TransactionOutputChanges" (§ fn connectTransactions) [#_"int" (§ name ßheight), #_"Block" (§ name ßblock)])
        (§ throws #_"VerificationException", #_"BlockStoreException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (§ if (== (.. ßblock transactions) nil))
        (§ block
            (§ throw (§ new #_"RuntimeException" "connectTransactions called with Block that didn't have transactions!"))
        )
        (§ if (not (.. params (passesCheckpoint ßheight, (.. ßblock (getHash))))))
        (§ block
            (§ throw (§ new #_"VerificationException" (str "Block failed checkpoint lockin at " ßheight)))
        )

        (.. blockStore (beginDatabaseBatchWrite))

        (§ let #_"LinkedList<UTXO>" (§ name ßtxOutsSpent) (§ new #_"LinkedList<>"))
        (§ block
            (§ let #_"LinkedList<UTXO>" (§ name ßtxOutsCreated) (§ new #_"LinkedList<>"))
            (§ block
                (§ let #_"long" (§ name ßsigOps) 0)
                (§ block

                    (§ if (.. scriptVerificationExecutor (isShutdown)))
                    (§ block
                        (§ ass (§ name scriptVerificationExecutor) (Executors/newFixedThreadPool (.. (Runtime/getRuntime) (availableProcessors))))
                    )

                    (§ let #_"List<Future<VerificationException>>" (§ name ßlistScriptVerificationResults) (§ new #_"ArrayList<>" (.. ßblock transactions (size))))
                    (§ block
                        (§ try )
                        (§ block
                            (§ if (not (.. params (isCheckpoint ßheight))))
                            (§ block
                                ;; BIP30 violator blocks are ones that contain a duplicated transaction.  They are all in the checkpoints list
                                ;; and we therefore only check non-checkpoints for duplicated transactions here.  See the BIP30 document
                                ;; for more details on this: https://github.com/bitcoin/bips/blob/master/bip-0030.mediawiki
                                (§ for (§ var #_"Transaction" (§ name ßtx)) :for (.. ßblock transactions))
                                (§ block
                                    (§ let #_"Set<VerifyFlag>" (§ name ßverifyFlags) (.. params (getTransactionVerificationFlags ßblock, ßtx, (getVersionTally), ßheight)))
                                    (§ block
                                        (§ let #_"Sha256Hash" (§ name ßhash) (.. ßtx (getHash)))
                                        (§ block
                                            ;; If we already have unspent outputs for this hash, we saw the tx already.
                                            ;; Either the block is being added twice (bug) or the block is a BIP30 violator.
                                            (§ if (.. blockStore (hasUnspentOutputs ßhash, (.. ßtx (getOutputs) (size)))))
                                            (§ block
                                                (§ throw (§ new #_"VerificationException" "Block failed BIP30 test!"))
                                            )
                                            ;; We already check non-BIP16 sigops in Block.verifyTransactions(true).
                                            (§ if (.. ßverifyFlags (contains VerifyFlag/P2SH)))
                                            (§ block
                                                (§ ass (§ name ßsigOps) (+ ßsigOps (.. ßtx (getSigOpCount))))
                                            )
                                        )
                                    )
                                )
                            )
                            (§ let #_"Coin" (§ name ßtotalFees) Coin/ZERO)
                            (§ block
                                (§ let #_"Coin" (§ name ßcoinbaseValue) nil)
                                (§ block
                                    (§ for (§ var Transaction tx) :for (.. ßblock transactions))
                                    (§ block
                                        (§ let #_"boolean" (§ name ßisCoinBase) (.. tx (isCoinBase)))
                                        (§ block
                                            (§ let #_"Coin" (§ name ßvalueIn) Coin/ZERO)
                                            (§ block
                                                (§ let #_"Coin" (§ name ßvalueOut) Coin/ZERO)
                                                (§ block
                                                    (§ let #_"List<Script>" (§ name ßprevOutScripts) (§ new #_"LinkedList<>"))
                                                    (§ block
                                                        (§ let #_"Set<VerifyFlag>" (§ name ßverifyFlags) (.. params (getTransactionVerificationFlags ßblock, tx, (getVersionTally), ßheight)))
                                                        (§ block
                                                            (§ if (not ßisCoinBase))
                                                            (§ block
                                                                ;; For each input of the transaction remove the corresponding output from the set of unspent outputs.
                                                                (§ for (§ var #_"int" (§ name ßindex) 0) :for (< ßindex (.. tx (getInputs) (size))) :for (§ recur (+ ßindex 1)))
                                                                (§ block
                                                                    (§ let #_"TransactionInput" (§ name ßin) (.. tx (getInputs) (get ßindex)))
                                                                    (§ block
                                                                        (§ let #_"UTXO" (§ name ßprevOut) (.. blockStore (getTransactionOutput (.. ßin (getOutpoint) (getHash)), (.. ßin (getOutpoint) (getIndex)))))
                                                                        (§ block
                                                                            (§ if (nil? ßprevOut))
                                                                            (§ block
                                                                                (§ throw (§ new #_"VerificationException" "Attempted to spend a non-existent or already spent output!"))
                                                                            )
                                                                            ;; Coinbases can't be spent until they mature, to avoid re-orgs destroying entire transaction chains.
                                                                            ;; The assumption is there will ~never be re-orgs deeper than the spendable coinbase chain depth.
                                                                            (§ if (.. ßprevOut (isCoinbase)))
                                                                            (§ block
                                                                                (§ if (< (- ßheight (.. ßprevOut (getHeight))) (.. params (getSpendableCoinbaseDepth))))
                                                                                (§ block
                                                                                    (§ throw (§ new #_"VerificationException" (str "Tried to spend coinbase at depth " (- ßheight (.. ßprevOut (getHeight))))))
                                                                                )
                                                                            )
                                                                            ;; TODO: Check we're not spending the genesis transaction here. Bitcoin Core won't allow it.
                                                                            (§ ass (§ name ßvalueIn) (.. ßvalueIn (add (.. ßprevOut (getValue)))))
                                                                            (§ if (.. ßverifyFlags (contains VerifyFlag/P2SH)))
                                                                            (§ block
                                                                                (§ if (.. ßprevOut (getScript) (isPayToScriptHash)))
                                                                                (§ block
                                                                                    (§ ass (§ name ßsigOps) (+ ßsigOps (Script/getP2SHSigOpCount (.. ßin (getScriptBytes)))))
                                                                                )
                                                                                (§ if (< Block/MAX_BLOCK_SIGOPS ßsigOps))
                                                                                (§ block
                                                                                    (§ throw (§ new #_"VerificationException" "Too many P2SH SigOps in block"))
                                                                                )
                                                                            )

                                                                            (.. ßprevOutScripts (add (.. ßprevOut (getScript))))
                                                                            (.. blockStore (removeUnspentTransactionOutput ßprevOut))
                                                                            (.. ßtxOutsSpent (add ßprevOut))
                                                                        )
                                                                    )
                                                                )
                                                            )
                                                            (§ let #_"Sha256Hash" (§ name ßhash) (.. tx (getHash)))
                                                            (§ block
                                                                (§ for (§ var #_"TransactionOutput" (§ name ßout)) :for (.. tx (getOutputs)))
                                                                (§ block
                                                                    (§ ass (§ name ßvalueOut) (.. ßvalueOut (add (.. ßout (getValue)))))
                                                                    ;; For each output, add it to the set of unspent outputs so it can be consumed in future.
                                                                    (§ let #_"Script" (§ name ßscript) (getScript (.. ßout (getScriptBytes))))
                                                                    (§ block
                                                                        (§ let #_"UTXO" (§ name ßnewOut) (§ new #_"UTXO" ßhash, (.. ßout (getIndex)), (.. ßout (getValue)), ßheight, ßisCoinBase, ßscript, (getScriptAddress ßscript)))
                                                                        (§ block
                                                                            (.. blockStore (addUnspentTransactionOutput ßnewOut))
                                                                            (.. ßtxOutsCreated (add ßnewOut))
                                                                        )
                                                                    )
                                                                )
                                                                ;; All values were already checked for being non-negative (as it is verified in Transaction.verify()),
                                                                ;; but we check again here just for defence in depth.  Transactions with zero output value are OK.
                                                                (§ if (or (< (.. ßvalueOut (signum)) 0) (< 0 (.. ßvalueOut (compareTo (.. params (getMaxMoney)))))))
                                                                (§ block
                                                                    (§ throw (§ new #_"VerificationException" "Transaction output value out of range"))
                                                                )

                                                                (§ if ßisCoinBase)
                                                                (§ block
                                                                    (§ ass (§ name ßcoinbaseValue) ßvalueOut)
                                                                )
                                                                (§ else )
                                                                (§ block
                                                                    (§ if (or (< (.. ßvalueIn (compareTo ßvalueOut)) 0) (< 0 (.. ßvalueIn (compareTo (.. params (getMaxMoney)))))))
                                                                    (§ block
                                                                        (§ throw (§ new #_"VerificationException" "Transaction input value out of range"))
                                                                    )
                                                                    (§ ass (§ name ßtotalFees) (.. ßtotalFees (add (.. ßvalueIn (subtract ßvalueOut)))))
                                                                )

                                                                (§ if (and (not ßisCoinBase) runScripts))
                                                                (§ block
                                                                    ;; Because correctlySpends modifies transactions, this must come after we are done with tx.
                                                                    (§ let #_"FutureTask<VerificationException>" (§ name ßfuture) (§ new #_"FutureTask<>" (§ new #_"Verifier" tx, ßprevOutScripts, ßverifyFlags)))
                                                                    (§ block
                                                                        (.. scriptVerificationExecutor (execute ßfuture))
                                                                        (.. ßlistScriptVerificationResults (add ßfuture))
                                                                    )
                                                                )
                                                            )
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                    (§ if (or (< 0 (.. ßtotalFees (compareTo (.. params (getMaxMoney))))) (< (.. ßblock (getBlockInflation ßheight) (add ßtotalFees) (compareTo ßcoinbaseValue)) 0)))
                                    (§ block
                                        (§ throw (§ new #_"VerificationException" "Transaction fees out of range"))
                                    )

                                    (§ for (§ var #_"Future<VerificationException>" (§ name ßfuture)) :for ßlistScriptVerificationResults)
                                    (§ block
                                        (§ let #_"VerificationException" (§ name ße))
                                        (§ block
                                            (§ try )
                                            (§ block
                                                (§ ass (§ name ße) (.. ßfuture (get)))
                                            )
                                            (§ catch #_"InterruptedException" (§ name ßie))
                                            (§ block
                                                (§ throw (§ new #_"RuntimeException" ßie)) ;; Shouldn't happen.
                                            )
                                            (§ catch #_"ExecutionException" (§ name ßee))
                                            (§ block
                                                (.. log (error (str "Script.correctlySpends threw a non-normal exception: " (.. ßee (getCause)))))
                                                (§ throw (§ new #_"VerificationException" "Bug in Script.correctlySpends, likely script malformed in some new and interesting way.", ßee))
                                            )
                                            (§ if (some? ße))
                                            (§ block
                                                (§ throw ße)
                                            )
                                        )
                                    )
                                )
                            )
                        )
                        (§ catch #_"VerificationException" (§ name ße))
                        (§ block
                            (.. scriptVerificationExecutor (shutdownNow))
                            (.. blockStore (abortDatabaseBatchWrite))
                            (§ throw ße)
                        )
                        (§ catch #_"BlockStoreException" (§ name ße))
                        (§ block
                            (.. scriptVerificationExecutor (shutdownNow))
                            (.. blockStore (abortDatabaseBatchWrite))
                            (§ throw ße)
                        )

                        (§ new #_"TransactionOutputChanges" ßtxOutsCreated, ßtxOutsSpent)
                    )
                )
            )
        )
    )

    #_override
    ;;;
     ; Used during reorgs to connect a block previously on a fork.
     ;;
    #_protected
    #_synchronized
    (§ method #_"TransactionOutputChanges" (§ fn connectTransactions) [#_"StoredBlock" (§ name ßnewBlock)])
        (§ throws #_"VerificationException", #_"BlockStoreException", #_"PrunedException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (§ if (not (.. params (passesCheckpoint (.. ßnewBlock (getHeight)), (.. ßnewBlock (getHeader) (getHash))))))
        (§ block
            (§ throw (§ new #_"VerificationException" (str "Block failed checkpoint lockin at " (.. ßnewBlock (getHeight)))))
        )

        (.. blockStore (beginDatabaseBatchWrite))
        (§ let #_"StoredUndoableBlock" (§ name ßblock) (.. blockStore (getUndoBlock (.. ßnewBlock (getHeader) (getHash)))))
        (§ block
            (§ if (nil? ßblock))
            (§ block
                ;; We're trying to re-org too deep and the data needed has been deleted.
                (.. blockStore (abortDatabaseBatchWrite))
                (§ throw (§ new #_"PrunedException" (.. ßnewBlock (getHeader) (getHash))))
            )

            (§ let #_"TransactionOutputChanges" (§ name ßtxOutChanges))
            (§ block
                (§ try )
                (§ block
                    (§ let #_"List<Transaction>" (§ name ßtransactions) (.. ßblock (getTransactions)))
                    (§ block
                        (§ if (some? ßtransactions))
                        (§ block
                            (§ let #_"LinkedList<UTXO>" (§ name ßtxOutsSpent) (§ new #_"LinkedList<>"))
                            (§ block
                                (§ let #_"LinkedList<UTXO>" (§ name ßtxOutsCreated) (§ new #_"LinkedList<>"))
                                (§ block
                                    (§ let #_"long" (§ name ßsigOps) 0)
                                    (§ block

                                        (§ if (not (.. params (isCheckpoint (.. ßnewBlock (getHeight))))))
                                        (§ block
                                            (§ for (§ var #_"Transaction" (§ name ßtx)) :for ßtransactions)
                                            (§ block
                                                (§ let #_"Sha256Hash" (§ name ßhash) (.. ßtx (getHash)))
                                                (§ block
                                                    (§ if (.. blockStore (hasUnspentOutputs ßhash, (.. ßtx (getOutputs) (size)))))
                                                    (§ block
                                                        (§ throw (§ new #_"VerificationException" "Block failed BIP30 test!"))
                                                    )
                                                )
                                            )
                                        )

                                        (§ let #_"Coin" (§ name ßtotalFees) Coin/ZERO)
                                        (§ block
                                            (§ let #_"Coin" (§ name ßcoinbaseValue) nil)
                                            (§ block

                                                (§ if (.. scriptVerificationExecutor (isShutdown)))
                                                (§ block
                                                    (§ ass (§ name scriptVerificationExecutor) (Executors/newFixedThreadPool (.. (Runtime/getRuntime) (availableProcessors))))
                                                )

                                                (§ let #_"List<Future<VerificationException>>" (§ name ßlistScriptVerificationResults) (§ new #_"ArrayList<>" (.. ßtransactions (size))))
                                                (§ block
                                                    (§ for (§ var Transaction tx) :for transactions)
                                                    (§ block
                                                        (§ let #_"Set<VerifyFlag>" (§ name ßverifyFlags) (.. params (getTransactionVerificationFlags (.. ßnewBlock (getHeader)), tx, (getVersionTally), Integer/SIZE)))
                                                        (§ block
                                                            (§ let #_"boolean" (§ name ßisCoinBase) (.. tx (isCoinBase)))
                                                            (§ block
                                                                (§ let #_"Coin" (§ name ßvalueIn) Coin/ZERO)
                                                                (§ block
                                                                    (§ let #_"Coin" (§ name ßvalueOut) Coin/ZERO)
                                                                    (§ block
                                                                        (§ let #_"List<Script>" (§ name ßprevOutScripts) (§ new #_"LinkedList<>"))
                                                                        (§ block

                                                                            (§ if (not ßisCoinBase))
                                                                            (§ block
                                                                                (§ for (§ var #_"int" (§ name ßindex) 0) :for (< ßindex (.. tx (getInputs) (size))) :for (§ recur (+ ßindex 1)))
                                                                                (§ block
                                                                                    (§ let #_"TransactionInput" (§ name ßin) (.. tx (getInputs) (get ßindex)))
                                                                                    (§ block
                                                                                        (§ let #_"UTXO" (§ name ßprevOut) (.. blockStore (getTransactionOutput (.. ßin (getOutpoint) (getHash)), (.. ßin (getOutpoint) (getIndex)))))
                                                                                        (§ block
                                                                                            (§ if (nil? ßprevOut))
                                                                                            (§ block
                                                                                                (§ throw (§ new #_"VerificationException" "Attempted spend of a non-existent or already spent output!"))
                                                                                            )
                                                                                            (§ if (and (.. ßprevOut (isCoinbase)) (< (- (.. ßnewBlock (getHeight)) (.. ßprevOut (getHeight))) (.. params (getSpendableCoinbaseDepth)))))
                                                                                            (§ block
                                                                                                (§ throw (§ new #_"VerificationException" (str "Tried to spend coinbase at depth " (- (.. ßnewBlock (getHeight)) (.. prevOut (getHeight))))))
                                                                                            )
                                                                                            (§ ass (§ name ßvalueIn) (.. valueIn (add (.. prevOut (getValue)))))
                                                                                            (§ if (.. ßverifyFlags (contains VerifyFlag/P2SH)))
                                                                                            (§ block
                                                                                                (§ if (.. prevOut (getScript) (isPayToScriptHash)))
                                                                                                (§ block
                                                                                                    (§ ass (§ name ßsigOps) (+ sigOps (Script/getP2SHSigOpCount (.. ßin (getScriptBytes)))))
                                                                                                )
                                                                                                (§ if (< Block/MAX_BLOCK_SIGOPS sigOps))
                                                                                                (§ block
                                                                                                    (§ throw (§ new #_"VerificationException" "Too many P2SH SigOps in block"))
                                                                                                )
                                                                                            )

                                                                                            ;; TODO: Enforce DER signature format.

                                                                                            (.. ßprevOutScripts (add (.. prevOut (getScript))))

                                                                                            (.. blockStore (removeUnspentTransactionOutput prevOut))
                                                                                            (.. ßtxOutsSpent (add prevOut))
                                                                                        )
                                                                                    )
                                                                                )
                                                                            )
                                                                            (§ let #_"Sha256Hash" (§ name ßhash) (.. tx (getHash)))
                                                                            (§ block
                                                                                (§ for (§ var #_"TransactionOutput" (§ name ßout)) :for (.. tx (getOutputs)))
                                                                                (§ block
                                                                                    (§ ass (§ name ßvalueOut) (.. valueOut (add (.. ßout (getValue)))))
                                                                                    (§ let #_"Script" (§ name ßscript) (getScript (.. ßout (getScriptBytes))))
                                                                                    (§ block
                                                                                        (§ let #_"UTXO" (§ name ßnewOut) (§ new #_"UTXO" ßhash, (.. ßout (getIndex)), (.. ßout (getValue)), (.. ßnewBlock (getHeight)), ßisCoinBase, ßscript, (getScriptAddress ßscript)))
                                                                                        (§ block
                                                                                            (.. blockStore (addUnspentTransactionOutput ßnewOut))
                                                                                            (.. ßtxOutsCreated (add ßnewOut))
                                                                                        )
                                                                                    )
                                                                                )
                                                                                ;; All values were already checked for being non-negative (as it is verified in Transaction.verify())
                                                                                ;; but we check again here just for defence in depth.  Transactions with zero output value are OK.
                                                                                (§ if (or (< (.. valueOut (signum)) 0) (< 0 (.. valueOut (compareTo (.. params (getMaxMoney)))))))
                                                                                (§ block
                                                                                    (§ throw (§ new #_"VerificationException" "Transaction output value out of range"))
                                                                                )

                                                                                (§ if ßisCoinBase)
                                                                                (§ block
                                                                                    (§ ass (§ name ßcoinbaseValue) valueOut)
                                                                                )
                                                                                (§ else )
                                                                                (§ block
                                                                                    (§ if (or (< (.. valueIn (compareTo valueOut)) 0) (< 0 (.. valueIn (compareTo (.. params (getMaxMoney)))))))
                                                                                    (§ block
                                                                                        (§ throw (§ new #_"VerificationException" "Transaction input value out of range"))
                                                                                    )
                                                                                    (§ ass (§ name ßtotalFees) (.. ßtotalFees (add (.. valueIn (subtract valueOut)))))
                                                                                )

                                                                                (§ if (not isCoinBase))
                                                                                (§ block
                                                                                    ;; Because correctlySpends modifies transactions, this must come after we are done with tx.
                                                                                    (§ let #_"FutureTask<VerificationException>" (§ name ßfuture) (§ new #_"FutureTask<>" (§ new #_"Verifier" tx, prevOutScripts, ßverifyFlags)))
                                                                                    (§ block
                                                                                        (.. scriptVerificationExecutor (execute ßfuture))
                                                                                        (.. ßlistScriptVerificationResults (add ßfuture))
                                                                                    )
                                                                                )
                                                                            )
                                                                        )
                                                                    )
                                                                )
                                                            )
                                                        )
                                                    )
                                                    (§ if (or (< 0 (.. ßtotalFees (compareTo (.. params (getMaxMoney))))) (< (.. ßnewBlock (getHeader) (getBlockInflation (.. ßnewBlock (getHeight))) (add ßtotalFees) (compareTo ßcoinbaseValue)) 0)))
                                                    (§ block
                                                        (§ throw (§ new #_"VerificationException" "Transaction fees out of range"))
                                                    )

                                                    (§ ass (§ name ßtxOutChanges) (§ new #_"TransactionOutputChanges" ßtxOutsCreated, ßtxOutsSpent))
                                                    (§ for (§ var #_"Future<VerificationException>" (§ name ßfuture)) :for ßlistScriptVerificationResults)
                                                    (§ block
                                                        (§ let #_"VerificationException" (§ name ße))
                                                        (§ block
                                                            (§ try )
                                                            (§ block
                                                                (§ ass (§ name ße) (.. ßfuture (get)))
                                                            )
                                                            (§ catch #_"InterruptedException" (§ name ßie))
                                                            (§ block
                                                                (§ throw (§ new #_"RuntimeException" ßie)) ;; Shouldn't happen.
                                                            )
                                                            (§ catch #_"ExecutionException" (§ name ßee))
                                                            (§ block
                                                                (.. log (error (str "Script.correctlySpends threw a non-normal exception: " (.. ßee (getCause)))))
                                                                (§ throw (§ new #_"VerificationException" "Bug in Script.correctlySpends, likely script malformed in some new and interesting way.", ßee))
                                                            )
                                                            (§ if (some? ße))
                                                            (§ block
                                                                (§ throw ße)
                                                            )
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                )
                            )
                        )
                        (§ else )
                        (§ block
                            (§ ass (§ name ßtxOutChanges) (.. ßblock (getTxOutChanges)))
                            (§ if (not (.. params (isCheckpoint (.. ßnewBlock (getHeight))))))
                            (§ block
                                (§ for (§ var #_"UTXO" (§ name ßout)) :for (.. txOutChanges txOutsCreated))
                                (§ block
                                    (§ let #_"Sha256Hash" (§ name ßhash) (.. ßout (getHash)))
                                    (§ block
                                        (§ if (!= (.. blockStore (getTransactionOutput ßhash, (.. ßout (getIndex)))) nil))
                                        (§ block
                                            (§ throw (§ new #_"VerificationException" "Block failed BIP30 test!"))
                                        )
                                    )
                                )
                            )
                            (§ for (§ var #_"UTXO" (§ name ßout)) :for (.. txOutChanges txOutsCreated))
                            (§ block
                                (.. blockStore (addUnspentTransactionOutput ßout))
                            )
                            (§ for (§ var #_"UTXO" (§ name ßout)) :for (.. txOutChanges txOutsSpent))
                            (§ block
                                (.. blockStore (removeUnspentTransactionOutput ßout))
                            )
                        )
                    )
                )
                (§ catch #_"VerificationException" (§ name ße))
                (§ block
                    (.. scriptVerificationExecutor (shutdownNow))
                    (.. blockStore (abortDatabaseBatchWrite))
                    (§ throw ße)
                )
                (§ catch #_"BlockStoreException" (§ name ße))
                (§ block
                    (.. scriptVerificationExecutor (shutdownNow))
                    (.. blockStore (abortDatabaseBatchWrite))
                    (§ throw ße)
                )
                txOutChanges
            )
        )
    )

    ;;;
     ; This is broken for blocks that do not pass BIP30, so all BIP30-failing blocks which are allowed to fail BIP30
     ; must be checkpointed.
     ;;
    #_override
    #_protected
    (§ method #_"void" (§ fn disconnectTransactions) [#_"StoredBlock" (§ name ßoldBlock)])
        (§ throws #_"PrunedException", #_"BlockStoreException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (.. blockStore (beginDatabaseBatchWrite))
        (§ try )
        (§ block
            (§ let #_"StoredUndoableBlock" (§ name ßundoBlock) (.. blockStore (getUndoBlock (.. ßoldBlock (getHeader) (getHash)))))
            (§ block
                (§ if (nil? ßundoBlock))
                (§ block
                    (§ throw (§ new #_"PrunedException" (.. ßoldBlock (getHeader) (getHash))))
                )

                (§ let #_"TransactionOutputChanges" (§ name ßtxOutChanges) (.. ßundoBlock (getTxOutChanges)))
                (§ block
                    (§ for (§ var #_"UTXO" (§ name ßout)) :for (.. ßtxOutChanges txOutsSpent))
                    (§ block
                        (.. blockStore (addUnspentTransactionOutput ßout))
                    )
                    (§ for (§ var #_"UTXO" (§ name ßout)) :for (.. ßtxOutChanges txOutsCreated))
                    (§ block
                        (.. blockStore (removeUnspentTransactionOutput ßout))
                    )
                )
            )
        )
        (§ catch #_"PrunedException" (§ name ße))
        (§ block
            (.. blockStore (abortDatabaseBatchWrite))
            (§ throw ße)
        )
        (§ catch #_"BlockStoreException" (§ name ße))
        (§ block
            (.. blockStore (abortDatabaseBatchWrite))
            (§ throw ße)
        )
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn doSetChainHead) [#_"StoredBlock" (§ name ßchainHead)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (.. blockStore (setVerifiedChainHead ßchainHead))
        (.. blockStore (commitDatabaseBatchWrite))
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn notSettingChainHead) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. blockStore (abortDatabaseBatchWrite))
        nil
    )

    #_override
    #_protected
    (§ method #_"StoredBlock" (§ fn getStoredBlockInCurrentScope) [#_"Sha256Hash" (§ name ßhash)])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (.. blockStore (getOnceUndoableStoredBlock ßhash))
    )
)

#_(ns org.bitcoinj.core #_"GetAddrMessage")

;;;
 ; <p>Represents the "getaddr" P2P protocol message, which requests network {@link AddressMessage}s from a peer.
 ; Not to be confused with {@link Address} which is sort of like an account number.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"GetAddrMessage" (§ extends #_"EmptyMessage")
    #_public
    (§ constructor #_"GetAddrMessage" [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ super ßparams)
        this
    )
)

#_(ns org.bitcoinj.core #_"GetBlocksMessage"
    (:import [java.io IOException OutputStream]
             [java.util ArrayList List]))

;;;
 ; <p>Represents the "getblocks" P2P network message, which requests the hashes of the parts of the block chain we're missing.
 ; Those blocks can then be downloaded with a {@link GetDataMessage}.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"GetBlocksMessage" (§ extends #_"Message")
    #_protected
    (§ field #_"long" (§ name version))
    #_protected
    (§ field #_"List<Sha256Hash>" (§ name locator))
    #_protected
    (§ field #_"Sha256Hash" (§ name stopHash))

    #_public
    (§ constructor #_"GetBlocksMessage" [#_"NetworkParameters" (§ name ßparams), #_"List<Sha256Hash>" (§ name ßlocator), #_"Sha256Hash" (§ name ßstopHash)])
    (§ block
        (§ super ßparams)

        (§ ass (§ name (.. this version)) protocolVersion)
        (§ ass (§ name (.. this ßlocator)) ßlocator)
        (§ ass (§ name (.. this ßstopHash)) ßstopHash)
        this
    )

    #_public
    (§ constructor #_"GetBlocksMessage" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayload)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayload, 0)
        this
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ ass (§ name cursor) offset)
        (§ ass (§ name version) (readUint32))
        (§ let #_"int" (§ name ßstartCount) (§ cast #_"int" (readVarInt)))
        (§ block
            (§ if (< 500 ßstartCount))
            (§ block
                (§ throw (§ new #_"ProtocolException" (str "Number of locators cannot be > 500, received: " ßstartCount)))
            )

            (§ ass (§ name length) (+ (- cursor offset) (* (+ ßstartCount 1) 32)))
            (§ ass (§ name locator) (§ new #_"ArrayList<>" ßstartCount))
            (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßstartCount) :for (§ recur (+ ßi 1)))
            (§ block
                (.. locator (add (readHash)))
            )
            (§ ass (§ name stopHash) (readHash))
            nil
        )
    )

    #_public
    (§ method #_"List<Sha256Hash>" (§ fn getLocator) [])
    (§ block
        locator
    )

    #_public
    (§ method #_"Sha256Hash" (§ fn getStopHash) [])
    (§ block
        stopHash
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "getblocks: " (.. Utils/SPACE_JOINER (join locator)))
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        ;; Version, for some reason.
        (Utils/uint32ToByteStreamLE (.. params (getProtocolVersionNum NetworkParameters/ProtocolVersion/CURRENT)), ßstream)
        ;; Then a vector of block hashes.  This is actually a "block locator", a set of block
        ;; identifiers that spans the entire chain with exponentially increasing gaps between
        ;; them, until we end up at the genesis block.  See CBlockLocator::Set().
        (.. ßstream (write (.. (§ new #_"VarInt" (.. locator (size))) (encode))))
        (§ for (§ var #_"Sha256Hash" (§ name ßhash)) :for locator)
        (§ block
            ;; Have to reverse as wire format is little endian.
            (.. ßstream (write (.. ßhash (getReversedBytes))))
        )
        ;; Next, a block ID to stop at.
        (.. ßstream (write (.. stopHash (getReversedBytes))))
        nil
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"GetBlocksMessage" (§ name ßother) (§ cast #_"GetBlocksMessage" ßo))
        (§ block
            (and (== version (.. ßother version)) (.. stopHash (equals (.. ßother stopHash))) (== (.. locator (size)) (.. ßother locator (size))) (.. locator (containsAll (.. ßother locator)))) ;; ignores locator ordering
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (§ let #_"int" (§ name ßhashCode) (bit-xor (§ cast #_"int" version) (.. "getblocks" (hashCode)) (.. stopHash (hashCode))))
        (§ block
            (§ for (§ var #_"Sha256Hash" (§ name ßl)) :for locator)
            (§ block
                (§ ass (§ name ßhashCode) (bit-xor ßhashCode (.. ßl (hashCode)))) ;; ignores locator ordering
            )
            ßhashCode
        )
    )
)

#_(ns org.bitcoinj.core #_"GetDataMessage")

;;;
 ; <p>Represents the "getdata" P2P network message, which requests the contents of blocks or transactions given their hashes.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"GetDataMessage" (§ extends #_"ListMessage")
    #_public
    (§ constructor #_"GetDataMessage" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayloadBytes)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayloadBytes)
        this
    )

    ;;;
     ; Deserializes a 'getdata' message.
     ; @param params NetworkParameters object.
     ; @param payload Bitcoin protocol formatted byte array containing message content.
     ; @param serializer The serializer to use for this message.
     ; @param length The length of message if known.  Usually this is provided when deserializing of the wire
     ; as the length will be provided as part of the header.  Set to Message.UNKNOWN_LENGTH, if not known.
     ; @throws ProtocolException
     ;;
    #_public
    (§ constructor #_"GetDataMessage" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayload), #_"MessageSerializer" (§ name ßserializer), #_"int" (§ name ßlength)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayload, ßserializer, ßlength)
        this
    )

    #_public
    (§ constructor #_"GetDataMessage" [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ super ßparams)
        this
    )

    #_public
    (§ method #_"void" (§ fn addTransaction) [#_"Sha256Hash" (§ name ßhash)])
    (§ block
        (addItem (§ new #_"InventoryItem" InventoryItem/Type/Transaction, ßhash))
        nil
    )

    #_public
    (§ method #_"void" (§ fn addBlock) [#_"Sha256Hash" (§ name ßhash)])
    (§ block
        (addItem (§ new #_"InventoryItem" InventoryItem/Type/Block, ßhash))
        nil
    )

    #_public
    (§ method #_"void" (§ fn addFilteredBlock) [#_"Sha256Hash" (§ name ßhash)])
    (§ block
        (addItem (§ new #_"InventoryItem" InventoryItem/Type/FilteredBlock, ßhash))
        nil
    )

    #_public
    (§ method #_"Sha256Hash" (§ fn getHashOf) [#_"int" (§ name ßi)])
    (§ block
        (.. (getItems) (get ßi) hash)
    )
)

#_(ns org.bitcoinj.core #_"GetHeadersMessage"
    (:import [java.util List]))

;;;
 ; <p>The "getheaders" command is structurally identical to "getblocks", but has different meaning.  On receiving this
 ; message a Bitcoin node returns matching blocks up to the limit, but without the bodies.  It is useful as an
 ; optimization: when your wallet does not contain any keys created before a particular time, you don't have to download
 ; the bodies for those blocks because you know there are no relevant transactions.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"GetHeadersMessage" (§ extends #_"GetBlocksMessage")
    #_public
    (§ constructor #_"GetHeadersMessage" [#_"NetworkParameters" (§ name ßparams), #_"List<Sha256Hash>" (§ name ßlocator), #_"Sha256Hash" (§ name ßstopHash)])
    (§ block
        (§ super ßparams, ßlocator, ßstopHash)
        this
    )

    #_public
    (§ constructor #_"GetHeadersMessage" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayload)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayload)
        this
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "getheaders: " (.. Utils/SPACE_JOINER (join locator)))
    )

    ;;;
     ; Compares two getheaders messages.  Note that even though they are structurally identical a GetHeadersMessage
     ; will not compare equal to a GetBlocksMessage containing the same data.
     ;;
    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" (§ name ßo)])
    (§ block
        (§ if (== this ßo))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? ßo) (!= (getClass) (.. ßo (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"GetHeadersMessage" (§ name ßother) (§ cast #_"GetHeadersMessage" ßo))
        (§ block
            (and (== version (.. ßother version)) (.. stopHash (equals (.. ßother stopHash))) (== (.. locator (size)) (.. ßother locator (size))) (.. locator (containsAll (.. ßother locator)))) ;; ignores locator ordering
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (§ let #_"int" (§ name ßhashCode) (bit-xor (§ cast #_"int" version) (.. "getheaders" (hashCode)) (.. stopHash (hashCode))))
        (§ block
            (§ for (§ var #_"Sha256Hash" (§ name ßl)) :for locator)
            (§ block
                (§ ass (§ name ßhashCode) (bit-xor ßhashCode (.. ßl (hashCode)))) ;; ignores locator ordering
            )
            ßhashCode
        )
    )
)

#_(ns org.bitcoinj.core #_"HeadersMessage"
    (:import [java.io IOException OutputStream]
             [java.util ArrayList Arrays List])
    (:import [org.slf4j Logger LoggerFactory]))

;;;
 ; <p>A protocol message that contains a repeated series of block headers, sent in response to the "getheaders" command.
 ; This is useful when you want to traverse the chain but know you don't care about the block contents, for example,
 ; because you have a freshly created wallet with no keys.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"HeadersMessage" (§ extends #_"Message")
    #_private
    #_static
    (§ def- #_"Logger" (§ name log) (LoggerFactory/getLogger (§ klass #_"HeadersMessage")))

    ;; The main client will never send us more than this number of headers.
    #_public
    #_static
    (§ def #_"int" (§ name MAX_HEADERS) 2000)

    #_private
    (§ field- #_"List<Block>" (§ name blockHeaders))

    #_public
    (§ constructor #_"HeadersMessage" [#_"NetworkParameters" (§ name ßparams), #_"byte[]" (§ name ßpayload)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams, ßpayload, 0)
        this
    )

    #_public
    (§ constructor #_"HeadersMessage" [#_"NetworkParameters" (§ name ßparams), #_"Block..." (§ name ßheaders)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams)
        (§ ass (§ name blockHeaders) (Arrays/asList ßheaders))
        this
    )

    #_public
    (§ constructor #_"HeadersMessage" [#_"NetworkParameters" (§ name ßparams), #_"List<Block>" (§ name ßheaders)])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super ßparams)
        (§ ass (§ name blockHeaders) ßheaders)
        this
    )

    #_override
    #_public
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" (§ name ßstream)])
        (§ throws #_"IOException")
    (§ block
        (.. ßstream (write (.. (§ new #_"VarInt" (.. blockHeaders (size))) (encode))))
        (§ for (§ var #_"Block" (§ name ßheader)) :for blockHeaders)
        (§ block
            (.. ßheader (cloneAsHeader) (bitcoinSerializeToStream ßstream))
            (.. ßstream (write 0))
        )
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ let #_"long" (§ name ßnumHeaders) (readVarInt))
        (§ block
            (§ if (< MAX_HEADERS ßnumHeaders))
            (§ block
                (§ throw (§ new #_"ProtocolException" (str "Too many headers: got " ßnumHeaders " which is larger than " MAX_HEADERS)))
            )

            (§ ass (§ name blockHeaders) (§ new #_"ArrayList<>"))
            (§ let #_"BitcoinSerializer" (§ name ßserializer) (.. this params (getSerializer true)))
            (§ block

                (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumHeaders) :for (§ recur (+ ßi 1)))
                (§ block
                    (§ let #_"Block" (§ name ßnewBlockHeader) (.. ßserializer (makeBlock payload, cursor, UNKNOWN_LENGTH)))
                    (§ block
                        (§ if (.. ßnewBlockHeader (hasTransactions)))
                        (§ block
                            (§ throw (§ new #_"ProtocolException" "Block header does not end with a nil byte"))
                        )

                        (§ ass (§ name cursor) (+ cursor (.. ßnewBlockHeader optimalEncodingMessageSize)))
                        (.. blockHeaders (add ßnewBlockHeader))
                    )
                )

                (§ if (== length UNKNOWN_LENGTH))
                (§ block
                    (§ ass (§ name length) (- cursor offset))
                )

                (§ if (.. log (isDebugEnabled)))
                (§ block
                    (§ for (§ var #_"int" (§ name ßi) 0) :for (< ßi ßnumHeaders) :for (§ recur (+ ßi 1)))
                    (§ block
                        (.. log (debug (.. this blockHeaders (get ßi) (toString))))
                    )
                )
                nil
            )
        )
    )

    #_public
    (§ method #_"List<Block>" (§ fn getBlockHeaders) [])
    (§ block
        blockHeaders
    )
)

