        ;; Announce ourselves.  This has to come first to connect to clients beyond v0.3.20.2 which wait to hear
        ;; from us until they send their version message back.
        (§ let #_"PeerAddress" (§ name ßaddress) (getAddress))
        (§ block
            (.. log (info "Announcing to {} as: {}", (if (some? ßaddress) (.. ßaddress (toSocketAddress)) "Peer"), (.. versionMessage subVer)))
            (sendMessage versionMessage)
            (.. connectionOpenFuture (set this))
            ;; When connecting, the remote peer sends us a version message with various bits of
            ;; useful data in it.  We need to know the peer protocol version before we can talk to it.
            nil
        )
    )

