    ;; in broadcastTransaction.
    #_private
    (§ field- #_"Set<TransactionBroadcast>" (§ name runningBroadcasts))

    #_private
    (§ class #_"PeerListener" (§ implements #_"GetDataEventListener", #_"BlocksDownloadedEventListener")
        #_public
        (§ constructor #_"PeerListener" [])
        (§ block
            this
        )

        #_override
        #_public
        (§ method #_"List<Message>" (§ fn getData) [#_"Peer" (§ name ßpeer), #_"GetDataMessage" (§ name ßm)])
        (§ block
            (handleGetData ßm)
        )

        #_override
        #_public
        (§ method #_"void" (§ fn onBlocksDownloaded) [#_"Peer" (§ name ßpeer), #_"Block" (§ name ßblock), #_nilable #_"FilteredBlock" (§ name ßfilteredBlock), #_"int" (§ name ßblocksLeft)])
        (§ block
            (§ if (some? chain))
            (§ block
                (§ let #_"double" (§ name ßrate) (.. chain (getFalsePositiveRate)))
                (§ block
                    (§ let #_"double" (§ name ßtarget) (* (.. bloomFilterMerger (getBloomFilterFPRate)) MAX_FP_RATE_INCREASE))
                    (§ block
                        (§ if (< ßtarget ßrate))
                        (§ block
                            ;; TODO: Avoid hitting this path if the remote peer didn't acknowledge applying a new filter yet.
                            (§ if (.. log (isDebugEnabled)))
                            (§ block
                                (.. log (debug "Force update Bloom filter due to high false positive ßrate ({} vs {})", ßrate, ßtarget))
                            )

                            (recalculateFastCatchupAndFilter FilterRecalculateMode/FORCE_SEND_FOR_REFRESH)
                        )
                    )
                )
            )
            nil
        )
    )

    #_private
    (§ class #_"PeerStartupListener" (§ implements #_"PeerConnectedEventListener", #_"PeerDisconnectedEventListener")
        #_override
        #_public
        (§ method #_"void" (§ fn onPeerConnected) [#_"Peer" (§ name ßpeer), #_"int" (§ name ßpeerCount)])
        (§ block
            (handleNewPeer ßpeer)
            nil
        )

        #_override
        #_public
        (§ method #_"void" (§ fn onPeerDisconnected) [#_"Peer" (§ name ßpeer), #_"int" (§ name ßpeerCount)])
        (§ block
            ;; The channel will be automatically removed from channels.
            (handlePeerDeath ßpeer, nil)
            nil
        )
    )

    #_private
    (§ field- #_"PeerStartupListener" (§ name startupListener) (§ new #_"PeerStartupListener"))

    ;;;
     ; The default Bloom filter false positive rate, which is selected to be extremely low such that you hardly ever
     ; download false positives.  This provides maximum performance.  Although this default can be overridden to push
     ; the FP rate higher, due to <a href="https://groups.google.com/forum/#!msg/bitcoinj/Ys13qkTwcNg/9qxnhwnkeoIJ">
     ; various complexities</a> there are still ways a remote peer can deanonymize the users wallet.  This is why the
     ; FP rate is chosen for performance rather than privacy.  If a future version of bitcoinj fixes the known
     ; de-anonymization attacks this FP rate may rise again (or more likely, become expressed as a bandwidth allowance).
     ;;
    #_public
    #_static
    (§ def #_"double" (§ name DEFAULT_BLOOM_FILTER_FP_RATE) 0.00001)
    ;;; Maximum increase in FP rate before forced refresh of the bloom filter. ;;
    #_public
    #_static
    (§ def #_"double" (§ name MAX_FP_RATE_INCREASE) 10.0)
    ;; An object that calculates bloom filters given a list of filter providers, whilst tracking some state useful
    ;; for privacy purposes.
    #_private
    (§ field- #_"FilterMerger" (§ name bloomFilterMerger))

    ;;; The default timeout between when a connection attempt begins and version message exchange completes. ;;
    #_public
    #_static
    (§ def #_"int" (§ name DEFAULT_CONNECT_TIMEOUT_MILLIS) 5000)
    #_private
    #_volatile
    (§ field- #_"int" (§ name vConnectTimeoutMillis) DEFAULT_CONNECT_TIMEOUT_MILLIS)

    ;;; Whether bloom filter support is enabled when using a non FullPrunedBlockchain. ;;
    #_private
    #_volatile
    (§ field- #_"boolean" (§ name vBloomFilteringEnabled) true)

    ;;; See {@link #PeerGroup(Context)}. ;;
    #_public
    (§ constructor #_"PeerGroup" [#_"NetworkParameters" (§ name ßparams)])
    (§ block
        (§ this ßparams, nil)
        this
    )

    ;;;
     ; Creates a PeerGroup with the given context.  No chain is provided so this node will report its chain height
     ; as zero to other peers.  This constructor is useful if you just want to explore the network but aren't interested
     ; in downloading block data.
     ;;
    #_public
    (§ constructor #_"PeerGroup" [#_"Context" (§ name ßcontext)])
    (§ block
        (§ this ßcontext, nil)
        this
    )

    ;;; See {@link #PeerGroup(Context, AbstractBlockChain)}. ;;
    #_public
    (§ constructor #_"PeerGroup" [#_"NetworkParameters" (§ name ßparams), #_nilable #_"AbstractBlockChain" (§ name ßchain)])
    (§ block
        (§ this (Context/getOrCreate ßparams), ßchain, (§ new #_"NioClientManager"))
        this
    )

    ;;;
     ; Creates a PeerGroup for the given context and chain.  Blocks will be passed to the chain as they are broadcast
     ; and downloaded.  This is probably the constructor you want to use.
     ;;
    #_public
    (§ constructor #_"PeerGroup" [#_"Context" (§ name ßcontext), #_nilable #_"AbstractBlockChain" (§ name ßchain)])
    (§ block
        (§ this ßcontext, ßchain, (§ new #_"NioClientManager"))
        this
    )

    ;;; See {@link #PeerGroup(Context, AbstractBlockChain, ClientConnectionManager)}. ;;
    #_public
    (§ constructor #_"PeerGroup" [#_"NetworkParameters" (§ name ßparams), #_nilable #_"AbstractBlockChain" (§ name ßchain), #_"ClientConnectionManager" (§ name ßconnectionManager)])
    (§ block
        (§ this (Context/getOrCreate ßparams), ßchain, ßconnectionManager)
        this
    )

    ;;;
     ; Creates a new PeerGroup allowing you to specify the {@link ClientConnectionManager} which is used to create new
     ; connections and keep track of existing ones.
     ;;
    #_private
    (§ constructor- #_"PeerGroup" [#_"Context" (§ name ßcontext), #_nilable #_"AbstractBlockChain" (§ name ßchain), #_"ClientConnectionManager" (§ name ßconnectionManager)])
    (§ block
        (Preconditions/checkNotNull ßcontext)

        (§ ass (§ name (.. this params)) (.. ßcontext (getParams)))
        (§ ass (§ name (.. this ßchain)) ßchain)
        (§ ass (§ name fastCatchupTimeSecs) (.. params (getGenesisBlock) (getTimeSeconds)))
        (§ ass (§ name wallets) (§ new #_"CopyOnWriteArrayList<>"))
        (§ ass (§ name peerFilterProviders) (§ new #_"CopyOnWriteArrayList<>"))

        (§ ass (§ name executor) (createPrivateExecutor))

        ;; This default sentinel value will be overridden by one of two actions:
        ;;   - adding a peer discovery source sets it to the default.
        ;;   - using connectTo() will increment it by one.
        (§ ass (§ name maxConnections) 0)

        (§ let #_"int" (§ name ßheight) (if (some? ßchain) (.. ßchain (getBestChainHeight)) 0))
        (§ block
            (§ ass (§ name versionMessage) (§ new #_"VersionMessage" params, ßheight))
            ;; We never request that the remote node wait for a bloom filter yet, as we have no wallets.
            (§ ass (§ name (.. versionMessage relayTxesBeforeFilter)) true)

            (§ ass (§ name downloadTxDependencyDepth) Integer/MAX_VALUE)

            (§ ass (§ name inactives) (§ new #_"PriorityQueue<>" 1, (§ new #_"Comparator<PeerAddress>")
            (§ anon
                #_suppress(§ opt "FieldAccessNotGuarded") ;; Only called when inactives is accessed, and lock is held then.
                #_override
                #_public
                (§ method #_"int" (§ fn compare) [#_"PeerAddress" (§ name ßa), #_"PeerAddress" (§ name ßb)])
                (§ block
                    (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

                    (§ let #_"int" (§ name ßresult) (.. backoffMap (get ßa) (compareTo (.. backoffMap (get ßb)))))
                    (§ block
                        ;; Sort by port if otherwise equals - for testing.
                        (§ if (== ßresult 0))
                        (§ block
                            (§ ass (§ name ßresult) (Ints/compare (.. ßa (getPort)), (.. ßb (getPort))))
                        )
                        ßresult
                    )
                )
            )))
            (§ ass (§ name backoffMap) (§ new #_"HashMap<>"))
            (§ ass (§ name peers) (§ new #_"CopyOnWriteArrayList<>"))
            (§ ass (§ name pendingPeers) (§ new #_"CopyOnWriteArrayList<>"))
            (§ ass (§ name channels) ßconnectionManager)
            (§ ass (§ name peerDiscoverers) (§ new #_"CopyOnWriteArraySet<>"))
            (§ ass (§ name runningBroadcasts) (Collections/synchronizedSet (§ new #_"HashSet<TransactionBroadcast>")))
            (§ ass (§ name bloomFilterMerger) (§ new #_"FilterMerger" DEFAULT_BLOOM_FILTER_FP_RATE))
            (§ ass (§ name vMinRequiredProtocolVersion) (.. params (getProtocolVersionNum NetworkParameters/ProtocolVersion/BLOOM_FILTER)))
            this
        )
    )

    #_private
    (§ field- #_"CountDownLatch" (§ name executorStartupLatch) (§ new #_"CountDownLatch" 1))

    #_protected
    (§ method #_"ListeningScheduledExecutorService" (§ fn createPrivateExecutor) [])
    (§ block
        (§ let #_"ListeningScheduledExecutorService" (§ name ßresult) (MoreExecutors/listeningDecorator (§ new #_"ScheduledThreadPoolExecutor" 1, (§ new #_"ContextPropagatingThreadFactory" "PeerGroup Thread"))))
        (§ block
            ;; Hack: jam the executor so jobs just queue up until the user calls start() on us.  For example, adding a wallet
            ;; results in a bloom filter recalc being queued, but we don't want to do that until we're actually started.
            (.. ßresult (execute (§ new #_"Runnable")
            (§ anon
                #_override
                #_public
                (§ method #_"void" (§ fn run) [])
                (§ block
                    (Uninterruptibles/awaitUninterruptibly executorStartupLatch)
                    nil
                )
            )))
            ßresult
        )
    )

    ;;;
     ; This is how many milliseconds we wait for peer discoveries to return their results.
     ;;
    #_public
    (§ method #_"void" (§ fn setPeerDiscoveryTimeoutMillis) [#_"long" (§ name ßpeerDiscoveryTimeoutMillis)])
    (§ block
        (§ ass (§ name (.. this vPeerDiscoveryTimeoutMillis)) ßpeerDiscoveryTimeoutMillis)
        nil
    )

    ;;;
     ; Adjusts the desired number of connections that we will create to peers.  Note that if there are already peers
     ; open and the new value is lower than the current number of peers, those connections will be terminated.
     ; Likewise if there aren't enough current connections to meet the new requested max size, some will be added.
     ;;
    #_public
    (§ method #_"void" (§ fn setMaxConnections) [#_"int" (§ name ßmaxConnections)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ ass (§ name (.. this ßmaxConnections)) ßmaxConnections)
            (§ if (not (isRunning)))
            (§ block
                (§ return nil)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )

        ;; We may now have too many or too few open connections.  Add more or drop some to get to the right amount.
        (§ let #_"int" (§ name ßadjustment) (- ßmaxConnections (.. channels (getConnectedClientCount))))
        (§ block
            (§ if (< 0 ßadjustment))
            (§ block
                (triggerConnections)
            )

            (§ if (< ßadjustment 0))
            (§ block
                (.. channels (closeConnections (- ßadjustment)))
            )
            nil
        )
    )

    ;;;
     ; Configure download of pending transaction dependencies.  A change of values only takes effect for newly connected peers.
     ;;
    #_public
    (§ method #_"void" (§ fn setDownloadTxDependencies) [#_"int" (§ name ßdepth)])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ ass (§ name (.. this downloadTxDependencyDepth)) ßdepth)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    #_private
    (§ field- #_"Runnable" (§ name triggerConnectionsJob) (§ new #_"Runnable"
        (§ anon
            #_private
            (§ field- #_"boolean" (§ name firstRun) true)
            #_private
            #_static
            (§ def- #_"long" (§ name MIN_PEER_DISCOVERY_INTERVAL) 1000)

            #_override
            #_public
            (§ method #_"void" (§ fn run) [])
            (§ block
                (§ try )
                (§ block
                    (go)
                )
                (§ catch #_"Throwable" (§ name ße))
                (§ block
                    (.. log (error "Exception when trying to build connections", ße)) ;; The executor swallows exceptions :( ;; )
                )
                nil
            )

            #_public
            (§ method #_"void" (§ fn go) [])
            (§ block
                (§ if (not vRunning))
                (§ block
                    (§ return nil)
                )

                (§ let #_"boolean" (§ name ßdoDiscovery) false)
                (§ block
                    (§ let #_"long" (§ name ßnow) (Utils/currentTimeMillis))
                    (§ block
                        (.. lock (lock))
                        (§ try )
                        (§ block
                            ;; First run: try and use a local node if there is one, for the additional security it can provide.
                            ;; But, not on Android as there are none for this platform: it could only be a malicious app trying
                            ;; to hijack our traffic.
                            (§ if (and (not (Utils/isAndroidRuntime)) useLocalhostPeerWhenPossible (maybeCheckForLocalhostPeer) firstRun))
                            (§ block
                                (.. log (info "Localhost peer detected, trying to use it instead of P2P discovery"))
                                (§ ass (§ name maxConnections) 0)
                                (connectToLocalHost)
                                (§ return nil)
                            )

                            (§ let #_"boolean" (§ name ßhavePeerWeCanTry) (and (not (.. inactives (isEmpty))) (<= (.. backoffMap (get (.. inactives (peek))) (getRetryTime)) ßnow)))
                            (§ block
                                (§ ass (§ name ßdoDiscovery) (not ßhavePeerWeCanTry))
                            )
                        )
                        (§ finally )
                        (§ block
                            (§ ass (§ name firstRun) false)
                            (.. lock (unlock))
                        )

                        ;; Don't hold the lock across discovery as this process can be very slow.
                        (§ let #_"boolean" (§ name ßdiscoverySuccess) false)
                        (§ block
                            (§ if ßdoDiscovery)
                            (§ block
                                (§ try )
                                (§ block
                                    (§ ass (§ name ßdiscoverySuccess) (< 0 (discoverPeers)))
                                )
                                (§ catch #_"PeerDiscoveryException" (§ name ße))
                                (§ block
                                    (.. log (error "Peer discovery failure", ße))
                                )
                            )

                            (§ let #_"long" (§ name ßretryTime))
                            (§ block
                                (§ let #_"PeerAddress" (§ name ßaddrToTry))
                                (§ block
                                    (.. lock (lock))
                                    (§ try )
                                    (§ block
                                        (§ if ßdoDiscovery)
                                        (§ block
                                            ;; Require that we have enough connections, to consider this a success,
                                            ;; or we just constantly test for new peers.
                                            (§ if (and ßdiscoverySuccess (<= (getMaxConnections) (countConnectedAndPendingPeers))))
                                            (§ block
                                                (.. groupBackoff (trackSuccess))
                                            )
                                            (§ else )
                                            (§ block
                                                (.. groupBackoff (trackFailure))
                                            )
                                        )
                                        ;; Inactives is sorted by backoffMap time.
                                        (§ if (.. inactives (isEmpty)))
                                        (§ block
                                            (§ if (< (countConnectedAndPendingPeers) (getMaxConnections)))
                                            (§ block
                                                (§ let #_"long" (§ name ßinterval) (Math/max (- (.. groupBackoff (getRetryTime)) ßnow), MIN_PEER_DISCOVERY_INTERVAL))
                                                (§ block
                                                    (.. log (info (str "Peer discovery didn't provide us any more peers, will try again in " ßinterval "ms.")))
                                                    (.. executor (schedule this, ßinterval, TimeUnit/MILLISECONDS))
                                                )
                                            )
                                            (§ else )
                                            (§ block
                                                ;; We have enough peers and discovery provided no more, so just settle down.
                                                ;; Most likely we were given a fixed set of addresses in some test scenario.
                                            )
                                            (§ return nil)
                                        )
                                        (§ else )
                                        (§ block
                                            (§ do )
                                            (§ block
                                                (§ ass (§ name ßaddrToTry) (.. inactives (poll)))
                                            )
                                            (§ again (and ipv6Unreachable (§ insta (.. ßaddrToTry (getAddr)) #_"Inet6Address")))
                                            (§ ass (§ name ßretryTime) (.. backoffMap (get ßaddrToTry) (getRetryTime)))
                                        )
                                        (§ ass (§ name ßretryTime) (Math/max ßretryTime, (.. groupBackoff (getRetryTime))))
                                        (§ if (< ßnow ßretryTime))
                                        (§ block
                                            (§ let #_"long" (§ name ßdelay) (- ßretryTime ßnow))
                                            (§ block
                                                (.. log (info "Waiting {} msec before next connect attempt {}", ßdelay, (if (some? ßaddrToTry) (str "to " ßaddrToTry) "")))
                                                (.. inactives (add ßaddrToTry))
                                                (.. executor (schedule this, ßdelay, TimeUnit/MILLISECONDS))
                                                (§ return nil)
                                            )
                                        )
                                        (connectTo ßaddrToTry, false, vConnectTimeoutMillis)
                                    )
                                    (§ finally )
                                    (§ block
                                        (.. lock (unlock))
                                    )
                                    (§ if (< (countConnectedAndPendingPeers) (getMaxConnections)))
                                    (§ block
                                        (.. executor (execute this)) ;; Try next peer immediately.
                                    )
                                    nil
                                )
                            )
                        )
                    )
                )
            )
        )))

