    #_private
    (§ method- #_"List<Message>" (§ fn handleGetData) [#_"GetDataMessage" (§ name ßm)])
    (§ block
        ;; Scans the wallets and memory pool for transactions in the getdata message and returns them.
        ;; Runs on peer threads.
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"LinkedList<Message>" (§ name ßtransactions) (§ new #_"LinkedList<>"))
            (§ block
                (§ let #_"LinkedList<InventoryItem>" (§ name ßitems) (§ new #_"LinkedList<>" (.. ßm (getItems))))
                (§ block
                    (§ let #_"Iterator<InventoryItem>" (§ name ßit) (.. ßitems (iterator)))
                    (§ block
                        (§ while (.. ßit (hasNext)))
                        (§ block
                            (§ let #_"InventoryItem" (§ name ßitem) (.. ßit (next)))
                            (§ block
                                ;; Check the wallets.
                                (§ for (§ var #_"Wallet" (§ name ßw)) :for wallets)
                                (§ block
                                    (§ let #_"Transaction" (§ name ßtx) (.. ßw (getTransaction (.. ßitem hash))))
                                    (§ block
                                        (§ if (some? ßtx))
                                        (§ block
                                            (.. ßtransactions (add ßtx))
                                            (.. ßit (remove))
                                            (§ break )
                                        )
                                    )
                                )
                            )
                        )
                        (§ return ßtransactions)
                    )
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

