    ;;;
     ; Sets information that identifies this software to remote nodes.  This is a convenience wrapper for creating
     ; a new {@link VersionMessage}, calling {@link VersionMessage#appendToSubVer(String, String, String)} on it,
     ; and then calling {@link PeerGroup#setVersionMessage(VersionMessage)} on the result of that.  See the docs for
     ; {@link VersionMessage#appendToSubVer(String, String, String)} for information on what the fields should contain.
     ;;
    #_public
    (§ method #_"void" (§ fn setUserAgent) [#_"String" (§ name ßname), #_"String" (§ name ßversion), #_nilable #_"String" (§ name ßcomments)])
    (§ block
        ;; TODO: Check that height is needed here (it wasnt, but it should be, no?)
        (§ let #_"int" (§ name ßheight) (if (some? chain) (.. chain (getBestChainHeight)) 0))
        (§ block
            (§ let #_"VersionMessage" (§ name ßver) (§ new #_"VersionMessage" params, ßheight))
            (§ block
                (§ ass (§ name (.. ßver relayTxesBeforeFilter)) false)
                (updateVersionMessageRelayTxesBeforeFilter ßver)
                (.. ßver (appendToSubVer ßname, ßversion, ßcomments))
                (setVersionMessage ßver)
                nil
            )
        )
    )

    ;; Updates the relayTxesBeforeFilter flag of ver.
    #_private
    (§ method- #_"void" (§ fn updateVersionMessageRelayTxesBeforeFilter) [#_"VersionMessage" (§ name ßver)])
    (§ block
        ;; We will provide the remote node with a bloom filter (i.e. they shouldn't relay yet)
        ;; if chain == null || !chain.shouldVerifyTransactions() and a wallet is added and bloom filters are enabled.
        ;; Note that the default here means that no tx invs will be received if no wallet is ever added.
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"boolean" (§ name ßspvMode) (and (some? chain) (not (.. chain (shouldVerifyTransactions)))))
            (§ block
                (§ let #_"boolean" (§ name ßwillSendFilter) (and ßspvMode (< 0 (.. peerFilterProviders (size))) vBloomFilteringEnabled))
                (§ block
                    (§ ass (§ name (.. ßver relayTxesBeforeFilter)) (not ßwillSendFilter))
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

