    )

    ;;; Returns number of discovered peers. ;;
    #_protected
    (§ method #_"int" (§ fn discoverPeers) [])
        (§ throws #_"PeerDiscoveryException")
    (§ block
        ;; Don't hold the lock whilst doing peer discovery: it can take a long time and cause high API latency.
        (Preconditions/checkState (not (.. lock (isHeldByCurrentThread))))

        (§ let #_"int" (§ name ßmaxPeersToDiscoverCount) (.. this vMaxPeersToDiscoverCount))
        (§ block
            (§ let #_"long" (§ name ßpeerDiscoveryTimeoutMillis) (.. this vPeerDiscoveryTimeoutMillis))
            (§ block
                (§ let #_"Stopwatch" (§ name ßwatch) (Stopwatch/createStarted))
                (§ block
                    (§ let #_"List<PeerAddress>" (§ name ßaddressList) (Lists/newLinkedList))
                    (§ block

                        (§ for (§ var #_"PeerDiscovery" (§ name ßpeerDiscovery)) :for peerDiscoverers) ;; COW
                        (§ block
                            (§ let #_"InetSocketAddress[]" (§ name ßaddresses) (.. ßpeerDiscovery (getPeers requiredServices, ßpeerDiscoveryTimeoutMillis, TimeUnit/MILLISECONDS)))
                            (§ block
                                (§ for (§ var #_"InetSocketAddress" (§ name ßaddress)) :for ßaddresses)
                                (§ block
                                    (.. ßaddressList (add (§ new #_"PeerAddress" params, ßaddress)))
                                )
                                (§ if (<= ßmaxPeersToDiscoverCount (.. ßaddressList (size))))
                                (§ block
                                    (§ break )
                                )
                            )
                        )

                        (§ if (not (.. ßaddressList (isEmpty))))
                        (§ block
                            (§ for (§ var #_"PeerAddress" (§ name ßaddress)) :for ßaddressList)
                            (§ block
                                (addInactive ßaddress)
                            )

                            (§ let #_"ImmutableSet<PeerAddress>" (§ name ßpeersDiscoveredSet) (ImmutableSet/copyOf ßaddressList))
                            (§ block
                                (§ for (§ var ListenerRegistration<PeerDiscoveredEventListener> registration) :for peerDiscoveredEventListeners) ;; COW
                                (§ block
                                    (.. registration executor (execute (§ new #_"Runnable")
                                    (§ anon
                                        #_override
                                        #_public
                                        (§ method #_"void" (§ fn run) [])
                                        (§ block
                                            (.. registration listener (onPeersDiscovered ßpeersDiscoveredSet))
                                            nil
                                        )
                                    )))
                                )
                            )
                        )
                        (.. ßwatch (stop))
                        (.. log (info "Peer discovery took {} and returned {} items", ßwatch, (.. ßaddressList (size))))
                        (.. ßaddressList (size))
                    )
                )
            )
        )
    )

    #_testing
